INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398480, 'NACE', 'NACE Rev. 2', 'NACE Revision 2, downloaded from http://ec.europa.eu/eurostat/ramon/nomenclatures/index.cfm?TargetUrl=LST_NOM_DTL_LINEAR&StrNom=NACE_REV2&StrLanguageCode=EN on November 25, 2018', 1, NULL, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398481, 'A', 'AGRICULTURE, FORESTRY AND FISHING', 'This section includes the exploitation of vegetal and animal natural resources, comprising the activities of growing of crops, raising and breeding of animals, harvesting of timber and other plants, animals or animal products from a farm or their natural habitats.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398482, '1', 'Crop and animal production, hunting and related service activities', 'This division includes two basic activities, namely the production of crop products and production of animal products, covering also the forms of organic agriculture, the growing of genetically modified crops and the raising of genetically modified animals. This division includes growing of crops in open fields as well in greenhouses. Group 01.5 (Mixed farming) breaks with the usual principles for identifying main activity. It accepts that many agricultural holdings have reasonably balanced crop and animal production, and that it would be arbitrary to classify them in one category or the other.', 1, 398481, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398483, '1.1', 'Growing of non-perennial crops', 'This group includes the growing of non-perennial crops, i.e. plants that do not last for more than two growing seasons. Included is the growing of these plants for the purpose of seed production.', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398484, '1.11', 'Growing of cereals (except rice), leguminous crops and oil seeds', 'This class includes all forms of growing of cereals, leguminous crops and oil seeds in open fields. The growing of these crops is often combined within agricultural units. This class includes: - growing of cereals such as: . wheat . grain maize . sorghum . barley . rye . oats . millets . other cereals n.e.c. - growing of leguminous crops such as: . beans . broad beans . chick peas . cow peas . lentils . lupines . peas . pigeon peas . other leguminous crops - growing of oil seeds such as: . soya beans . groundnuts . castor bean . linseed . mustard seed . niger seed . rapeseed . safflower seed . sesame seed . sunflower seed . other oil seeds', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398485, '1.12', 'Growing of rice', 'This class includes: - growing of rice (including organic farming and the growing of genetically modified rice)', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398486, '1.13', 'Growing of vegetables and melons, roots and tubers', 'This class includes: - growing of leafy or stem vegetables such as: . artichokes . asparagus . cabbages . cauliflower and broccoli . lettuce and chicory . spinach . other leafy or stem vegetables - growing of fruit bearing vegetables such as: . cucumbers and gherkins . eggplants (aubergines) . tomatoes . watermelons . cantaloupes . other melons and fruit-bearing vegetables - growing of root, bulb or tuberous vegetables such as: . carrots . turnips . garlic . onions (incl. shallots) . leeks and other alliaceous vegetables . other root, bulb or tuberous vegetables - growing of mushrooms and truffles - growing of vegetable seeds, including sugar beet seeds, excluding other beet seeds - growing of sugar beet - growing of other vegetables - growing of roots and tubers such as: . potatoes . sweet potatoes . cassava . yams . other roots and tubers', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398487, '1.14', 'Growing of sugar cane', '', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398488, '1.15', 'Growing of tobacco', 'This class includes: - growing of unmanufactured tobacco', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398489, '1.16', 'Growing of fibre crops', 'This class includes: - growing of cotton - growing of jute, kenaf and other textile bast fibres - growing of flax and true hemp - growing of sisal and other textile fibre of the genus agave - growing of abaca, ramie and other vegetable textile fibres - growing of other fibre crops', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398490, '1.19', 'Growing of other non-perennial crops', 'This class includes the growing of all other non-perennial crops: - growing of swedes, mangolds, fodder roots, clover, alfalfa, sainfoin, fodder maize and other grasses, forage kale and similar forage products - growing of beet seeds (excluding sugar beet seeds) and seeds of forage plants - growing of flowers - production of cut flowers and flower buds - growing of flower seeds', 1, 398483, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398491, '1.2', 'Growing of perennial crops', 'This group includes the growing of perennial crops, i.e. plants that lasts for more than two growing seasons, either dying back after each season or growing continuously. Included is the growing of these plants for the purpose of seed production.', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398492, '1.21', 'Growing of grapes', 'This class includes: - growing of wine grapes and table grapes in vineyards', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398493, '1.22', 'Growing of tropical and subtropical fruits', 'This class includes: - growing of tropical and subtropical fruits: . avocados . bananas and plantains . dates . figs . mangoes . papayas . pineapples . other tropical and subtropical fruits', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398494, '1.23', 'Growing of citrus fruits', 'This class includes: - growing of citrus fruits: . grapefruit and pomelo . lemons and limes . oranges . tangerines, mandarins and clementines . other citrus fruits', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398495, '1.24', 'Growing of pome fruits and stone fruits', 'This class includes: - growing of pome fruits and stone fruits: . apples . apricots . cherries and sour cherries . peaches and nectarines . pears and quinces . plums and sloes . other pome fruits and stone fruits', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398496, '1.25', 'Growing of other tree and bush fruits and nuts', 'This class includes: - growing of berries: . blueberries . currants . gooseberries . kiwi fruit . raspberries . strawberries . other berries - growing of fruit seeds - growing of edible nuts: . almonds . cashew nuts . chestnuts . hazelnuts . pistachios . walnuts . other nuts - growing of other tree and bush fruits: . locust beans', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398497, '1.26', 'Growing of oleaginous fruits', 'This class includes: - growing of oleaginous fruits: . coconuts . olives . oil palms . other oleaginous fruits', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398498, '1.27', 'Growing of beverage crops', 'This class includes: - growing of beverage crops: . coffee . tea . maté . cocoa . other beverage crops', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398499, '1.28', 'Growing of spices, aromatic, drug and pharmaceutical crops', 'This class includes: - growing of perennial and non-perennial spices and aromatic crops: . pepper (piper sop.) . chillies and peppers (capsicum sop.) . nutmeg, mace and cardamoms . anise, badian and fennel . cinnamon (canella) . cloves . ginger . vanilla . hops . other spices and aromatic crops - growing of drug and narcotic crops', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398500, '1.29', 'Growing of other perennial crops', 'This class includes: - growing of rubber trees for harvesting of latex - growing of Christmas trees - growing of trees for extraction of sap - growing of vegetable materials of a kind used primarily for plaiting', 1, 398491, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398501, '1.3', 'Plant propagation', '', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398502, '1.3', 'Plant propagation', 'This class includes the production of all vegetative planting materials including cuttings, suckers and seedlings for direct plant propagation or to create plant grafting stock into which selected scion is grafted for eventual planting to produce crops. This class includes: - growing of plants for planting - growing of plants for ornamental purposes, including turf for transplanting - growing of live plants for bulbs, tubers and roots; cuttings and slips; mushroom spawn - operation of tree nurseries, except forest tree nurseries', 1, 398501, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398503, '1.4', 'Animal production', 'This group includes raising (farming) and breeding of all animals, except aquatic animals.', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398504, '1.41', 'Raising of dairy cattle', 'This class includes: - raising and breeding of dairy cattle - production of raw milk from cows or buffaloes', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398505, '1.42', 'Raising of other cattle and buffaloes', 'This class includes: - raising and breeding of cattle and buffaloes for meat - production of bovine semen', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398506, '1.43', 'Raising of horses and other equines', 'This class includes: - raising and breeding of horses, asses, mules or hinnies', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398507, '1.44', 'Raising of camels and camelids', 'This class includes: - raising and breeding of camels (dromedary) and camelids', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398508, '1.45', 'Raising of sheep and goats', 'This class includes: - raising and breeding of sheep and goats - production of raw sheep or goat milk - production of raw wool', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398509, '1.46', 'Raising of swine/pigs', 'This class includes: - raising and breeding of swine (pigs)', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398510, '1.47', 'Raising of poultry', 'This class includes: - raising and breeding of poultry: . chickens, turkeys, ducks, geese and guinea fowls - production of eggs from poultry - operation of poultry hatcheries', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398511, '1.49', 'Raising of other animals', 'This class includes: - raising and breeding of semi-domesticated or other live animals: . ostriches and emus . other birds (except poultry) . insects . rabbits and other fur animals - production of fur skins, reptile or bird skins from ranching operation - operation of worm farms, land mollusc farms, snail farms etc. - raising of silk worms, production of silk worm cocoons - bee-keeping and production of honey and beeswax - raising and breeding of pet animals (except fish): . cats and dogs . birds, such as parakeets etc. . hamsters etc. - raising of diverse animals', 1, 398503, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398512, '1.5', 'Mixed farming', '', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398513, '1.5', 'Mixed farming', 'This class includes the combined production of crops and animals without a specialised production of crops or animals. The size of the overall farming operation is not a determining factor. If either production of crops or animals in a given unit is 66% or more of standard gross margins, the combined activity should not be included here, but allocated to crop or animal farming.', 1, 398512, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398514, '1.6', 'Support activities to agriculture and post-harvest crop activities', 'This group includes activities incidental to agricultural production and activities similar to agriculture not undertaken for production purposes (in the sense of harvesting agricultural products), done on a fee or contract basis.', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398515, '1.61', 'Support activities for crop production', 'This class includes: - agricultural activities on a fee or contract basis: . preparation of fields . establishing a crop . treatment of crops . crop spraying, including by air . trimming of fruit trees and vines . transplanting of rice, thinning of beets . harvesting . pest control (including rabbits) in connection with agriculture - maintenance of agricultural land in good agricultural and environmental condition - operation of agricultural irrigation equipment', 1, 398514, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398516, '1.62', 'Support activities for animal production', 'This class includes: - agricultural activities on a fee or contract basis: . activities to promote propagation, growth and output of animals . herd testing services, droving services, agistment services, poultry caponising, coop cleaning etc. . activities related to artificial insemination . stud services . sheep shearing . farm animal boarding and care', 1, 398514, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398517, '1.63', 'Post-harvest crop activities', 'This class includes: - preparation of crops for primary markets, i.e. cleaning, trimming, grading, disinfecting - cotton ginning - preparation of tobacco leaves, e.g. drying - preparation of cocoa beans, e.g. peeling - waxing of fruit - sun-drying of fruit and vegetables', 1, 398514, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398518, '1.64', 'Seed processing for propagation', 'This class includes all post-harvest activities aimed at improving the propagation quality of seed through the removal of non-seed materials, undersized, mechanically or insect-damaged and immature seeds as well as removing the seed moisture to a safe level for seed storage. This activity includes the drying, cleaning, grading and treating of seeds until they are marketed. The treatment of genetically modified seeds is included here.', 1, 398514, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398519, '1.7', 'Hunting, trapping and related service activities', '', 1, 398482, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398520, '1.7', 'Hunting, trapping and related service activities', 'This class includes: - hunting and trapping on a commercial basis - taking of animals (dead or alive) for food, fur, skin, or for use in research, in zoos or as pets - production of fur skins, reptile or bird skins from hunting or trapping activities', 1, 398519, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398521, '2', 'Forestry and logging', 'This division includes the production of roundwood as well as the extraction and gathering of wild growing non-wood forest products. Besides the production of timber, forestry activities result in products that undergo little processing, such as firewood, charcoal and roundwood used in an unprocessed form (e.g. pit-props, pulpwood etc.). These activities can be carried out in natural or planted forests.', 1, 398481, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398522, '2.1', 'Silviculture and other forestry activities', '', 1, 398521, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398523, '2.1', 'Silviculture and other forestry activities', 'This class includes: - growing of standing timber: planting, replanting, transplanting, thinning and conserving of forests and timber tracts - growing of coppice, pulpwood and fire wood - operation of forest tree nurseries These activities can be carried out in natural or planted forests.', 1, 398522, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398524, '2.2', 'Logging', '', 1, 398521, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398525, '2.2', 'Logging', 'This class includes: - production of roundwood for forest-based manufacturing industries - production of roundwood used in an unprocessed form such as pit-props, fence posts and utility poles - gathering and production of wood for energy - gathering and production of forest harvesting residues for energy - production of charcoal in the forest (using traditional methods) The output of this activity can take the form of logs or fire wood.', 1, 398524, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398526, '2.3', 'Gathering of wild growing non-wood products', '', 1, 398521, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398527, '2.3', 'Gathering of wild growing non-wood products', 'This class includes: - gathering of wild growing materials: . mushrooms, truffles . berries . nuts . balata and other rubber-like gums . cork . lac and resins . balsams . vegetable hair . eelgrass . acorns, horse chestnuts . mosses and lichens', 1, 398526, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398528, '2.4', 'Support services to forestry', '', 1, 398521, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398529, '2.4', 'Support services to forestry', 'This class includes carrying out part of the forestry operation on a fee or contract basis. This class includes: - forestry service activities: . forestry inventories . forest management consulting services . timber evaluation . forest fire fighting and protection . forest pest control - logging service activities: . transport of logs within the forest', 1, 398528, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398530, '3', 'Fishing and aquaculture', 'This division includes capture fishery and aquaculture, covering the use of fishery resources from marine, brackish or freshwater environments, with the goal of capturing or gathering fish, crustaceans, molluscs and other marine organisms and products (e.g. aquatic plants, pearls, sponges etc).', 1, 398481, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398531, '3.1', 'Fishing', 'This group includes "capture fishery", i.e. the hunting, collecting and gathering activities directed at removing or collecting live wild aquatic organisms (predominantly fish, molluscs and crustaceans) including plants from the oceanic, coastal or inland waters for human consumption and other purposes by hand or more usually by various types of fishing gear such as nets, lines and stationary traps. Such activities can be conducted on the intertidal shoreline (e.g. collection of molluscs such as mussels and oysters) or shore based netting, or from home-made dugouts or more commonly using commercially made boats in inshore, coastal waters or offshore waters. Such activities also include fishing in restocked water bodies.', 1, 398530, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398532, '3.11', 'Marine fishing', 'This class includes: - fishing on a commercial basis in ocean and coastal waters - taking of marine crustaceans and molluscs - whale catching - taking of marine aquatic animals: turtles, sea squirts, tunicates, sea urchins etc.', 1, 398531, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398533, '3.12', 'Freshwater fishing', 'This class includes: - fishing on a commercial basis in inland waters - taking of freshwater crustaceans and molluscs - taking of freshwater aquatic animals', 1, 398531, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398534, '3.2', 'Aquaculture', 'This group includes "aquaculture" (or aquafarming), i.e. the production process involving the culturing or farming (including harvesting) of aquatic organisms (fish, molluscs, crustaceans, plants, crocodiles, alligators and amphibians) using techniques designed to increase the production of the organisms in question beyond the natural capacity of the environment (for example regular stocking, feeding and protection from predators). Culturing/farming refers to the rearing up to their juvenile and/or adult phase under captive conditions of the above organisms.', 1, 398530, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398535, '3.21', 'Marine aquaculture', 'This class includes: - fish farming in sea water including farming of marine ornamental fish - production of bivalve spat (oyster mussel etc.), lobsterlings, shrimp post-larvae, fish fry and fingerlings - growing of laver and other edible seaweeds - culture of crustaceans, bivalves, other molluscs and other aquatic animals in sea water', 1, 398534, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398536, '3.22', 'Freshwater aquaculture', 'This class includes: - fish farming in freshwater including farming of freshwater ornamental fish - culture of freshwater crustaceans, bivalves, other molluscs and other aquatic animals - operation of fish hatcheries (freshwater) - farming of frogs', 1, 398534, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398537, 'B', 'MINING AND QUARRYING', 'Mining and quarrying include the extraction of minerals occurring naturally as solids (coal and ores), liquids (petroleum) or gases (natural gas). Extraction can be achieved by different methods such as underground or surface mining, well operation, seabed mining etc. This section includes supplementary activities aimed at preparing the crude materials for marketing, for example, crushing, grinding, cleaning, drying, sorting, concentrating ores, liquefaction of natural gas and agglomeration of solid fuels. These operations are often accomplished by the units that extracted the resource and/or others located nearby. Mining activities are classified into divisions, groups and classes on the basis of the principal mineral produced. Divisions 05, 06 are concerned with mining and quarrying of fossil fuels (coal, lignite, petroleum, gas); divisions 07, 08 concern metal ores, various minerals and quarry products. Some of the technical operations of this section, particularly related to the extraction of hydrocarbons, may also be carried out for third parties by specialised units as an industrial service which is reflected in division 09.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398538, '5', 'Mining of coal and lignite', 'This division includes the extraction of solid mineral fuels through underground or open-cast mining and includes operations (e.g. grading, cleaning, compressing and other steps necessary for transportation etc.) leading to a marketable product.', 1, 398537, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398539, '5.1', 'Mining of hard coal', '', 1, 398538, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398540, '5.1', 'Mining of hard coal', 'This class includes: - mining of hard coal: underground or surface mining, including mining through liquefaction methods - cleaning, sizing, grading, pulverising, compressing etc. of coal to classify, improve quality or facilitate transport or storage', 1, 398539, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398541, '5.2', 'Mining of lignite', '', 1, 398538, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398542, '5.2', 'Mining of lignite', 'This class includes: - mining of lignite (brown coal): underground or surface mining, including mining through liquefaction methods - washing, dehydrating, pulverising, compressing of lignite to improve quality or facilitate transport or storage', 1, 398541, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398543, '6', 'Extraction of crude petroleum and natural gas', 'This division includes the production of crude petroleum, the mining and extraction of oil from oil shale and oil sands and the production of natural gas and recovery of hydrocarbon liquids. This division includes the activities of operating and/or developing oil and gas field properties. Such activities may include drilling, completing and equipping wells; operating separators, emulsion breakers, desalting equipment and field gathering lines for crude petroleum; and all other activities in the preparation of oil and gas up to the point of shipment from the producing property.', 1, 398537, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398544, '6.1', 'Extraction of crude petroleum', '', 1, 398543, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398545, '6.1', 'Extraction of crude petroleum', 'This class includes: - extraction of crude petroleum oils', 1, 398544, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398546, '6.2', 'Extraction of natural gas', '', 1, 398543, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398547, '6.2', 'Extraction of natural gas', 'This class includes: - production of crude gaseous hydrocarbon (natural gas) - extraction of condensates - draining and separation of liquid hydrocarbon fractions - gas desulphurisation', 1, 398546, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398548, '7', 'Mining of metal ores', 'This division includes mining for metallic minerals (ores), performed through underground or open-cast extraction, seabed mining etc.', 1, 398537, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398549, '7.1', 'Mining of iron ores', '', 1, 398548, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398550, '7.1', 'Mining of iron ores', 'This class includes: - mining of ores valued chiefly for iron content - beneficiation and agglomeration of iron ores', 1, 398549, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398551, '7.2', 'Mining of non-ferrous metal ores', 'This group includes the mining of non-ferrous metal ores.', 1, 398548, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398552, '7.21', 'Mining of uranium and thorium ores', 'This class includes: - mining of ores chiefly valued for uranium and thorium content: pitchblende etc. - concentration of such ores - manufacture of yellowcake', 1, 398551, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398553, '7.29', 'Mining of other non-ferrous metal ores', 'This class includes: - mining and preparation of ores chiefly valued for non-ferrous metal content: . aluminium (bauxite), copper, lead, zinc, tin, manganese, chrome, nickel, cobalt, molybdenum, tantalum, vanadium etc. . precious metals: gold, silver, platinum', 1, 398551, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398554, '8', 'Other mining and quarrying', 'This division includes extraction from a mine or quarry, but also dredging of alluvial deposits, rock crushing and the use of salt marshes. The products are used most notably in construction (e.g. sands, stones etc.), manufacture of materials (e.g. clay, gypsum, calcium etc.), manufacture of chemicals etc.', 1, 398537, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398555, '8.1', 'Quarrying of stone, sand and clay', '', 1, 398554, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398556, '8.11', 'Quarrying of ornamental and building stone, limestone, gypsum, chalk and slate', 'This class includes: - quarrying, rough trimming and sawing of monumental and building stone such as marble, granite, sandstone etc. - breaking and crushing of ornamental and building stone - quarrying, crushing and breaking of limestone - mining of gypsum and anhydrite - mining of chalk and uncalcined dolomite', 1, 398555, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398557, '8.12', 'Operation of gravel and sand pits; mining of clays and kaolin', 'This class includes: - extraction and dredging of industrial sand, sand for construction and gravel - breaking and crushing of gravel - quarrying of sand - mining of clays, refractory clays and kaolin', 1, 398555, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398558, '8.9', 'Mining and quarrying n.e.c.', '', 1, 398554, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398559, '8.91', 'Mining of chemical and fertiliser minerals', 'This class includes: - mining of natural phosphates and natural potassium salts - mining of native sulphur - extraction and preparation of pyrites and pyrrhotite, except roasting - mining of natural barium sulphate and carbonate (barytes and witherite), natural borates, natural magnesium sulphates (kieserite) - mining of earth colours, fluorspar and other minerals valued chiefly as a source of chemicals', 1, 398558, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398560, '8.92', 'Extraction of peat', 'This class includes: - peat digging - preparation of peat to improve quality or facilitate transport or storage', 1, 398558, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398561, '8.93', 'Extraction of salt', 'This class includes: - extraction of salt from underground including by dissolving and pumping - salt production by evaporation of sea water or other saline waters - crushing, purification and refining of salt by the producer', 1, 398558, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398562, '8.99', 'Other mining and quarrying n.e.c.', 'This class includes: - mining and quarrying of various minerals and materials: . abrasive materials, asbestos, siliceous fossil meals, natural graphite, steatite (talc), feldspar etc. . natural asphalt, asphaltites and asphaltic rock; natural solid bitumen . gemstones, quartz, mica etc.', 1, 398558, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398563, '9', 'Mining support service activities', 'This division includes specialised support services incidental to mining provided on a fee or contract basis. It includes exploration services through traditional prospecting methods such as taking core samples and making geological observations as well as drilling, test-drilling or redrilling for oil wells, metallic and non-metallic minerals. Other typical services cover building oil and gas well foundations, cementing oil and gas well casings, cleaning, bailing and swabbing oil and gas wells, draining and pumping mines, overburden removal services at mines, etc.', 1, 398537, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398564, '9.1', 'Support activities for petroleum and natural gas extraction', '', 1, 398563, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398565, '9.1', 'Support activities for petroleum and natural gas extraction', 'This class includes: - oil and gas extraction service activities provided on a fee or contract basis: . exploration services in connection with petroleum or gas extraction, e.g. traditional prospecting methods, such as making geological observations at prospective sites . directional drilling and redrilling; "spudding in"; derrick erection in situ, repairing and dismantling; cementing oil and gas well casings; pumping of wells; plugging and abandoning wells etc. . liquefaction and regasification of natural gas for purpose of transport, done at the mine site . draining and pumping services, on a fee or contract basis . test drilling in connection with petroleum or gas extraction', 1, 398564, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398566, '9.9', 'Support activities for other mining and quarrying', '', 1, 398563, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398567, '9.9', 'Support activities for other mining and quarrying', 'This class includes: - support services on a fee or contract basis, required for mining activities of divisions 05, 07 and 08 . exploration services, e.g. traditional prospecting methods, such as taking core samples and making geological observations at prospective sites . draining and pumping services, on a fee or contract basis . test drilling and test hole boring', 1, 398566, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398568, 'C', 'MANUFACTURING', 'This section includes the physical or chemical transformation of materials, substances, or components into new products, although this cannot be used as the single universal criterion for defining manufacturing (see remark on processing of waste below). The materials, substances, or components transformed are raw materials that are products of agriculture, forestry, fishing, mining or quarrying as well as products of other manufacturing activities. Substantial alteration, renovation or reconstruction of goods is generally considered to be manufacturing. The output of a manufacturing process may be finished in the sense that it is ready for utilisation or consumption, or it may be semi-finished in the sense that it is to become an input for further manufacturing. For example, the output of alumina refining is the input used in the primary production of aluminium; primary aluminium is the input to aluminium wire drawing; and aluminium wire is the input for the manufacture of fabricated wire products. Manufacture of specialised components and parts of, and accessories and attachments to, machinery and equipment is, as a general rule, classified in the same class as the manufacture of the machinery and equipment for which the parts and accessories are intended. Manufacture of unspecialised components and parts of machinery and equipment, e.g. engines, pistons, electric motors, electrical assemblies, valves, gears, roller bearings, is classified in the appropriate class of manufacturing, without regard to the machinery and equipment in which these items may be included. However, making specialised components and accessories by moulding or extruding plastics materials is included in group 22.2. Assembly of the component parts of manufactured products is considered manufacturing. This includes the assembly of manufactured products from either self-produced or purchased components. The recovery of waste, i.e. the processing of waste into secondary raw materials is classified in group 38.3 (Materials recovery). While this may involve physical or chemical transformations, this is not considered to be a part of manufacturing. The primary purpose of these activities is considered to be the treatment or processing of waste and they are therefore classified in Section E (Water supply; sewerage, waste management and remediation activities). However, the manufacture of new final products (as opposed to secondary raw materials) is classified in manufacturing, even if these processes use waste as an input. For example, the production of silver from film waste is considered to be a manufacturing process. Specialised maintenance and repair of industrial, commercial and similar machinery and equipment is, in general, classified in division 33 (Repair, maintenance and installation of machinery and equipment). However, the repair of computers and personal and household goods is classified in division 95 (Repair of computers and personal and household goods), while the repair of motor vehicles is classified in division 45 (Wholesale and retail trade and repair of motor vehicles and motorcycles). The installation of machinery and equipment, when carried out as a specialised activity, is classified in 33.20. Remark: The boundaries of manufacturing and the other sectors of the classification system can be somewhat blurry. As a general rule, the activities in the manufacturing section involve the transformation of materials into new products. Their output is a new product. However, the definition of what constitutes a new product can be somewhat subjective. As clarification, the following activities are considered manufacturing in NACE: - fresh fish processing (oyster shucking, fish filleting), not done on a fishing boat (see 10.20) - milk pasteurising and bottling (see 10.51) - leather converting (see 15.11) - wood preserving (see 16.10) - printing and related activities (see 18.1) - tyre retreading (see 22.11) - ready-mixed concrete production (see 23.63) - electroplating, plating, and metal heat treating (see 25.61) - rebuilding or remanufacture of machinery (e.g. automobile engines, see 29.10) Conversely, there are activities that, although sometimes involving transformation processes, are classified in other sections of NACE; in other words, they are not classified as manufacturing. They include: - logging, classified in section A (Agriculture, forestry and fishing); - beneficiating of agricultural products, classified in section A (Agriculture, forestry and fishing); - preparation of food for immediate consumption on the premises is classified to division 56 (Food and beverage service activities); - beneficiating of ores and other minerals, classified in section B (Mining and quarrying); - construction of structures and fabricating operations performed at the site of construction, classified in section F (Construction); - activities of breaking bulk and redistribution in smaller lots, including packaging, repackaging, or bottling products, such as liquors or chemicals; sorting of scrap; mixing paints to customer order; and cutting metals to customer order; treatment not resulting into a different good is classified to section G (Wholesale and retail trade; repair of motor vehicles and motorcycles).', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398569, '10', 'Manufacture of food products', 'This division includes the processing of the products of agriculture, forestry and fishing into food for humans or animals, and includes the production of various intermediate products that are not directly food products. The activity often generates associated products of greater or lesser value (for example, hides from slaughtering, or oilcake from oil production). This division is organised by activities dealing with different kinds of products: meat, fish, fruit and vegetables, fats and oils, milk products, grain mill products, animal feeds and other food products. Production can be carried out for own account, as well as for third parties, as in custom slaughtering. Some activities are considered manufacturing (for example, those performed in bakeries, pastry shops, and prepared meat shops etc. which sell their own production) even though there is retail sale of the products in the producers'' own shop. However, where the processing is minimal and does not lead to a real transformation, the unit is classified to wholesale and retail trade (section G). Preparation of food for immediate consumption on the premises is classified to division 56 (Food and beverage service activities). Production of animal feeds from slaughter waste or by-products is classified in 10.9, while processing food and beverage waste into secondary raw material is classified to 38.3, and disposal of food and beverage waste in 38.21.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398570, '10.1', 'Processing and preserving of meat and production of meat products', '', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398571, '10.11', 'Processing and preserving of meat', 'This class includes: - operation of slaughterhouses engaged in killing, dressing or packing meat: beef, pork, lamb, rabbit, mutton, camel, etc. - production of fresh, chilled or frozen meat, in carcasses - production of fresh, chilled or frozen meat, in cuts', 1, 398570, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398572, '10.12', 'Processing and preserving of poultry meat', 'This class includes: - operation of slaughterhouses engaged in killing, dressing or packing poultry - production of fresh, chilled or frozen meat in individual portions - rendering of edible poultry fats', 1, 398570, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398573, '10.13', 'Production of meat and poultry meat products', 'This class includes: - production of dried, salted or smoked meat - production of meat products: . sausages, salami, puddings, "andouillettes", saveloys, bolognas, pâtés, rillettes, boiled ham', 1, 398570, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398574, '10.2', 'Processing and preserving of fish, crustaceans and molluscs', '', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398575, '10.2', 'Processing and preserving of fish, crustaceans and molluscs', 'This class includes: - preparation and preservation of fish, crustaceans and molluscs: freezing, deep-freezing, drying, cooking, smoking, salting, immersing in brine, canning etc. - production of fish, crustacean and mollusc products: fish fillets, roes, caviar, caviar substitutes etc. - production of fishmeal for human consumption or animal feed - production of meals and solubles from fish and other aquatic animals unfit for human consumption', 1, 398574, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398576, '10.3', 'Processing and preserving of fruit and vegetables', '', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398577, '10.31', 'Processing and preserving of potatoes', 'This class includes: - processing and preserving of potatoes: . manufacture of prepared frozen potatoes . manufacture of dehydrated mashed potatoes . manufacture of potato snacks . manufacture of potato crisps . manufacture of potato flour and meal', 1, 398576, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398578, '10.32', 'Manufacture of fruit and vegetable juice', 'This class includes: - manufacture of fruit or vegetable juices', 1, 398576, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398579, '10.39', 'Other processing and preserving of fruit and vegetables', 'This class includes: - manufacture of food consisting chiefly of fruit or vegetables, except ready-made dishes in frozen or canned form - preserving of fruit, nuts or vegetables: freezing, drying, immersing in oil or in vinegar, canning etc. - manufacture of fruit or vegetable food products - manufacture of jams, marmalades and table jellies - roasting of nuts - manufacture of nut foods and pastes', 1, 398576, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398580, '10.4', 'Manufacture of vegetable and animal oils and fats', 'This group includes the manufacture of crude and refined oils and fats from vegetable or animal materials, except rendering or refining of lard and other edible animal fats.', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398581, '10.41', 'Manufacture of oils and fats', 'This class includes: - manufacture of crude vegetable oils: olive oil, soya-bean oil, palm oil, sunflower-seed oil, cotton-seed oil, rape, colza or mustard oil, linseed oil etc. - manufacture of non-defatted flour or meal of oilseeds, oil nuts or oil kernels - manufacture of refined vegetable oils: olive oil, soya-bean oil etc. - processing of vegetable oils: blowing, boiling, dehydration, hydrogenation etc.', 1, 398580, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398582, '10.42', 'Manufacture of margarine and similar edible fats', 'This class includes: - manufacture of margarine - manufacture of melanges and similar spreads - manufacture of compound cooking fats', 1, 398580, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398583, '10.5', 'Manufacture of dairy products', '', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398584, '10.51', 'Operation of dairies and cheese making', 'This class includes: - manufacture of fresh liquid milk, pasteurised, sterilised, homogenised and/or ultra heat treated - manufacture of milk-based drinks - manufacture of cream from fresh liquid milk, pasteurised, sterilised, homogenised - manufacture of dried or concentrated milk whether or not sweetened - manufacture of milk or cream in solid form - manufacture of butter - manufacture of yoghurt - manufacture of cheese and curd - manufacture of whey - manufacture of casein or lactose', 1, 398583, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398585, '10.52', 'Manufacture of ice cream', 'This class includes: - manufacture of ice cream and other edible ice such as sorbet', 1, 398583, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398586, '10.6', 'Manufacture of grain mill products, starches and starch products', 'This group includes the milling of flour or meal from grains or vegetables, the milling, cleaning and polishing of rice, as well as the manufacture of flour mixes or doughs from these products.', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398587, '10.61', 'Manufacture of grain mill products', 'This class includes: - grain milling: production of flour, groats, meal or pellets of wheat, rye, oats, maize (corn) or other cereal grains - rice milling: production of husked, milled, polished, glazed, parboiled or converted rice; production of rice flour - vegetable milling: production of flour or meal of dried leguminous vegetables, of roots or tubers, or of edible nuts - manufacture of cereal breakfast foods - manufacture of flour mixes and prepared blended flour and dough for bread, cakes, biscuits or pancakes', 1, 398586, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398588, '10.62', 'Manufacture of starches and starch products', 'This class includes: - manufacture of starches from rice, potatoes, maize etc. - wet corn milling - manufacture of glucose, glucose syrup, maltose, inulin etc. - manufacture of gluten - manufacture of tapioca and tapioca substitutes prepared from starch - manufacture of corn oil', 1, 398586, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398589, '10.7', 'Manufacture of bakery and farinaceous products', 'This group includes the production of bakery products, macaroni, noodles and similar products.', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398590, '10.71', 'Manufacture of bread; manufacture of fresh pastry goods and cakes', 'This class includes: - manufacture of bakery products: . bread and rolls . pastry, cakes, pies, tarts, pancakes, waffles, rolls etc.', 1, 398589, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398591, '10.72', 'Manufacture of rusks and biscuits; manufacture of preserved pastry goods and cakes', 'This class includes: - manufacture of rusks, biscuits and other dry bakery products - manufacture of preserved pastry goods and cakes - manufacture of snack products (cookies, crackers, pretzels etc.), whether sweet or salted', 1, 398589, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398592, '10.73', 'Manufacture of macaroni, noodles, couscous and similar farinaceous products', 'This class includes: - manufacture of pastas such as macaroni and noodles, whether or not cooked or stuffed - manufacture of couscous - manufacture of canned or frozen pasta products', 1, 398589, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398593, '10.8', 'Manufacture of other food products', 'This group includes the production of sugar and confectionery, prepared meals and dishes, coffee, tea and spices, as well as perishable and specialty food products.', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398594, '10.81', 'Manufacture of sugar', 'This class includes: - manufacture or refining of sugar (sucrose) and sugar substitutes from the juice of cane, beet, maple and palm - manufacture of sugar syrups - manufacture of molasses - production of maple syrup and sugar', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398595, '10.82', 'Manufacture of cocoa, chocolate and sugar confectionery', 'This class includes: - manufacture of cocoa, cocoa butter, cocoa fat, cocoa oil - manufacture of chocolate and chocolate confectionery - manufacture of sugar confectionery: caramels, cachous, nougats, fondant, white chocolate - manufacture of chewing gum - preserving in sugar of fruit, nuts, fruit peels and other parts of plants - manufacture of confectionery lozenges and pastilles', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398596, '10.83', 'Processing of tea and coffee', 'This class includes: - decaffeinating and roasting of coffee - production of coffee products: . ground coffee . soluble coffee . extracts and concentrates of coffee - manufacture of coffee substitutes - blending of tea and maté - manufacture of extracts and preparations based on tea or maté - packing of tea including packing in tea-bags', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398597, '10.84', 'Manufacture of condiments and seasonings', 'This class includes: - manufacture of spices, sauces and condiments: . mayonnaise . mustard flour and meal . prepared mustard etc. - manufacture of vinegar', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398598, '10.85', 'Manufacture of prepared meals and dishes', 'This class includes the manufacture of ready-made (i.e. prepared, seasoned and cooked) meals and dishes. These dishes are processed to preserve them, such as in frozen or canned form, and are usually packaged and labelled for re-sale, i.e. this class does not include the preparation of meals for immediate consumption, such as in restaurants. To be considered a dish, these foods have to contain at least two distinct ingredients (except seasonings etc.). This class includes: - manufacture of meat or poultry dishes - manufacture of fish dishes, including fish and chips - manufacture of vegetable dishes - manufacture of frozen or otherwise preserved pizza', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398599, '10.86', 'Manufacture of homogenised food preparations and dietetic food', 'This class includes: - manufacture of foods for particular nutritional uses: . infant formulae . follow-up milk and other follow-up foods . baby foods . low-energy and energy-reduced foods intended for weight control . dietary foods for special medical purposes . low-sodium foods, including low-sodium or sodium-free dietary salts . gluten-free foods . foods intended to meet the expenditure of intense muscular effort, especially for sportsmen . foods for persons suffering from carbohydrate metabolism disorders (diabetes)', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398600, '10.89', 'Manufacture of other food products n.e.c.', 'This class includes: - manufacture of soups and broths - manufacture of artificial honey and caramel - manufacture of perishable prepared foods, such as: . sandwiches . fresh (uncooked) pizza - manufacture of food supplements and other food products n.e.c.', 1, 398593, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398601, '10.9', 'Manufacture of prepared animal feeds', '', 1, 398569, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398602, '10.91', 'Manufacture of prepared feeds for farm animals', 'This class includes: - manufacture of prepared feeds for farm animals, including concentrated animal feed and feed supplements - preparation of unmixed (single) feeds for farm animals', 1, 398601, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398603, '10.92', 'Manufacture of prepared pet foods', 'This class includes: - manufacture of prepared feeds for pets, including dogs, cats, birds, fish etc.', 1, 398601, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398604, '11', 'Manufacture of beverages', 'This division includes the manufacture of beverages, such as non-alcoholic beverages and mineral water, manufacture of alcoholic beverages mainly through fermentation, beer and wine, and the manufacture of distilled alcoholic beverages.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398605, '11', 'Manufacture of beverages', '', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398606, '11.01', 'Distilling, rectifying and blending of spirits', 'This class includes: - manufacture of distilled, potable, alcoholic beverages: whisky, brandy, gin, liqueurs etc. - manufacture of drinks mixed with distilled alcoholic beverages - blending of distilled spirits - production of neutral spirits', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398607, '11.02', 'Manufacture of wine from grape', 'This class includes: - manufacture of wine - manufacture of sparkling wine - manufacture of wine from concentrated grape must', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398608, '11.03', 'Manufacture of cider and other fruit wines', 'This class includes: - manufacture of fermented but not distilled alcoholic beverages: sake, cider, perry and other fruit wines', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398609, '11.04', 'Manufacture of other non-distilled fermented beverages', 'This class includes: - manufacture of vermouth and the like', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398610, '11.05', 'Manufacture of beer', 'This class includes: - manufacture of malt liquors, such as beer, ale, porter and stout', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398611, '11.06', 'Manufacture of malt', '', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398612, '11.07', 'Manufacture of soft drinks; production of mineral waters and other bottled waters', 'This class includes manufacture of non-alcoholic beverages (except non-alcoholic beer and wine): - production of natural mineral waters and other bottled waters - manufacture of soft drinks: . non-alcoholic flavoured and/or sweetened waters: lemonade, orangeade, cola, fruit drinks, tonic waters etc.', 1, 398604, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398613, '12', 'Manufacture of tobacco products', 'This division includes the processing of an agricultural product, tobacco, into a form suitable for final consumption.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398614, '12', 'Manufacture of tobacco products', '', 1, 398613, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398615, '12', 'Manufacture of tobacco products', 'This class includes: - manufacture of tobacco products and products of tobacco substitutes: cigarettes, fine cut tobacco, cigars, pipe tobacco, chewing tobacco, snuff - manufacture of "homogenised" or "reconstituted" tobacco', 1, 398613, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398616, '13', 'Manufacture of textiles', 'This division includes preparation and spinning of textile fibres as well as textile weaving, finishing of textiles and wearing apparel, manufacture of made-up textile articles, except apparel (e.g. household linen, blankets, rugs, cordage etc.). Growing of natural fibres is covered under division 01, while manufacture of synthetic fibres is a chemical process classified in class 20.60. Manufacture of wearing apparel is covered in division 14.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398617, '13.1', 'Preparation and spinning of textile fibres', '', 1, 398616, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398618, '13.1', 'Preparation and spinning of textile fibres', 'This class includes preparatory operations on textile fibres and the spinning of textile fibres. This can be done from varying raw materials, such as silk, wool, other animal, vegetable or man-made fibres, paper or glass etc. This class includes: - preparatory operations on textile fibres: . reeling and washing of silk . degreasing and carbonising of wool and dyeing of wool fleece . carding and combing of all kinds of animal, vegetable and man-made fibres - spinning and manufacture of yarn or thread for weaving or sewing, for the trade or for further processing . scutching of flax . texturising, twisting, folding, cabling and dipping of synthetic or artificial filament yarns', 1, 398617, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398619, '13.2', 'Weaving of textiles', '', 1, 398616, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398620, '13.2', 'Weaving of textiles', 'This class includes weaving of textiles. This can be done from varying raw materials, such as silk, wool, other animal, vegetable or man-made fibres, paper or glass etc. This class includes: - manufacture of broad woven cotton-type, woollen-type, worsted-type or silk-type fabrics, including from mixtures or artificial or synthetic yarns (polypropylene etc.) - manufacture of other broad woven fabrics, using flax, ramie, hemp, jute, bast fibres and special yarns', 1, 398619, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398621, '13.3', 'Finishing of textiles', '', 1, 398616, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398622, '13.3', 'Finishing of textiles', 'This class includes finishing of textiles and wearing apparel, i.e. bleaching, dyeing, dressing and similar activities. This class includes: - bleaching and dyeing of textile fibres, yarns, fabrics and textile articles, including wearing apparel - dressing, drying, steaming, shrinking, mending, sanforising, mercerising of textiles and textile articles, including wearing apparel', 1, 398621, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398623, '13.9', 'Manufacture of other textiles', 'This group includes the manufacture of products produced from textiles, except wearing apparel, such as made-up textile articles, carpets and rugs, rope, narrow woven fabrics, trimmings etc.', 1, 398616, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398624, '13.91', 'Manufacture of knitted and crocheted fabrics', 'This class includes: - manufacture and processing of knitted or crocheted fabrics: . pile and terry fabrics . net and window furnishing type fabrics knitted on Raschel or similar machines . other knitted or crocheted fabrics', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398625, '13.92', 'Manufacture of made-up textile articles, except apparel', 'This class includes: - manufacture, of made-up articles of any textile material, including of knitted or crocheted fabrics: . blankets, including travelling rugs . bed, table, toilet or kitchen linen . quilts, eiderdowns, cushions, pouffes, pillows, sleeping bags etc. - manufacture of made-up furnishing articles: . curtains, valances, blinds, bedspreads, furniture or machine covers etc. . tarpaulins, tents, camping goods, sails, sunblinds, loose covers for cars, machines or furniture etc. . flags, banners, pennants etc. . dust cloths, dishcloths and similar articles, life jackets, parachutes etc.', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398626, '13.93', 'Manufacture of carpets and rugs', 'This class includes: - manufacture of textile floor coverings: . carpets, rugs and mats, tiles', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398627, '13.94', 'Manufacture of cordage, rope, twine and netting', 'This class includes: - manufacture of twine, cordage, rope and cables of textile fibres or strip or the like, whether or not impregnated, coated, covered or sheathed with rubber or plastics - manufacture of knotted netting of twine, cordage or rope - manufacture of products of rope or netting: fishing nets, ships'' fenders, unloading cushions, loading slings, rope or cable fitted with metal rings etc.', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398628, '13.95', 'Manufacture of non-wovens and articles made from non-wovens, except apparel', 'This class includes all activities related to the manufacture of textiles or textile products, not specified elsewhere in division 13 or 14, involving a large number of processes and a great variety of goods produced.', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398629, '13.96', 'Manufacture of other technical and industrial textiles', 'This class includes: - manufacture of narrow woven fabrics, including fabrics consisting of warp without weft assembled by means of an adhesive - manufacture of labels, badges etc. - manufacture of ornamental trimmings: braids, tassels, pompons etc. - manufacture of fabrics impregnated, coated, covered or laminated with plastics - manufacture of metallised yarn or metallised gimped yarn, rubber thread and cord covered with textile material, textile yarn or strip covered, impregnated, coated or sheathed with rubber or plastics - manufacture of tyre cord fabric of high-tenacity man-made yarn - manufacture of other treated or coated fabrics: buckram and similar stiffened textile fabrics, fabrics coated with gum or amylaceous substances - manufacture of diverse textile articles: textile wicks, incandescent gas mantles and tubular gas - manufacture of mantle fabric, hosepiping, transmission or conveyor belts or belting (whether or not reinforced with metal or other material), bolting cloth, straining cloth - manufacture of automotive trimmings - manufacture of artists'' canvas boards and tracing cloth', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398630, '13.99', 'Manufacture of other textiles n.e.c.', 'This class includes: - manufacture of felt - manufacture of tulles and other net fabrics, and of lace and embroidery, in the piece, in strips or in motifs - manufacture of pressure sensitive cloth-tape - manufacture of shoe-lace, of textiles - manufacture of powder puffs and mitts', 1, 398623, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398631, '14', 'Manufacture of wearing apparel', 'This division includes all tailoring (ready-to-wear or made-to-measure), in all materials (e.g. leather, fabric, knitted and crocheted fabrics etc.), of all items of clothing (e.g. outerwear, underwear for men, women or children; work, city or casual clothing etc.) and accessories. There is no distinction made between clothing for adults and clothing for children, or between modern and traditional clothing.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398632, '14.1', 'Manufacture of wearing apparel, except fur apparel', 'This group includes manufacture of wearing apparel. The material used may be of any kind and may be coated, impregnated or rubberised.', 1, 398631, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398633, '14.11', 'Manufacture of leather clothes', 'This class includes: - manufacture of wearing apparel made of leather or composition leather including leather industrial work accessories as welder''s leather aprons', 1, 398632, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398634, '14.12', 'Manufacture of workwear', '', 1, 398632, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398635, '14.13', 'Manufacture of other outerwear', 'This class includes: - manufacture of other outerwear made of woven, knitted or crocheted fabric, non-wovens etc. for men, women and children: . coats, suits, ensembles, jackets, trousers, skirts etc.', 1, 398632, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398636, '14.14', 'Manufacture of underwear', 'This class includes: - manufacture of underwear and nightwear made of woven, knitted or crocheted fabric, lace etc. for men, women and children: . shirts, T-shirts, underpants, briefs, pyjamas, nightdresses, dressing gowns, blouses, slips, brassieres, corsets etc.', 1, 398632, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398637, '14.19', 'Manufacture of other wearing apparel and accessories', 'This class includes: - manufacture of babies'' garments, tracksuits, ski suits, swimwear etc. - manufacture of hats and caps - manufacture of other clothing accessories: gloves, belts, shawls, ties, cravats, hairnets etc.', 1, 398632, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398638, '14.2', 'Manufacture of articles of fur', '', 1, 398631, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398639, '14.2', 'Manufacture of articles of fur', 'This class includes: - manufacture of articles made of fur skins: . fur wearing apparel and clothing accessories . assemblies of fur skins such as "dropped" fur skins, plates, mats, strips etc. . diverse articles of fur skins: rugs, unstuffed pouffes, industrial polishing cloths', 1, 398638, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398640, '14.3', 'Manufacture of knitted and crocheted apparel', '', 1, 398631, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398641, '14.31', 'Manufacture of knitted and crocheted hosiery', 'This class includes: - manufacture of hosiery, including socks, tights and pantyhose', 1, 398640, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398642, '14.39', 'Manufacture of other knitted and crocheted apparel', 'This class includes: - manufacture of knitted or crocheted wearing apparel and other made-up articles directly into shape: pullovers, cardigans, jerseys, waistcoats and similar articles', 1, 398640, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398643, '15', 'Manufacture of leather and related products', 'This division includes dressing and dyeing of fur and the transformation of hides into leather by tanning or curing and fabricating the leather into products for final consumption.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398644, '15.1', 'Tanning and dressing of leather; manufacture of luggage, handbags, saddlery and harness; dressing and dyeing of fur', 'This group includes the manufacture of leather and fur and products thereof.', 1, 398643, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398645, '15.11', 'Tanning and dressing of leather; dressing and dyeing of fur', 'This class includes: - tanning, dyeing and dressing of hides and skins - manufacture of chamois dressed, parchment dressed, patent or metallised leathers - manufacture of composition leather - scraping, shearing, plucking, currying, tanning, bleaching and dyeing of fur skins and hides with the hair on', 1, 398644, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398646, '15.12', 'Manufacture of luggage, handbags and the like, saddlery and harness', 'This class includes: - manufacture of luggage, handbags and the like, of leather, composition leather or any other material, such as plastic sheeting, textile materials, vulcanised fibre or paperboard, where the same technology is used as for leather - manufacture of saddlery and harness - manufacture of non-metallic watch bands (e.g. fabric, leather, plastic) - manufacture of diverse articles of leather or composition leather: driving belts, packings etc. - manufacture of shoe-lace, of leather - manufacture of horse whips and riding crops', 1, 398644, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398647, '15.2', 'Manufacture of footwear', '', 1, 398643, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398648, '15.2', 'Manufacture of footwear', 'This class includes: - manufacture of footwear for all purposes, of any material, by any process, including moulding (see below for exceptions) - manufacture of leather parts of footwear: manufacture of uppers and parts of uppers, outer and inner soles, heels etc. - manufacture of gaiters, leggings and similar articles', 1, 398647, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398649, '16', 'Manufacture of wood and of products of wood and cork, except furniture; manufacture of articles of straw and plaiting materials', 'This division includes the manufacture of wood products, such as lumber, plywood, veneers, wood containers, wood flooring, wood trusses, and prefabricated wood buildings. The production processes include sawing, planing, shaping, laminating, and assembling of wood products starting from logs that are cut into bolts, or lumber that may then be cut further, or shaped by lathes or other shaping tools. The lumber or other transformed wood shapes may also be subsequently planed or smoothed, and assembled into finished products, such as wood containers. With the exception of sawmilling, this division is subdivided mainly based on the specific products manufactured.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398650, '16.1', 'Sawmilling and planing of wood', '', 1, 398649, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398651, '16.1', 'Sawmilling and planing of wood', 'This class includes: - sawing, planing and machining of wood - slicing, peeling or chipping logs - manufacture of wooden railway sleepers - manufacture of unassembled wooden flooring - manufacture of wood wool, wood flour, chips, particles', 1, 398650, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398652, '16.2', 'Manufacture of products of wood, cork, straw and plaiting materials', 'This group includes the manufacture of products of wood, cork, straw or plaiting materials, including basic shapes as well as assembled products.', 1, 398649, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398653, '16.21', 'Manufacture of veneer sheets and wood-based panels', 'This class includes: - manufacture of veneer sheets thin enough to be used for veneering, making plywood or other purposes: . smoothed, dyed, coated, impregnated, reinforced (with paper or fabric backing) . made in the form of motifs - manufacture of plywood, veneer panels and similar laminated wood boards and sheets - manufacture of oriented strand board (OSB) and other particle board - manufacture of medium density fibreboard (MDF) and other fibreboard - manufacture of densified wood - manufacture of glue laminated wood, laminated veneer wood', 1, 398652, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398654, '16.22', 'Manufacture of assembled parquet floors', 'This class includes: - manufacture of wooden parquet floor blocks, strips etc., assembled into panels', 1, 398652, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398655, '16.23', 'Manufacture of other builders'' carpentry and joinery', 'This class includes: - manufacture of wooden goods intended to be used primarily in the construction industry: . beams, rafters, roof struts . glue-laminated and metal connected, prefabricated wooden roof trusses . doors, windows, shutters and their frames, whether or not containing metal fittings, such as hinges, locks etc. . stairs, railings . wooden beadings and mouldings, shingles and shakes - manufacture of prefabricated buildings, or elements thereof, predominantly of wood, e.g. saunas - manufacture of mobile homes - manufacture of wood partitions (except free standing)', 1, 398652, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398656, '16.24', 'Manufacture of wooden containers', 'This class includes: - manufacture of packing cases, boxes, crates, drums and similar packings of wood - manufacture of pallets, box pallets and other load boards of wood - manufacture of barrels, vats, tubs and other coopers'' products of wood - manufacture of wooden cable-drums', 1, 398652, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398657, '16.29', 'Manufacture of other products of wood; manufacture of articles of cork, straw and plaiting materials', 'This class includes: - manufacture of various wood products: . wooden handles and bodies for tools, brooms, brushes . wooden boot or shoe lasts and trees, clothes hangers . household utensils and kitchenware of wood . wooden statuettes and ornaments, wood marquetry, inlaid wood . wooden cases for jewellery, cutlery and similar articles . wooden spools, cops, bobbins, sewing thread reels and similar articles of turned wood . other articles of wood - natural cork processing, manufacture of agglomerated cork - manufacture of articles of natural or agglomerated cork, including floor coverings - manufacture of plaits and products of plaiting materials: mats, matting, screens, cases etc. - manufacture of basket-ware and wickerwork - manufacture of fire logs and pellets for energy, made of pressed wood or substitute materials like coffee or soybean grounds - manufacture of wooden mirror and picture frames - manufacture of frames for artists'' canvases - manufacture of wooden shoe parts (e.g. heels and lasts) - manufacture of handles for umbrellas, canes and similar - manufacture of blocks for the manufacture of smoking pipes', 1, 398652, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398658, '17', 'Manufacture of paper and paper products', 'This division includes the manufacture of pulp, paper and converted paper products. The manufacture of these products is grouped together because they constitute a series of vertically connected processes. More than one activity is often carried out in a single unit. There are essentially three activities: The manufacture of pulp involves separating the cellulose fibres from other matter in wood, or dissolving and de-inking of used paper, and mixing in small amounts of reagents to reinforce the binding of the fibres. The manufacture of paper involves releasing pulp onto a moving wire mesh so as to form a continuous sheet. Converted paper products are made from paper and other materials by various techniques. The paper articles may be printed (e.g. wallpaper, gift wrap etc.), as long as the printing of information is not the main purpose. The production of pulp, paper and paperboard in bulk is included in group 17.1, while the remaining classes include the production of further-processed paper and paper products.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398659, '17.1', 'Manufacture of pulp, paper and paperboard', '', 1, 398658, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398660, '17.11', 'Manufacture of pulp', 'This class includes: - manufacture of bleached, semi-bleached or unbleached paper pulp by mechanical, chemical (dissolving or non-dissolving) or semi-chemical processes - manufacture of cotton-linters pulp - removal of ink and manufacture of pulp from waste paper', 1, 398659, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398661, '17.12', 'Manufacture of paper and paperboard', 'This class includes: - manufacture of paper and paperboard intended for further industrial processing', 1, 398659, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398662, '17.2', 'Manufacture of articles of paper and paperboard ', '', 1, 398658, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398663, '17.21', 'Manufacture of corrugated paper and paperboard and of containers of paper and paperboard', 'This class includes: - manufacture of corrugated paper and paperboard - manufacture of containers of corrugated paper or paperboard - manufacture of folding paperboard containers - manufacture of containers of solid board - manufacture of other containers of paper and paperboard - manufacture of sacks and bags of paper - manufacture of office box files and similar articles', 1, 398662, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398664, '17.22', 'Manufacture of household and sanitary goods and of toilet requisites', 'This class includes: - manufacture of household and personal hygiene paper and cellulose wadding products: . cleansing tissues . handkerchiefs, towels, serviettes . toilet paper . sanitary towels and tampons, napkins and napkin liners for babies . cups, dishes and trays - manufacture of textile wadding and articles of wadding: sanitary towels, tampons etc.', 1, 398662, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398665, '17.23', 'Manufacture of paper stationery', 'This class includes: - manufacture of printing and writing paper ready for use - manufacture of computer printout paper ready for use - manufacture of self-copy paper ready for use - manufacture of duplicator stencils and carbon paper ready for use - manufacture of gummed or adhesive paper ready for use - manufacture of envelopes and letter-cards - manufacture of educational and commercial stationery (notebooks, binders, registers, accounting books, business forms etc.), when the printed information is not the main characteristic - manufacture of boxes, pouches, wallets and writing compendiums containing an assortment of paper stationery', 1, 398662, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398666, '17.24', 'Manufacture of wallpaper', 'This class includes: - manufacture of wallpaper and similar wall coverings, including vinyl-coated and textile wallpaper', 1, 398662, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398667, '17.29', 'Manufacture of other articles of paper and paperboard', 'This class includes: - manufacture of labels - manufacture of filter paper and paperboard - manufacture of paper and paperboard bobbins, spools, cops etc. - manufacture of egg trays and other moulded pulp packaging products etc. - manufacture of paper novelties - manufacture of paper or paperboard cards for use on Jacquard machines', 1, 398662, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398668, '18', 'Printing and reproduction of recorded media', 'This division includes printing of products, such as newspapers, books, periodicals, business forms, greeting cards, and other materials, and associated support activities, such as bookbinding, plate-making services, and data imaging. The support activities included here are an integral part of the printing industry, and a product (a printing plate, a bound book, or a computer disk or file) that is an integral part of the printing industry is almost always provided by these operations. Processes used in printing include a variety of methods for transferring an image from a plate, screen or computer file to a medium, such as paper, plastics, metal, textile articles, or wood. The most prominent of these methods entails the transfer of the image from a plate or screen to the medium through lithographic, gravure, screen or flexographic printing. Often a computer file is used to directly ''''drive'''' the printing mechanism to create the image or electrostatic and other types of equipment (digital or non-impact printing). Though printing and publishing can be carried out by the same unit (a newspaper, for example), it is less and less the case that these distinct activities are carried out in the same physical location.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398669, '18.1', 'Printing and service activities related to printing', 'This group includes printing of products, such as newspapers, books, periodicals, business forms, greeting cards, and other materials, and associated support activities, such as bookbinding, plate-making services, and data imaging. Printing can be done using various techniques and on different materials.', 1, 398668, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398670, '18.11', 'Printing of newspapers', '', 1, 398669, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398671, '18.12', 'Other printing', 'This class includes: - printing of magazines and other periodicals, appearing less than four times a week - printing of books and brochures, music and music manuscripts, maps, atlases, posters, advertising catalogues, prospectuses and other printed advertising, postage stamps, taxation stamps, documents of title, cheques and other security papers, smart cards, albums, diaries, calendars and other commercial printed matter, personal stationery and other printed matter by letterpress, offset, photogravure, flexographic, screen printing and other printing presses, duplication machines, computer printers, embossers etc., including quick printing - printing directly onto textiles, plastic, glass, metal, wood and ceramics The material printed is typically copyrighted.', 1, 398669, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398672, '18.13', 'Pre-press and pre-media services', 'This class includes: - composing, typesetting, phototypesetting, pre-press data input including scanning and optical character recognition, electronic make-up - preparation of data files for multi-media (printing on paper, CD-ROM, Internet) applications - plate-making services including image setting and plate setting (for the printing processes letterpress and offset) - cylinder preparation: engraving or etching of cylinders for gravure printing - plate processing: "computer to plate" CTP (also photopolymer plates) - preparation of plates and dies for relief stamping or printing - preparation of: . artistic works of technical character, such as preparation of lithographic stones and wood blocks . presentation media, e.g. overhead foils and other forms of presentation . sketches, layouts, dummies, etc. . production of proofs', 1, 398669, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398673, '18.14', 'Binding and related services', 'This class includes: - trade binding, sample mounting and post press services in support of printing activities, e.g. trade binding and finishing of books, brochures, magazines, catalogues, etc., by folding, cutting and trimming, assembling, stitching, thread sewing, adhesive binding, cutting and cover laying, gluing, collating, basting, gold stamping; spiral binding and plastic wire binding - binding and finishing of printed paper or printed cardboard, by folding, stamping, drilling, punching, perforating, embossing, sticking, gluing, laminating - finishing services for CD-ROMs - mailing finishing services such as customisation, envelope preparation - other finishing activities such as die, sinking or stamping, Braille copying', 1, 398669, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398674, '18.2', 'Reproduction of recorded media', '', 1, 398668, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398675, '18.2', 'Reproduction of recorded media', 'This class includes: - reproduction from master copies of gramophone records, compact discs and tapes with music or other sound recordings - reproduction from master copies of records, compact discs and tapes with motion pictures and other video recordings - reproduction from master copies of software and data on discs and tapes', 1, 398674, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398676, '19', 'Manufacture of coke and refined petroleum products', 'This division includes the transformation of crude petroleum and coal into usable products. The dominant process is petroleum refining, which involves the separation of crude petroleum into component products through such techniques as cracking and distillation. This division includes the manufacture of gases such as ethane, propane and butane as products of petroleum refineries.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398677, '19.1', 'Manufacture of coke oven products', '', 1, 398676, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398678, '19.1', 'Manufacture of coke oven products', 'This class includes: - operation of coke ovens - production of coke and semi-coke - production of pitch and pitch coke - production of coke oven gas - production of crude coal and lignite tars - agglomeration of coke', 1, 398677, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398679, '19.2', 'Manufacture of refined petroleum products', '', 1, 398676, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398680, '19.2', 'Manufacture of refined petroleum products', 'This class includes the manufacture of liquid or gaseous fuels or other products from crude petroleum, bituminous minerals or their fractionation products. Petroleum refining involves one or more of the following activities: fractionation; straight distillation of crude oil; and cracking. This class includes: - production of motor fuel: gasoline, kerosene etc. - production of fuel: light, medium and heavy fuel oil, refinery gases such as ethane, propane, butane etc. - manufacture of oil-based lubricating oils or greases, including from waste oil - manufacture of products for the petrochemical industry and for the manufacture of road coverings - manufacture of various products: white spirit, vaseline, paraffin wax, petroleum jelly etc. - manufacture of petroleum briquettes - blending of biofuels, i.e. blending of alcohols with petroleum (e.g. gasohol)', 1, 398679, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398681, '20', 'Manufacture of chemicals and chemical products', 'This division includes the transformation of organic and inorganic raw materials by a chemical process and the formation of products. It distinguishes the production of basic chemicals that constitute the first industry group from the production of intermediate and end products produced by further processing of basic chemicals that make up the remaining industry classes.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398682, '20.1', 'Manufacture of basic chemicals, fertilisers and nitrogen compounds, plastics and synthetic rubber in primary forms', 'This group includes the manufacture of basic chemical products, fertilisers and associated nitrogen compounds, as well as plastics and synthetic rubber in primary forms.', 1, 398681, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398683, '20.11', 'Manufacture of industrial gases', 'This class includes: - manufacture of liquefied or compressed inorganic industrial or medical gases: . elemental gases . liquid or compressed air . refrigerant gases . mixed industrial gases . inert gases such as carbon dioxide . isolating gases', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398684, '20.12', 'Manufacture of dyes and pigments', 'This class includes: - manufacture of dyes and pigments from any source in basic form or as concentrate', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398685, '20.13', 'Manufacture of other inorganic basic chemicals', 'This class includes the manufacture of chemicals using basic processes. The output of these processes are usually separate chemical elements or separate chemically defined compounds. This class includes: - manufacture of chemical elements (except industrial gases and basic metals) - manufacture of inorganic acids except nitric acid - manufacture of alkalis, lyes and other inorganic bases except ammonia - manufacture of other inorganic compounds - roasting of iron pyrites - manufacture of distilled water', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398686, '20.14', 'Manufacture of other organic basic chemicals', 'This class includes the manufacture of chemicals using basic processes, such as thermal cracking and distillation. The output of these processes are usually separate chemical elements or separate chemically defined compounds. This class includes: - manufacture of basic organic chemicals: . acyclic hydrocarbons, saturated and unsaturated . cyclic hydrocarbons, saturated and unsaturated . acyclic and cyclic alcohols . mono- and polycarboxylic acids, including acetic acid . other oxygen-function compounds, including aldehydes, ketones, quinones and dual or poly oxygen-function compounds . synthetic glycerol . nitrogen-function organic compounds, including amines . fermentation of sugarcane, corn or similar to produce alcohol and esters . other organic compounds, including wood distillation products (e.g. charcoal) etc. - manufacture of synthetic aromatic products - distillation of coal tar', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398687, '20.15', 'Manufacture of fertilisers and nitrogen compounds', 'This class includes: - manufacture of fertilisers: . straight or complex nitrogenous, phosphatic or potassic fertilisers . urea, crude natural phosphates and crude natural potassium salts - manufacture of associated nitrogen products: . nitric and sulphonitric acids, ammonia, ammonium chloride, ammonium carbonate, nitrites and nitrates of potassium', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398688, '20.16', 'Manufacture of plastics in primary forms', 'This class includes the manufacture of resins, plastics materials and non-vulcanisable thermoplastic elastomers, the mixing and blending of resins on a custom basis, as well as the manufacture of non-customised synthetic resins. This class includes: - manufacture of plastics in primary forms: . polymers, including those of ethylene, propylene, styrene, vinyl chloride, vinyl acetate and acrylics . polyamides . phenolic and epoxide resins and polyurethanes . alkyd and polyester resins and polyethers . silicones . ion-exchangers based on polymers', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398689, '20.17', 'Manufacture of synthetic rubber in primary forms', 'This class includes: - manufacture of synthetic rubber in primary forms: . synthetic rubber . factice - manufacture of mixtures of synthetic rubber and natural rubber or rubber-like gums (e.g. balata)', 1, 398682, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398690, '20.2', 'Manufacture of pesticides and other agrochemical products', '', 1, 398681, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398691, '20.2', 'Manufacture of pesticides and other agrochemical products', 'This class includes: - manufacture of insecticides, rodenticides, fungicides, herbicides, acaricides, molluscicides, biocides - manufacture of anti-sprouting products, plant growth regulators - manufacture of disinfectants (for agricultural and other use) - manufacture of other agrochemical products n.e.c.', 1, 398690, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398692, '20.3', 'Manufacture of paints, varnishes and similar coatings, printing ink and mastics', '', 1, 398681, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398693, '20.3', 'Manufacture of paints, varnishes and similar coatings, printing ink and mastics', 'This class includes: - manufacture of paints and varnishes, enamels or lacquers - manufacture of prepared pigments and dyes, opacifiers and colours - manufacture of vitrifiable enamels and glazes and engobes and similar preparations - manufacture of mastics - manufacture of caulking compounds and similar non-refractory filling or surfacing preparations - manufacture of organic composite solvents and thinners - manufacture of prepared paint or varnish removers - manufacture of printing ink', 1, 398692, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398694, '20.4', 'Manufacture of soap and detergents, cleaning and polishing preparations, perfumes and toilet preparations', '', 1, 398681, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398695, '20.41', 'Manufacture of soap and detergents, cleaning and polishing preparations', 'This class includes: - manufacture of organic surface-active agents - manufacture of paper, wadding, felt etc. coated or covered with soap or detergent - manufacture of glycerol - manufacture of soap, except cosmetic soap - manufacture of surface-active preparations: . washing powders in solid or liquid form and detergents . dish-washing preparations . textile softeners - manufacture of cleaning and polishing products: . preparations for perfuming or deodorising rooms . artificial waxes and prepared waxes . polishes and creams for leather . polishes and creams for wood . polishes for coachwork, glass and metal . scouring pastes and powders, including paper, wadding etc. coated or covered with these', 1, 398694, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398696, '20.42', 'Manufacture of perfumes and toilet preparations', 'This class includes: - manufacture of perfumes and toilet preparations: . perfumes and toilet water . beauty and make-up preparations . sunburn prevention and suntan preparations . manicure and pedicure preparations . shampoos, hair lacquers, waving and straightening preparations . dentifrices and preparations for oral hygiene, including denture fixative preparations . shaving preparations, including pre-shave and aftershave preparations . deodorants and bath salts . depilatories - manufacture of cosmetic soap', 1, 398694, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398697, '20.5', 'Manufacture of other chemical products', 'This group includes the manufacture of explosives and pyrotechnic products, glues, essential oils and chemical products n.e.c., e.g. photographic chemical material (including film and sensitised paper), composite diagnostic preparations etc.', 1, 398681, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398698, '20.51', 'Manufacture of explosives', 'This class includes: - manufacture of propellant powders - manufacture of explosives and pyrotechnic products, including percussion caps, detonators, signalling flares etc.', 1, 398697, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398699, '20.52', 'Manufacture of glues', 'This class includes: - manufacture of glues and prepared adhesives, including rubber-based glues and adhesives', 1, 398697, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398700, '20.53', 'Manufacture of essential oils', 'This class includes: - manufacture of extracts of natural aromatic products - manufacture of resinoids - manufacture of mixtures of odoriferous products for the manufacture of perfumes or food', 1, 398697, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398701, '20.59', 'Manufacture of other chemical products n.e.c.', 'This class includes: - manufacture of photographic plates, films, sensitised paper and other sensitised unexposed materials - manufacture of chemical preparations for photographic uses - manufacture of gelatine and its derivatives - manufacture of various chemical products: . peptones, peptone derivatives, other protein substances and their derivatives n.e.c. . chemically modified oils and fats . materials used in the finishing of textiles and leather . powders and pastes used in soldering, brazing or welding . substances used to pickle metal . prepared additives for cements . activated carbon, lubricating oil additives, prepared rubber accelerators, catalysts and other chemical products for industrial use . anti-knock preparations, antifreeze preparations . liquids for hydraulic transmission . composite diagnostic or laboratory reagents', 1, 398697, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398702, '20.6', 'Manufacture of man-made fibres', '', 1, 398681, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398703, '20.6', 'Manufacture of man-made fibres', 'This class includes: - manufacture of synthetic or artificial filament tow - manufacture of synthetic or artificial staple fibres, not carded, combed or otherwise processed for spinning - manufacture of synthetic or artificial filament yarn, including high-tenacity yarn - manufacture of synthetic or artificial monofilament or strip', 1, 398702, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398704, '21', 'Manufacture of basic pharmaceutical products and pharmaceutical preparations', 'This division includes the manufacture of basic pharmaceutical products and pharmaceutical preparations.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398705, '21.1', 'Manufacture of basic pharmaceutical products', '', 1, 398704, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398706, '21.1', 'Manufacture of basic pharmaceutical products', 'This class includes: - manufacture of medicinal active substances to be used for their pharmacological properties in the manufacture of medicaments: antibiotics, basic vitamins, salicylic and O-acetylsalicylic acids etc. - processing of blood', 1, 398705, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398707, '21.2', 'Manufacture of pharmaceutical preparations', '', 1, 398704, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398708, '21.2', 'Manufacture of pharmaceutical preparations', 'This class includes: - manufacture of medicaments: . antisera and other blood fractions . vaccines . diverse medicaments, including homeopathic preparations - manufacture of chemical contraceptive products for external use and hormonal contraceptive medicaments - manufacture of medical diagnostic preparations, including pregnancy tests - manufacture of radioactive in-vivo diagnostic substances - manufacture of biotech pharmaceuticals', 1, 398707, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398709, '22', 'Manufacture of rubber and plastic products', 'This division includes the manufacture of rubber and plastics products. This division is characterised by the raw materials used in the manufacturing process. However, this does not imply that the manufacture of all products made of these materials is classified here.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398710, '22.1', 'Manufacture of rubber products', 'This group includes the manufacture of rubber products.', 1, 398709, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398711, '22.11', 'Manufacture of rubber tyres and tubes; retreading and rebuilding of rubber tyres', 'This class includes: - manufacture of rubber tyres for vehicles, equipment, mobile machinery, aircraft, toy, furniture and other uses: . pneumatic tyres . solid or cushion tyres - manufacture of inner tubes for tyres - manufacture of interchangeable tyre treads, tyre flaps, "camelback" strips for retreading tyres etc. - tyre rebuilding and retreading', 1, 398710, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398712, '22.19', 'Manufacture of other rubber products', 'This class includes: - manufacture of other products of natural or synthetic rubber, unvulcanised, vulcanised or hardened: . rubber plates, sheets, strip, rods, profile shapes . tubes, pipes and hoses . rubber conveyor or transmission belts or belting . rubber hygienic articles: sheath contraceptives, teats, hot water bottles etc. . rubber articles of apparel (if only sealed together, not sewn) . rubber sole and other rubber parts of footwear . rubber thread and rope . rubberised yarn and fabrics . rubber rings, fittings and seals . rubber roller coverings . inflatable rubber mattresses . inflatable balloons - manufacture of rubber brushes - manufacture of hard rubber pipe stems - manufacture of hard rubber combs, hair pins, hair rollers, and similar', 1, 398710, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398713, '22.2', 'Manufacture of plastic products', 'This group comprises processing new or spent (i.e., recycled) plastics resins into intermediate or final products, using such processes as compression moulding; extrusion moulding; injection moulding; blow moulding; and casting. For most of these, the production process is such that a wide variety of products can be made.', 1, 398709, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398714, '22.21', 'Manufacture of plastic plates, sheets, tubes and profiles', 'This class includes: - manufacture of semi-manufactures of plastic products: . plastic plates, sheets, blocks, film, foil, strip etc. (whether self-adhesive or not) - manufacture of finished plastic products: . plastic tubes, pipes and hoses; hose and pipe fittings . cellophane film or sheet', 1, 398713, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398715, '22.22', 'Manufacture of plastic packing goods', 'This class includes: - manufacture of plastic articles for the packing of goods: . plastic bags, sacks, containers, boxes, cases, carboys, bottles etc.', 1, 398713, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398716, '22.23', 'Manufacture of builders’ ware of plastic', 'This class includes: - manufacture of builders'' plastics ware: . plastic doors, windows, frames, shutters, blinds, skirting boards . tanks, reservoirs . plastic floor, wall or ceiling coverings in rolls or in the form of tiles etc. . plastic sanitary ware like plastic baths, shower baths, washbasins, lavatory pans, flushing cisterns etc. - manufacture of resilient floor coverings, such as vinyl, linoleum etc. - manufacture of artificial stone (e.g. cultured marble)', 1, 398713, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398717, '22.29', 'Manufacture of other plastic products', 'This class includes: - manufacture of plastic tableware, kitchenware and toilet articles - manufacture of diverse plastic products: . plastic headgear, insulating fittings, parts of lighting fittings, office or school supplies, articles of apparel (if only sealed together, not sewn), fittings for furniture, statuettes, transmission and conveyer belts, self-adhesive tapes of plastic, plastic shoe lasts, plastic cigar and cigarette holders, combs, plastics hair curlers, plastics novelties, etc.', 1, 398713, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398718, '23', 'Manufacture of other non-metallic mineral products', 'This division includes manufacturing activities related to a single substance of mineral origin. This division includes the manufacture of glass and glass products (e.g. flat glass, hollow glass, fibres, technical glassware etc.), ceramic products, tiles and baked clay products, and cement and plaster, from raw materials to finished articles.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398719, '23.1', 'Manufacture of glass and glass products', 'This group includes glass in all its forms, made by any process, and articles of glass.', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398720, '23.11', 'Manufacture of flat glass', 'This class includes: - manufacture of flat glass, including wired, coloured or tinted flat glass', 1, 398719, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398721, '23.12', 'Shaping and processing of flat glass', 'This class includes: - manufacture of toughened or laminated flat glass - manufacture of glass mirrors - manufacture of multiple-walled insulating units of glass', 1, 398719, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398722, '23.13', 'Manufacture of hollow glass', 'This class includes: - manufacture of bottles and other containers of glass or crystal - manufacture of drinking glasses and other domestic glass or crystal articles', 1, 398719, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398723, '23.14', 'Manufacture of glass fibres', 'This class includes: - manufacture of glass fibres, including glass wool and non-woven products thereof', 1, 398719, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398724, '23.19', 'Manufacture and processing of other glass, including technical glassware', 'This class includes: - manufacture of laboratory, hygienic or pharmaceutical glassware - manufacture of clock or watch glasses, optical glass and optical elements not optically worked - manufacture of glassware used in imitation jewellery - manufacture of glass insulators and glass insulating fittings - manufacture of glass envelopes for lamps - manufacture of glass figurines - manufacture of glass paving blocks - manufacture of glass in rods or tubes', 1, 398719, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398725, '23.2', 'Manufacture of refractory products', '', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398726, '23.2', 'Manufacture of refractory products', 'This class includes the manufacture of intermediate products from mined or quarried non-metallic minerals, such as sand, gravel, stone or clay. This class includes: - manufacture of refractory mortars, concretes etc. - manufacture of refractory ceramic goods: . heat-insulating ceramic goods of siliceous fossil meals . refractory bricks, blocks and tiles etc. . retorts, crucibles, muffles, nozzles, tubes, pipes etc.', 1, 398725, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398727, '23.3', 'Manufacture of clay building materials', '', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398728, '23.31', 'Manufacture of ceramic tiles and flags', 'This class includes: - manufacture of non-refractory ceramic hearth or wall tiles, mosaic cubes etc. - manufacture of non-refractory ceramic flags and paving', 1, 398727, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398729, '23.32', 'Manufacture of bricks, tiles and construction products, in baked clay', 'This class includes: - manufacture of structural non-refractory clay building materials: . manufacture of ceramic bricks, roofing tiles, chimney pots, pipes, conduits etc. - manufacture of flooring blocks in baked clay', 1, 398727, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398730, '23.4', 'Manufacture of other porcelain and ceramic products', 'This group includes the manufacture of final products from mined or quarried non-metallic minerals, such as sand, gravel, stone or clay.', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398731, '23.41', 'Manufacture of ceramic household and ornamental articles', 'This class includes: - manufacture of ceramic tableware and other domestic or toilet articles - manufacture of statuettes and other ornamental ceramic articles', 1, 398730, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398732, '23.42', 'Manufacture of ceramic sanitary fixtures', 'This class includes: - manufacture of ceramic sanitary fixtures, e.g. sinks, baths, bidets, water closet pans etc. - manufacture of other ceramic fixtures', 1, 398730, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398733, '23.43', 'Manufacture of ceramic insulators and insulating fittings', 'This class includes: - manufacture of electrical insulators and insulating fittings of ceramics', 1, 398730, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398734, '23.44', 'Manufacture of other technical ceramic products', 'This class includes: - manufacture of ceramic and ferrite magnets - manufacture of ceramic laboratory, chemical and industrial products', 1, 398730, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398735, '23.49', 'Manufacture of other ceramic products', 'This class includes: - manufacture of ceramic pots, jars and similar articles of a kind used for conveyance or packing of goods - manufacture of ceramic products n.e.c.', 1, 398730, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398736, '23.5', 'Manufacture of cement, lime and plaster', '', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398737, '23.51', 'Manufacture of cement', 'This class includes: - manufacture of clinkers and hydraulic cements, including Portland, aluminous cement, slag cement and superphosphate cements', 1, 398736, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398738, '23.52', 'Manufacture of lime and plaster', 'This class includes: - manufacture of quicklime, slaked lime and hydraulic lime - manufacture of plasters of calcined gypsum or calcined sulphate', 1, 398736, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398739, '23.6', 'Manufacture of articles of concrete, cement and plaster', '', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398740, '23.61', 'Manufacture of concrete products for construction purposes', 'This class includes: - manufacture of precast concrete, cement or artificial stone articles for use in construction: . tiles, flagstones, bricks, boards, sheets, panels, pipes, posts etc. - manufacture of prefabricated structural components for building or civil engineering of cement, concrete or artificial stone', 1, 398739, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398741, '23.62', 'Manufacture of plaster products for construction purposes', 'This class includes: - manufacture of plaster articles for use in construction: . boards, sheets, panels etc.', 1, 398739, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398742, '23.63', 'Manufacture of ready-mixed concrete', 'This class includes: - manufacture of ready-mix and dry-mix concrete and mortars', 1, 398739, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398743, '23.64', 'Manufacture of mortars', 'This class includes: - manufacture of powdered mortars', 1, 398739, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398744, '23.65', 'Manufacture of fibre cement', 'This class includes: - manufacture of building materials of vegetable substances (wood wool, straw, reeds, rushes) agglomerated with cement, plaster or other mineral binder - manufacture of articles of asbestos-cement or cellulose fibre-cement or the like: . corrugated sheets, other sheets, panels, tiles, tubes, pipes, reservoirs, troughs, basins, sinks, jars, furniture, window frames etc.', 1, 398739, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398745, '23.69', 'Manufacture of other articles of concrete, plaster and cement', 'This class includes: - manufacture of other articles of concrete, plaster, cement or artificial stone: . statuary, furniture, bas- and haut-reliefs, vases, flowerpots etc.', 1, 398739, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398746, '23.7', 'Cutting, shaping and finishing of stone', '', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398747, '23.7', 'Cutting, shaping and finishing of stone', 'This class includes: - cutting, shaping and finishing of stone for use in construction, in cemeteries, on roads, as roofing etc. - manufacture of stone furniture', 1, 398746, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398748, '23.9', 'Manufacture of abrasive products and non-metallic mineral products n.e.c.', 'This group includes the manufacture of other non-metallic mineral products.', 1, 398718, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398749, '23.91', 'Production of abrasive products', 'This class includes: - manufacture of millstones, sharpening or polishing stones and natural or artificial abrasive products on a support, including abrasive products on a soft base (e.g. sandpaper)', 1, 398748, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398750, '23.99', 'Manufacture of other non-metallic mineral products n.e.c.', 'This class includes: - manufacture of friction material and unmounted articles thereof with a base of mineral substances or of cellulose - manufacture of mineral insulating materials: . slag wool, rock wool and similar mineral wools; exfoliated vermiculite, expanded clays and similar heat-insulating, sound-insulating or sound-absorbing materials - manufacture of articles of diverse mineral substances: . worked mica and articles of mica, of peat, of graphite (other than electrical articles) etc. - manufacture of articles of asphalt or similar material, e.g. asphalt-based adhesives, coal tar pitch etc. - manufacture of carbon and graphite fibres and products (except electrodes and electrical applications) - manufacture of artificial corundum', 1, 398748, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398751, '24', 'Manufacture of basic metals', 'This division includes the activities of smelting and/or refining ferrous and non-ferrous metals from ore, pig or scrap, using electrometallurgic and other process metallurgic techniques.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398752, '24.1', 'Manufacture of basic iron and steel and of ferro-alloys', 'This group includes activities such as direct reduction of iron ore, production of pig iron in molten or solid form, conversion of pig iron into steel, manufacture of ferroalloys and manufacture of steel products.', 1, 398751, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398753, '24.1', 'Manufacture of basic iron and steel and of ferro-alloys ', 'This class includes: - operation of blast furnaces, steel converters, rolling and finishing mills - production of pig iron and spiegeleisen in pigs, blocks or other primary forms - production of ferro-alloys - production of ferrous products by direct reduction of iron and other spongy ferrous products - production of iron of exceptional purity by electrolysis or other chemical processes - remelting of scrap ingots of iron or steel - production of granular iron and iron powder - production of steel in ingots or other primary forms - production of semi-finished products of steel - manufacture of hot-rolled and cold-rolled flat-rolled products of steel - manufacture of hot-rolled bars and rods of steel - manufacture of hot-rolled open sections of steel - manufacture of sheet piling of steel and welded open sections of steel - manufacture of railway track materials (unassembled rails) of steel', 1, 398752, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398754, '24.2', 'Manufacture of tubes, pipes, hollow profiles and related fittings, of steel', '', 1, 398751, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398755, '24.2', 'Manufacture of tubes, pipes, hollow profiles and related fittings, of steel', 'This class includes: - manufacture of seamless tubes and pipes of circular or non-circular cross section and of blanks of circular cross section, for further processing, by hot rolling, hot extrusion or by other hot processes of an intermediate product which can be a bar or a billet obtained by hot rolling or continuous casting - manufacture of precision and non-precision seamless tubes and pipes from hot rolled or hot extruded blanks by further processing, by cold-drawing or cold-rolling of tubes and pipes of circular cross section and by cold drawing only for tubes and pipes of non circular cross section and hollow profiles - manufacture of welded tubes and pipes of an external diameter exceeding 406,4 mm, cold formed from hot rolled flat products and longitudinally or spirally welded - manufacture of welded tubes and pipes of an external diameter of 406,4 mm or less of circular cross section by continuous cold or hot forming of hot or cold rolled flat products and longitudinally or spirally welded and of non-circular cross section by hot or cold forming into shape from hot or cold rolled strip longitudinally welded - manufacture of welded precision tubes and pipes of an external diameter of 406,4 mm or less by hot or cold forming of hot or cold rolled strip and longitudinally welded delivered as welded or further processed, by cold drawing or cold rolling or cold formed into shape for tube and pipe of non-circular cross section - manufacture of flat flanges and flanges with forged collars by processing of hot rolled flat products of steel - manufacture of butt-welding fittings, such as elbows and reductions, by forging of hot rolled seamless tubes of steel - threaded and other tube or pipe fittings of steel', 1, 398754, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398756, '24.3', 'Manufacture of other products of first processing of steel', 'This group includes manufacturing other products by cold processing of steel.', 1, 398751, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398757, '24.31', 'Cold drawing of bars', 'This class includes: - manufacture of steel bars and solid sections of steel by cold drawing, grinding or turning', 1, 398756, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398758, '24.32', 'Cold rolling of narrow strip', 'This class includes: - manufacture of coated or uncoated flat rolled steel products in coils or in straight lengths of a width less than 600 mm by cold re-rolling of hot-rolled flat products or of steel rod', 1, 398756, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398759, '24.33', 'Cold forming or folding', 'This class includes: - manufacture of open sections by progressive cold forming on a roll mill or folding on a press of flat-rolled products of steel - manufacture of cold-formed or cold-folded, ribbed sheets and sandwich panels', 1, 398756, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398760, '24.34', 'Cold drawing of wire', 'This class includes: - manufacture of drawn steel wire, by cold drawing of steel wire rod', 1, 398756, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398761, '24.4', 'Manufacture of basic precious and other non-ferrous metals', '', 1, 398751, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398762, '24.41', 'Precious metals production', 'This class includes: - production of basic precious metals: . production and refining of unwrought or wrought precious metals: gold, silver, platinum etc. from ore and scrap - production of precious metal alloys - production of precious metal semi-products - production of silver rolled onto base metals - production of gold rolled onto base metals or silver - production of platinum and platinum group metals rolled onto gold, silver or base metals', 1, 398761, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398763, '24.42', 'Aluminium production', 'This class includes: - production of aluminium from alumina - production of aluminium from electrolytic refining of aluminium waste and scrap - production of aluminium alloys - semi-manufacture of aluminium', 1, 398761, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398764, '24.43', 'Lead, zinc and tin production', 'This class includes: - production of lead, zinc and tin from ores - production of lead, zinc and tin from electrolytic refining of lead, zinc and tin waste and scrap - production of lead, zinc and tin alloys - semi-manufacture of lead, zinc and tin', 1, 398761, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398765, '24.44', 'Copper production', 'This class includes: - production of copper from ores - production of copper from electrolytic refining of copper waste and scrap - production of copper alloys - manufacture of fuse wire or strip - semi-manufacture of copper', 1, 398761, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398766, '24.45', 'Other non-ferrous metal production', 'This class includes: - production of chrome, manganese, nickel etc. from ores or oxides - production of chrome, manganese, nickel etc. from electrolytic and aluminothermic refining of chrome, manganese, nickel etc., waste and scrap - production of alloys of chrome, manganese, nickel etc. - semi-manufacture of chrome, manganese, nickel etc. - production of mattes of nickel', 1, 398761, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398767, '24.46', 'Processing of nuclear fuel ', 'This class includes: - production of uranium metal from pitchblende or other ores - smelting and refining of uranium', 1, 398761, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398768, '24.5', 'Casting of metals', 'This group includes the manufacture of semi-finished products and various castings by a casting process.', 1, 398751, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398769, '24.51', 'Casting of iron', 'This class includes activities of iron foundries. This class includes: - casting of semi-finished iron products - casting of grey iron castings - casting of spheroidal graphite iron castings - casting of malleable cast-iron products - manufacture of tubes, pipes and hollow profiles and of tube or pipe fittings of cast-iron', 1, 398768, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398770, '24.52', 'Casting of steel', 'This class includes activities of steel foundries. This class includes: - casting of semi-finished steel products - casting of steel castings - manufacture of seamless tubes and pipes of steel by centrifugal casting - manufacture of tube or pipe fittings of cast-steel', 1, 398768, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398771, '24.53', 'Casting of light metals', 'This class includes: - casting of semi-finished products of aluminium, magnesium, titanium, zinc etc. - casting of light metal castings', 1, 398768, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398772, '24.54', 'Casting of other non-ferrous metals', 'This class includes: - casting of heavy metal castings - casting of precious metal castings - die-casting of non-ferrous metal castings', 1, 398768, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398773, '25', 'Manufacture of fabricated metal products, except machinery and equipment', 'This division includes the manufacture of "pure" metal products (such as parts, containers and structures), usually with a static, immovable function, as opposed to the following divisions 26-30, which cover the manufacture of combinations or assemblies of such metal products (sometimes with other materials) into more complex units that, unless they are purely electrical, electronic or optical, work with moving parts.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398774, '25.1', 'Manufacture of structural metal products', 'This group includes the manufacture of structural metal products (such as metal frameworks or parts for construction).', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398775, '25.11', 'Manufacture of metal structures and parts of structures', 'This class includes: - manufacture of metal frameworks or skeletons for construction and parts thereof (towers, masts, trusses, bridges etc.) - manufacture of industrial frameworks in metal (frameworks for blast furnaces, lifting and handling equipment etc.) - manufacture of prefabricated buildings mainly of metal: . site huts, modular exhibition elements etc.', 1, 398774, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398776, '25.12', 'Manufacture of doors and windows of metal', 'This class includes: - manufacture of metal doors, windows and their frames, shutters and gates - metal room partitions for floor attachment', 1, 398774, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398777, '25.2', 'Manufacture of tanks, reservoirs and containers of metal', 'This group includes the manufacture of tanks, central heating radiators and boilers.', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398778, '25.21', 'Manufacture of central heating radiators and boilers', '', 1, 398777, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398779, '25.29', 'Manufacture of other tanks, reservoirs and containers of metal', 'This class includes: - manufacture of reservoirs, tanks and similar containers of metal, of types normally installed as fixtures for storage or manufacturing use - manufacture of metal containers for compressed or liquefied gas', 1, 398777, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398780, '25.3', 'Manufacture of steam generators, except central heating hot water boilers', 'This group includes the manufacture of steam generators.', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398781, '25.3', 'Manufacture of steam generators, except central heating hot water boilers', 'This class includes: - manufacture of steam or other vapour generators - manufacture of auxiliary plant for use with steam generators: . condensers, economisers, superheaters, steam collectors and accumulators - manufacture of nuclear reactors, except isotope separators - manufacture of parts for marine or power boilers', 1, 398780, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398782, '25.4', 'Manufacture of weapons and ammunition', '', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398783, '25.4', 'Manufacture of weapons and ammunition', 'This class includes: - manufacture of heavy weapons (artillery, mobile guns, rocket launchers, torpedo tubes, heavy machine guns) - manufacture of small arms (revolvers, shotguns, light machine guns) - manufacture of air or gas guns and pistols - manufacture of war ammunition', 1, 398782, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398784, '25.5', 'Forging, pressing, stamping and roll-forming of metal; powder metallurgy', 'This group includes general activities for the treatment of metal, such as forging or pressing, which are typically carried out on a fee or contract basis.', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398785, '25.5', 'Forging, pressing, stamping and roll-forming of metal; powder metallurgy', 'This class includes: - forging, pressing, stamping and roll-forming of metal - powder metallurgy: production of metal objects directly from metal powders by heat treatment (sintering) or under pressure', 1, 398784, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398786, '25.6', 'Treatment and coating of metals; machining', 'This group includes general activities for the treatment of metal, such as plating, coating, engraving, boring, polishing, welding etc., which are typically carried out on a fee or contract basis.', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398787, '25.61', 'Treatment and coating of metals', 'This class includes: - plating, anodising etc. of metals - heat treatment of metals - deburring, sandblasting, tumbling, cleaning of metals - colouring, engraving of metals - non-metallic coating of metals: . plasticising, enamelling, lacquering etc. - hardening, buffing of metals', 1, 398786, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398788, '25.62', 'Machining', 'This class includes: - boring, turning, milling, eroding, planing, lapping, broaching, levelling, sawing, grinding, sharpening, polishing, welding, splicing etc. of metalwork pieces - cutting of and writing on metals by means of laser beams', 1, 398786, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398789, '25.7', 'Manufacture of cutlery, tools and general hardware', 'This group includes the manufacture of cutlery; metal hand tools and general hardware.', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398790, '25.71', 'Manufacture of cutlery', 'This class includes: - manufacture of domestic cutlery such as knives, forks, spoons etc. - manufacture of other articles of cutlery: . cleavers and choppers . razors and razor blades . scissors and hair clippers - manufacture of cutlasses, swords, bayonets etc.', 1, 398789, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398791, '25.72', 'Manufacture of locks and hinges', 'This class includes: - manufacture of padlocks, locks, keys, hinges and the like, hardware for buildings, furniture, vehicles etc.', 1, 398789, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398792, '25.73', 'Manufacture of tools', 'This class includes: - manufacture of knives and cutting blades for machines or for mechanical appliances - manufacture of hand tools such as pliers, screwdrivers etc. - manufacture of non-power-driven agricultural hand tools - manufacture of saws and saw blades, including circular saw blades and chainsaw blades - manufacture of interchangeable tools for hand tools, whether or not power-operated, or for machine tools: drills, punches, milling cutters etc. - manufacture of press tools - manufacture of blacksmiths'' tools: forges, anvils etc. - manufacture of moulding boxes and moulds (except ingot moulds) - manufacture of vices, clamps', 1, 398789, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398793, '25.9', 'Manufacture of other fabricated metal products', 'This group includes the manufacture of a variety of metal products, such as cans and buckets; nails, bolts and nuts; metal household articles; metal fixtures; ships propellers and anchors; assembled railway track fixtures etc. for a variety of household and industrial uses.', 1, 398773, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398794, '25.91', 'Manufacture of steel drums and similar containers', 'This class includes: - manufacture of pails, cans, drums, buckets, boxes', 1, 398793, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398795, '25.92', 'Manufacture of light metal packaging ', 'This class includes: - manufacture of tins and cans for food products, collapsible tubes and boxes - manufacture of metallic closures', 1, 398793, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398796, '25.93', 'Manufacture of wire products, chain and springs', 'This class includes: - manufacture of metal cable, plaited bands and similar articles - manufacture of uninsulated metal cable or insulated cable not capable of being used as a conductor of electricity - manufacture of coated or cored wire - manufacture of articles made of wire: barbed wire, wire fencing, grill, netting, cloth etc. - coated electrodes for electric arc-welding - manufacture of nails and pins - manufacture of springs (except watch springs): . leaf springs, helical springs, torsion bar springs . leaves for springs - manufacture of chain, except power transmission chain', 1, 398793, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398797, '25.94', 'Manufacture of fasteners and screw machine products', 'This class includes: - manufacture of rivets, washers and similar non-threaded products - manufacture of screw machine products - manufacture of bolts, screws, nuts and similar threaded products', 1, 398793, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398798, '25.99', 'Manufacture of other fabricated metal products n.e.c.', 'This class includes: - manufacture of metal household articles: . flatware: plates, saucers etc. . hollowware: pots, kettles etc. . dinnerware: bowls, platters etc. . saucepans, frying pans and other non-electrical utensils for use at the table or in the kitchen . small hand-operated kitchen appliances and accessories . metal scouring pads - manufacture of building components of zinc: gutters, roof capping, baths, sinks, washbasins and similar articles - manufacture of metal goods for office use, except furniture - manufacture of safes, strongboxes, armoured doors etc. - manufacture of various metal articles: . ship propellers and blades thereof . anchors . bells . assembled railway track fixtures . clasps, buckles, hooks . metal ladder . metal signs, including road signs - manufacture of foil bags - manufacture of permanent metallic magnets - manufacture of metal vacuum jugs and bottles - manufacture of metal badges and metal military insignia - manufacture of metal hair curlers, metal umbrella handles and frames, combs', 1, 398793, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398799, '26', 'Manufacture of computer, electronic and optical products', 'This division includes the manufacture of computers, computer peripherals, communications equipment, and similar electronic products, as well as the manufacture of components for such products. Production processes of this division are characterised by the design and use of integrated circuits and the application of highly specialised miniaturisation technologies.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398800, '26.1', 'Manufacture of electronic components and boards', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398801, '26.11', 'Manufacture of electronic components', 'This class includes the manufacture of semi-conductors and other components for electronic applications. This class includes: - manufacture of capacitors, electronic - manufacture of resistors, electronic - manufacture of microprocessors - manufacture of electron tubes - manufacture of electronic connectors - manufacture of bare printed circuit boards - manufacture of integrated circuits (analog, digital or hybrid) - manufacture of diodes, transistors and related discrete devices - manufacture of inductors (e.g. chokes, coils, transformers), electronic component type - manufacture of electronic crystals and crystal assemblies - manufacture of solenoids, switches and transducers for electronic applications - manufacture of dice or wafers, semi-conductor, finished or semi-finished - manufacture of display components (plasma, polymer, LCD) - manufacture of light emitting diodes (LED)', 1, 398800, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398802, '26.12', 'Manufacture of loaded electronic boards', 'This class includes: - manufacture of loaded printed circuit boards - loading of components onto printed circuit boards - manufacture of interface cards (e.g. sound, video, controllers, network, modems)', 1, 398800, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398803, '26.2', 'Manufacture of computers and peripheral equipment', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398804, '26.2', 'Manufacture of computers and peripheral equipment', 'This class includes the manufacture and/or assembly of electronic computers, such as mainframes, desktop computers, laptops and computer servers; and computer peripheral equipment, such as storage devices and input/output devices (printers, monitors, keyboards). Computers can be analog, digital, or hybrid. Digital computers, the most common type, are devices that do all of the following: (1) store the processing program or programs and the data immediately necessary for the execution of the program, (2) can be freely programmed in accordance with the requirements of the user, (3) perform arithmetical computations specified by the user and (4) execute, without human intervention, a processing program that requires the computer to modify its execution by logical decision during the processing run. Analog computers are capable of simulating mathematical models and comprise at least analog control and programming elements. This class includes: - manufacture of desktop computers - manufacture of laptop computers - manufacture of main frame computers - manufacture of hand-held computers (e.g. PDA) - manufacture of magnetic disk drives, flash drives and other storage devices - manufacture of optical (e.g. CD-RW, CD-ROM, DVD-ROM, DVD-RW) disk drives - manufacture of printers - manufacture of monitors - manufacture of keyboards - manufacture of all types of mice, joysticks, and trackball accessories - manufacture of dedicated computer terminals - manufacture of computer servers - manufacture of scanners, including bar code scanners - manufacture of smart card readers - manufacture of virtual reality helmets - manufacture of computer projectors (video beamers)', 1, 398803, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398805, '26.3', 'Manufacture of communication equipment', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398806, '26.3', 'Manufacture of communication equipment', 'This class includes the manufacture of telephone and data communications equipment used to move signals electronically over wires or through the air such as radio and television broadcast and wireless communications equipment. This class includes: - manufacture of central office switching equipment - manufacture of cordless telephones - manufacture of private branch exchange (PBX) equipment - manufacture of telephone and facsimile equipment, including telephone answering machines - manufacture of data communications equipment, such as bridges, routers, and gateways - manufacture of transmitting and receiving antenna - manufacture of cable television equipment - manufacture of pagers - manufacture of cellular phones - manufacture of mobile communication equipment - manufacture of radio and television studio and broadcasting equipment, including television cameras - manufacture of modems, carrier equipment - manufacture of burglar and fire alarm systems, sending signals to a control station - manufacture of radio and television transmitters - manufacture of communication devices using infrared signal (e.g. remote controls)', 1, 398805, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398807, '26.4', 'Manufacture of consumer electronics', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398808, '26.4', 'Manufacture of consumer electronics', 'This class includes the manufacture of electronic audio and video equipment for home entertainment, motor vehicle, public address systems and musical instrument amplification. This class includes: - manufacture of video cassette recorders and duplicating equipment - manufacture of televisions - manufacture of television monitors and displays - manufacture of audio recording and duplicating systems - manufacture of stereo equipment - manufacture of radio receivers - manufacture of speaker systems - manufacture of household-type video cameras - manufacture of jukeboxes - manufacture of amplifiers for musical instruments and public address systems - manufacture of microphones - manufacture of CD and DVD players - manufacture of karaoke machines - manufacture of headphones (e.g. radio, stereo, computer) - manufacture of video game consoles', 1, 398807, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398809, '26.5', 'Manufacture of instruments and appliances for measuring, testing and navigation; watches and clocks', 'This group includes the manufacture of measuring, testing and navigating equipment for various industrial and non-industrial purposes, including time-based measuring devices such as watches and clocks and related devices.', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398810, '26.51', 'Manufacture of instruments and appliances for measuring, testing and navigation', 'This class comprises manufacture of search, detection, navigation, guidance, aeronautical, and nautical systems and instruments; automatic controls and regulators for applications, such as heating, air-conditioning, refrigeration and appliances; instruments and devices for measuring, displaying, indicating, recording, transmitting, and controlling temperature, humidity, pressure, vacuum, combustion, flow, level, viscosity, density, acidity, concentration, and rotation; totalising (i.e., registering) fluid meters and counting devices; instruments for measuring and testing the characteristics of electricity and electrical signals; instruments and instrumentation systems for laboratory analysis of the chemical or physical composition or concentration of samples of solid, fluid, gaseous, or composite material; other measuring and testing instruments and parts thereof. The manufacture of non-electric measuring, testing and navigating equipment (except simple mechanical tools) is included here. This class includes: - manufacture of aircraft engine instruments - manufacture of automotive emissions testing equipment - manufacture of meteorological instruments - manufacture of physical properties testing and inspection equipment - manufacture of polygraph machines - manufacture of radiation detection and monitoring instruments - manufacture of surveying instruments - manufacture of thermometers liquid-in-glass and bimetal types (except medical) - manufacture of humidistats - manufacture of hydronic limit controls - manufacture of flame and burner control - manufacture of spectrometers - manufacture of pneumatic gauges - manufacture of consumption meters (e.g., water, gas, electricity) - manufacture of flow meters and counting devices - manufacture of tally counters - manufacture of mine detectors, pulse (signal) generators; metal detectors - manufacture of search, detection, navigation, aeronautical, and nautical equipment, including sonobuoys - manufacture of radar equipment - manufacture of GPS devices - manufacture of environmental controls and automatic controls for appliances - manufacture of measuring and recording equipment (e.g. flight recorders) - manufacture of motion detectors - manufacture of radars - manufacture of laboratory analytical instruments (e.g. blood analysis equipment) - manufacture of laboratory scales, balances, incubators, and miscellaneous laboratory apparatus for measuring, testing, etc.', 1, 398809, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398811, '26.52', 'Manufacture of watches and clocks', 'This class includes the manufacture of watches, clocks and timing mechanisms and parts thereof. This class includes: - manufacture of watches and clocks of all kinds, including instrument panel clocks - manufacture of watch and clock cases, including cases of precious metals - manufacture of time-recording equipment and equipment for measuring, recording and otherwise displaying intervals of time with a watch or clock movement or with synchronous motor, such as: . parking meters . time clocks . time/date stamps . process timers - manufacture of time switches and other releases with a watch or clock movement or with synchronous motor: . time locks - manufacture of components for clocks and watches: . movements of all kinds for watches and clocks . springs, jewels, dials, hands, plates, bridges and other parts . watch and clock cases and housings of all materials', 1, 398809, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398812, '26.6', 'Manufacture of irradiation, electromedical and electrotherapeutic equipment', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398813, '26.6', 'Manufacture of irradiation, electromedical and electrotherapeutic equipment', 'This class includes: - manufacture of irradiation apparatus and tubes (e.g. industrial, medical diagnostic, medical therapeutic, research, scientific): . beta-, gamma, X-ray or other radiation equipment - manufacture of CT scanners - manufacture of PET scanners - manufacture of magnetic resonance imaging (MRI) equipment - manufacture of medical ultrasound equipment - manufacture of electrocardiographs - manufacture of electromedical endoscopic equipment - manufacture of medical laser equipment - manufacture of pacemakers - manufacture of hearing aids', 1, 398812, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398814, '26.7', 'Manufacture of optical instruments and photographic equipment', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398815, '26.7', 'Manufacture of optical instruments and photographic equipment', 'This class includes the manufacture of optical instruments and lenses, such as binoculars, microscopes (except electron, proton), telescopes, prisms and lenses (except ophthalmic); the coating or polishing of lenses (except ophthalmic); the mounting of lenses (except ophthalmic) and the manufacture of photographic equipment such as cameras and light meters. This class includes: - manufacture of optical mirrors - manufacture of optical gun sighting equipment - manufacture of optical positioning equipment - manufacture of optical magnifying instruments - manufacture of optical machinist''s precision tools - manufacture of optical comparators - manufacture of film cameras and digital cameras - manufacture of motion picture and slide projectors - manufacture of overhead transparency projectors - manufacture of optical measuring and checking devices and instruments (e.g. fire control equipment, photographic light meters, range finders) - manufacture of lenses, optical microscopes, binoculars and telescopes - manufacture of laser assemblies', 1, 398814, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398816, '26.8', 'Manufacture of magnetic and optical media', '', 1, 398799, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398817, '26.8', 'Manufacture of magnetic and optical media', 'This class includes the manufacture of magnetic and optical recording media. This class includes: - manufacture of blank magnetic audio and video tapes - manufacture of blank magnetic audio and video cassettes - manufacture of blank diskettes - manufacture of blank optical discs - manufacture of hard drive media', 1, 398816, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398818, '27', 'Manufacture of electrical equipment', 'This division includes the manufacture of products that generate, distribute and use electrical power.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398819, '27.1', 'Manufacture of electric motors, generators, transformers and electricity distribution and control apparatus', 'This group comprises the manufacture of power, distribution and specialty transformers; electric motors, generators, and motor generator sets.', 1, 398818, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398820, '27.11', 'Manufacture of electric motors, generators and transformers', 'This class includes manufacture of all electric motors and transformers: AC, DC and AC/DC. This class includes: - manufacture of electric motors (except internal combustion engine starting motors) - manufacture of distribution transformers, electric - manufacture of arc-welding transformers - manufacture of fluorescent ballasts (i.e. transformers) - manufacture of substation transformers for electric power distribution - manufacture of transmission and distribution voltage regulators - manufacture of power generators (except battery charging alternators for internal combustion engines) - manufacture of motor generator sets (except turbine generator set units) - rewinding of armatures on a factory basis', 1, 398819, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398821, '27.12', 'Manufacture of electricity distribution and control apparatus', 'This class includes: - manufacture of power circuit breakers - manufacture of surge suppressors (for distribution level voltage) - manufacture of control panels for electric power distribution - manufacture of electrical relays - manufacture of duct for electrical switchboard apparatus - manufacture of electric fuses - manufacture of power switching equipment - manufacture of electric power switches (except pushbutton, snap, solenoid, tumbler) - manufacture of prime mover generator sets', 1, 398819, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398822, '27.2', 'Manufacture of batteries and accumulators', '', 1, 398818, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398823, '27.2', 'Manufacture of batteries and accumulators', 'This class includes the manufacture of non-rechargeable and rechargeable batteries. This class includes: - manufacture of primary cells and primary batteries . cells containing manganese dioxide, mercuric dioxide, silver oxide etc. - manufacture of electric accumulators, including parts thereof: . separators, containers, covers - manufacture of lead acid batteries - manufacture of NiCad batteries - manufacture of NiMH batteries - manufacture of lithium batteries - manufacture of dry cell batteries - manufacture of wet cell batteries', 1, 398822, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398824, '27.3', 'Manufacture of wiring and wiring devices', 'This group includes the manufacture of current-carrying wiring devices and non current-carrying wiring devices for wiring electrical circuits regardless of material.', 1, 398818, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398825, '27.31', 'Manufacture of fibre optic cables', 'This class includes: - manufacture of fibre optic cable for data transmission or live transmission of images', 1, 398824, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398826, '27.32', 'Manufacture of other electronic and electric wires and cables', 'This class includes: - manufacture of insulated wire and cable, made of steel, copper, aluminium', 1, 398824, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398827, '27.33', 'Manufacture of wiring devices', 'This class includes the manufacture of current-carrying and non current-carrying wiring devices for electrical circuits regardless of material. This class includes: - manufacture of bus bars, electrical conductors (except switchgear-type) - manufacture of GFCI (ground fault circuit interrupters) - manufacture of lamp holders - manufacture of lightning arrestors and coils - manufacture of switches for electrical wiring (e.g. pressure, pushbutton, snap, tumbler switches) - manufacture of electrical outlets or sockets - manufacture of boxes for electrical wiring (e.g. junction, outlet, switch boxes) - manufacture of electrical conduit and fitting - manufacture of transmission pole and line hardware - manufacture of plastic non current carrying wiring devices including plastic junction boxes, face plates and similar, plastic pole line fittings and switch covers', 1, 398824, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398828, '27.4', 'Manufacture of electric lighting equipment', '', 1, 398818, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398829, '27.4', 'Manufacture of electric lighting equipment', 'This class includes the manufacture of electric light bulbs and tubes and parts and components thereof (except glass blanks for electric light bulbs), electric lighting fixtures and lighting fixture components (except current-carrying wiring devices). This class includes: - manufacture of discharge, incandescent, fluorescent, ultra-violet, infra-red etc. lamps, fixtures and bulbs - manufacture of ceiling lighting fixtures - manufacture of chandeliers - manufacture of table lamps (i.e. lighting fixture) - manufacture of Christmas tree lighting sets - manufacture of electric fireplace logs - manufacture of flashlights - manufacture of electric insect lamps - manufacture of lanterns (e.g. carbide, electric, gas, gasoline, kerosene) - manufacture of spotlights - manufacture of street lighting fixtures (except traffic signals) - manufacture of lighting equipment for transportation equipment (e.g. for motor vehicles, aircraft, boats)', 1, 398828, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398830, '27.5', 'Manufacture of domestic appliances', 'This group includes the manufacture of small electric appliances and electric housewares, household-type fans, household-type vacuum cleaners, electric household-type floor care machines, household-type cooking appliances, household-type laundry equipment, household-type refrigerators, upright and chest freezers, and other electrical and non-electrical household appliances, such as dishwashers, water heaters, and garbage disposal units. This group includes the manufacture of appliances with electric, gas or other fuel sources.', 1, 398818, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398831, '27.51', 'Manufacture of electric domestic appliances', 'This class includes: - manufacture of domestic electric appliances: . refrigerators . freezers . dishwashers . washing and drying machines . vacuum cleaners . floor polishers . waste disposers . grinders, blenders, juice squeezers . tin openers . electric shavers, electric toothbrushes, and other electric personal care device . knife sharpeners . ventilating or recycling hoods - manufacture of domestic electrothermic appliances: . electric water heaters . electric blankets . electric dryers, combs, brushes, curlers . electric smoothing irons . space heaters and household-type fans, portable . electric ovens . microwave ovens . cookers, hotplates . toasters . coffee or tea makers . fry pans, roasters, grills, hoods . electric heating resistors etc.', 1, 398830, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398832, '27.52', 'Manufacture of non-electric domestic appliances', 'This class includes: - manufacture of domestic non-electric cooking and heating equipment: . non-electric space heaters, cooking ranges, grates, stoves, water heaters, cooking appliances, plate warmers', 1, 398830, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398833, '27.9', 'Manufacture of other electrical equipment', '', 1, 398818, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398834, '27.9', 'Manufacture of other electrical equipment', 'This class includes the manufacture of miscellaneous electrical equipment other than motors, generators and transformers, batteries and accumulators, wires and wiring devices, lighting equipment or domestic appliances. This class includes: - manufacture of battery chargers, solid-state - manufacture of door opening and closing devices, electrical - manufacture of electric bells - manufacture of extension cords made from purchased insulated wire - manufacture of ultrasonic cleaning machines (except laboratory and dental) - manufacture of tanning beds - manufacture of solid state inverters, rectifying apparatus, fuel cells, regulated and unregulated power supplies - manufacture of uninterruptible power supplies (UPS) - manufacture of surge suppressors (except for distribution level voltage) - manufacture of appliance cords, extension cords, and other electrical cord sets with insulated wire and connectors - manufacture of carbon and graphite electrodes, contacts, and other electrical carbon and graphite products - manufacture of particle accelerators - manufacture of electrical capacitors, resistors, condensers and similar components - manufacture of electromagnets - manufacture of sirens - manufacture of electronic scoreboards - manufacture of electrical signs - manufacture of electrical signalling equipment such as traffic lights and pedestrian signalling equipment - manufacture of electrical insulators (except glass or porcelain) - manufacture of electrical welding and soldering equipment, including hand-held soldering irons', 1, 398833, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398835, '28', 'Manufacture of machinery and equipment n.e.c.', 'This division includes the manufacture of machinery and equipment that act independently on materials either mechanically or thermally or perform operations on materials (such as handling, spraying, weighing or packing), including their mechanical components that produce and apply force, and any specially manufactured primary parts. This includes the manufacture of fixed and mobile or hand-held devices, regardless of whether they are designed for industrial, building and civil engineering, agricultural or home use. The manufacture of special equipment for passenger or freight transport within demarcated premises also belongs within this division. This division distinguishes between the manufacture of special-purpose machinery, i.e. machinery for exclusive use in a NACE industry or a small cluster of NACE industries, and general-purpose machinery, i.e. machinery that is being used in a wide range of NACE industries.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398836, '28.1', 'Manufacture of general-purpose machinery', '', 1, 398835, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398837, '28.11', 'Manufacture of engines and turbines, except aircraft, vehicle and cycle engines', 'This class includes: - manufacture of internal combustion piston engines, except motor vehicle, aircraft and cycle propulsion engines: . marine engines . railway engines - manufacture of pistons, piston rings, carburettors and such for all internal combustion engines, diesel engines etc. - manufacture of inlet and exhaust valves of internal combustion engines - manufacture of turbines and parts thereof: . steam turbines and other vapour turbines . hydraulic turbines, waterwheels and regulators thereof . wind turbines . gas turbines, except turbojets or turbo propellers for aircraft propulsion - manufacture of boiler-turbine sets - manufacture of turbine-generator sets - manufacture of engines for industrial application', 1, 398836, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398838, '28.12', 'Manufacture of fluid power equipment', 'This class includes: - manufacture of hydraulic and pneumatic components (including hydraulic pumps, hydraulic motors, hydraulic and pneumatic cylinders, hydraulic and pneumatic valves, hydraulic and pneumatic hose and fittings) - manufacture of air preparation equipment for use in pneumatic systems - manufacture of fluid power systems - manufacture of hydraulic transmission equipment - manufacture of hydrostatic transmissions', 1, 398836, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398839, '28.13', 'Manufacture of other pumps and compressors', 'This class includes: - manufacture of air or vacuum pumps, air or other gas compressors - manufacture of pumps for liquids whether or not fitted with a measuring device - manufacture of pumps designed for fitting to internal combustion engines: oil, water and fuel pumps for motor vehicles etc.', 1, 398836, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398840, '28.14', 'Manufacture of other taps and valves', 'This class includes: - manufacture of industrial taps and valves, including regulating valves and intake taps - manufacture of sanitary taps and valves - manufacture of heating taps and valves', 1, 398836, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398841, '28.15', 'Manufacture of bearings, gears, gearing and driving elements', 'This class includes: - manufacture of ball and roller bearings and parts thereof - manufacture of mechanical power transmission equipment: . transmission shafts and cranks: camshafts, crankshafts, cranks etc. . bearing housings and plain shaft bearings - manufacture of gears, gearing and gear boxes and other speed changers - manufacture of clutches and shaft couplings - manufacture of flywheels and pulleys - manufacture of articulated link chain - manufacture of power transmission chain', 1, 398836, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398842, '28.2', 'Manufacture of other general-purpose machinery', '', 1, 398835, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398843, '28.21', 'Manufacture of ovens, furnaces and furnace burners', 'This class includes: - manufacture of electrical and other industrial and laboratory furnaces and ovens, including incinerators - manufacture of burners - manufacture of permanent mount electric space heaters, electric swimming pool heaters - manufacture of permanent mount non-electric household heating equipment, such as solar heating, steam heating, oil heat and similar furnaces and heating equipment - manufacture of electric household-type furnaces (electric forced air furnaces, heat pumps, etc.), non-electric household forced air furnaces', 1, 398842, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398844, '28.22', 'Manufacture of lifting and handling equipment', 'This class includes: - manufacture of hand-operated or power-driven lifting, handling, loading or unloading machinery: . pulley tackle and hoists, winches, capstans and jacks . derricks, cranes, mobile lifting frames, straddle carriers etc. . works trucks, whether or not fitted with lifting or handling equipment, whether or not self-propelled, of the type used in factories (including hand trucks and wheelbarrows) . mechanical manipulators and industrial robots specifically designed for lifting, handling, loading or unloading - manufacture of conveyors, teleferics etc. - manufacture of lifts, escalators and moving walkways - manufacture of parts specialised for lifting and handling equipment', 1, 398842, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398845, '28.23', 'Manufacture of office machinery and equipment (except computers and peripheral equipment)', 'This class includes: - manufacture of calculating machines - manufacture of adding machines, cash registers - manufacture of calculators, electronic or not - manufacture of postage meters, mail handling machines (envelope stuffing, sealing and addressing machinery; opening, sorting, scanning), collating machinery - manufacture of typewriters - manufacture of stenography machines - manufacture of office-type binding equipment (i.e. plastic or tape binding) - manufacture of cheque writing machines - manufacture of coin counting and coin wrapping machinery - manufacture of pencil sharpeners - manufacture of staplers and staple removers - manufacture of voting machines - manufacture of tape dispensers - manufacture of hole punches - manufacture of cash registers, mechanically operated - manufacture of photocopy machines - manufacture of toner cartridges - manufacture of blackboards; white boards and marker boards - manufacture of dictating machines', 1, 398842, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398846, '28.24', 'Manufacture of power-driven hand tools', 'This class includes: - manufacture of hand tools, with self-contained electric or non-electric motor or pneumatic drive, such as: . circular or reciprocating saws . chain saws . drills and hammer drills . hand held power sanders . pneumatic nailers . buffers . routers . grinders . staplers . pneumatic rivet guns . planers . shears and nibblers . impact wrenches . powder actuated nailers', 1, 398842, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398847, '28.25', 'Manufacture of non-domestic cooling and ventilation equipment', 'This class includes: - manufacture of refrigerating or freezing industrial equipment, including assemblies of components - manufacture of air-conditioning machines, including for motor vehicles - manufacture of non-domestic fans - manufacture of heat exchangers - manufacture of machinery for liquefying air or gas - manufacture of attic ventilation fans (gable fans, roof ventilators, etc.)', 1, 398842, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398848, '28.29', 'Manufacture of other general-purpose machinery n.e.c.', 'This class includes: - manufacture of weighing machinery (other than sensitive laboratory balances): . household and shop scales, platform scales, scales for continuous weighing, weighbridges, weights etc. - manufacture of filtering or purifying machinery and apparatus for liquids - manufacture of equipment for projecting, dispersing or spraying liquids or powders: . spray guns, fire extinguishers, sandblasting machines, steam cleaning machines etc. - manufacture of packing and wrapping machinery: . filling, closing, sealing, capsuling or labelling machines etc. - manufacture of machinery for cleaning or drying bottles and for aerating beverages - manufacture of distilling or rectifying plant for petroleum refineries, chemical industries, beverage industries etc. - manufacture of gas generators - manufacture of calendaring or other rolling machines and cylinders thereof (except for metal and glass) - manufacture of centrifuges (except cream separators and clothes dryers) - manufacture of gaskets and similar joints made of a combination of materials or layers of the same material - manufacture of automatic goods vending machines - manufacture of levels, tape measures and similar hand tools, machinists'' precision tools (except optical) - manufacture of non-electrical welding and soldering equipment - manufacture of cooling towers and similar for direct cooling by means of re-circulated water', 1, 398842, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398849, '28.3', 'Manufacture of agricultural and forestry machinery', '', 1, 398835, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398850, '28.3', 'Manufacture of agricultural and forestry machinery', 'This class includes: - manufacture of tractors used in agriculture and forestry - manufacture of walking (pedestrian-controlled) tractors - manufacture of mowers, including lawnmowers - manufacture of agricultural self-loading or self-unloading trailers or semi-trailers - manufacture of agricultural machinery for soil preparation, planting or fertilising: . ploughs, manure spreaders, seeders, harrows etc. - manufacture of harvesting or threshing machinery: . harvesters, threshers, sorters etc. - manufacture of milking machines - manufacture of spraying machinery for agricultural use - manufacture of diverse agricultural machinery: . poultry-keeping machinery, bee-keeping machinery, equipment for preparing fodder etc. . machines for cleaning, sorting or grading eggs, fruit etc.', 1, 398849, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398851, '28.4', 'Manufacture of metal forming machinery and machine tools', 'This group includes the manufacture of metal forming machinery and machine tools, e.g. manufacture of machine tools for working metals and other materials (wood, bone, stone, hard rubber, hard plastics, cold glass etc.), including those using a laser beam, ultrasonic waves, plasma arc, magnetic pulse etc.', 1, 398835, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398852, '28.41', 'Manufacture of metal forming machinery', 'This class includes: - manufacture of machine tools for working metals, including those using a laser beam, ultrasonic waves, plasma arc, magnetic pulse etc. - manufacture of machine tools for turning, drilling, milling, shaping, planing, boring, grinding etc. - manufacture of stamping or pressing machine tools - manufacture of punch presses, hydraulic presses, hydraulic brakes, drop hammers, forging machines etc. - manufacture of draw-benches, thread rollers or machines for working wires', 1, 398851, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398853, '28.49', 'Manufacture of other machine tools', 'This class includes: - manufacture of machine tools for working wood, bone, stone, hard rubber, hard plastics, cold glass etc., including those using a laser beam, ultrasonic waves, plasma arc, magnetic pulse etc. - manufacture of work holders for machine tools - manufacture of dividing heads and other special attachments for machine tools - manufacture of stationary machines for nailing, stapling, glueing or otherwise assembling wood, cork, bone, hard rubber or plastics etc. - manufacture of stationary rotary or rotary percussion drills, filing machines, riveters, sheet metal cutters etc. - manufacture of presses for the manufacture of particle board and the like - manufacture of electroplating machinery', 1, 398851, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398854, '28.9', 'Manufacture of other special-purpose machinery', 'This group includes the manufacture of special-purpose machinery, i.e. machinery for exclusive use in an NACE industry or a small cluster of NACE industries.', 1, 398835, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398855, '28.91', 'Manufacture of machinery for metallurgy', 'This class includes: - manufacture of machines and equipment for handling hot metals: . converters, ingot moulds, ladles, casting machines - manufacture of metal-rolling mills and rolls for such mills', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398856, '28.92', 'Manufacture of machinery for mining, quarrying and construction', 'This class includes: - manufacture of continuous-action elevators and conveyors for underground use - manufacture of boring, cutting, sinking and tunnelling machinery (whether or not for underground use) - manufacture of machinery for treating minerals by screening, sorting, separating, washing, crushing etc. - manufacture of concrete and mortar mixers - manufacture of earth-moving machinery: . bulldozers, angle-dozers, graders, scrapers, levellers, mechanical shovels, shovel loaders etc. - manufacture of pile drivers and pile extractors, mortar spreaders, bitumen spreaders, concrete surfacing machinery etc. - manufacture of track laying tractors and tractors used in construction or mining - manufacture of bulldozer and angle-dozer blades - manufacture of off-road dumping trucks', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398857, '28.93', 'Manufacture of machinery for food, beverage and tobacco processing', 'This class includes: - manufacture of agricultural dryers - manufacture of machinery for the dairy industry: . cream separators . milk processing machinery (e.g. homogenisers) . milk converting machinery (e.g. butter chums, butter workers and moulding machines) . cheese-making machines (e.g. homogenisers, moulders, presses) etc. - manufacture of machinery for the grain milling industry: . machinery to clean, sort or grade seeds, grain or dried leguminous vegetables (winnowers, sieving belts, separators, grain brushing machines etc.) . machinery to produce flour and meal etc. (grinding mills, feeders, sifters, bran cleaners, blenders, rice hullers, pea splitters) - manufacture of presses, crushers etc. used to make wine, cider, fruit juices etc. - manufacture of machinery for the bakery industry or for making macaroni, spaghetti or similar products: . bakery ovens, dough mixers, dough-dividers, moulders, slicers, cake depositing machines etc. - manufacture of machines and equipment to process diverse foods: . machinery to make confectionery, cocoa or chocolate; to manufacture sugar; for breweries; to process meat or poultry, to prepare fruit, nuts or vegetables; to prepare fish, shellfish or other seafood . machinery for filtering and purifying . other machinery for the industrial preparation or manufacture of food or drink - manufacture of machinery for the extraction or preparation of animal or vegetable fats or oils - manufacture of machinery for the preparation of tobacco and for the making of cigarettes or cigars, or for pipe or chewing tobacco or snuff - manufacture of machinery for the preparation of food in hotels and restaurants', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398858, '28.94', 'Manufacture of machinery for textile, apparel and leather production', 'This class includes: - manufacture of textile machinery: . machines for preparing, producing, extruding, drawing, texturing or cutting man-made textile fibres, materials or yarns . machines for preparing textile fibres: cotton gins, bale breakers, garnetters, cotton spreaders, wool scourers, wool carbonisers, combs, carders, roving frames etc. . spinning machines . machines for preparing textile yarns: reelers, warpers and related machines . weaving machines (looms), including hand looms . knitting machines . machines for making knotted net, tulle, lace, braid etc. - manufacture of auxiliary machines or equipment for textile machinery: . dobbies, jacquards, automatic stop motions, shuttle changing mechanisms, spindles and spindle flyers etc. - manufacture of textile printing machinery - manufacture of machinery for fabric processing: . machinery for washing, bleaching, dyeing, dressing, finishing, coating or impregnating textile fabrics . manufacture of machines for reeling, unreeling, folding, cutting or pinking textile fabrics - manufacture of laundry machinery: . ironing machines, including fusing presses . commercial washing and drying machines . dry-cleaning machines - manufacture of sewing machines, sewing machine heads and sewing machine needles (whether or not for household use) - manufacture of machines for producing or finishing felt or non-wovens - manufacture of leather machines: . machinery for preparing, tanning or working hides, skins or leather . machinery for making or repairing footwear or other articles of hides, skins, leather or fur skins', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398859, '28.95', 'Manufacture of machinery for paper and paperboard production', 'This class includes: - manufacture of machinery for making paper pulp - manufacture of paper and paperboard making machinery - manufacture of machinery producing articles of paper or paperboard', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398860, '28.96', 'Manufacture of plastics and rubber machinery', 'This class includes: - manufacture of machinery for working soft rubber or plastics or for the manufacture of products of these materials: . extruders, moulders, pneumatic tyre making or retreading machines and other machines for making a specific rubber or plastic product', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398861, '28.99', 'Manufacture of other special-purpose machinery n.e.c.', 'This class includes the manufacture of special-purpose machinery not elsewhere classified. This class includes: - manufacture of dryers for wood, paper pulp, paper or paperboard and other materials (except for agricultural products and textiles) - manufacture of printing and bookbinding machines and machines for activities supporting printing on a variety of materials - manufacture of machinery for producing tiles, bricks, shaped ceramic pastes, pipes, graphite electrodes, blackboard chalk etc. - manufacture of semi-conductor manufacturing machinery - manufacture of industrial robots performing multiple tasks for special purposes - manufacture of diverse special-purpose machinery and equipment: . machines to assemble electric or electronic lamps, tubes (valves) or bulbs . machines for production or hot-working of glass or glassware, glass fibre or yarn . machinery or apparatus for isotopic separation - manufacture of tyre alignment and balancing equipment; balancing equipment (except wheel balancing) - manufacture of central greasing systems - manufacture of aircraft launching gear, aircraft carrier catapults and related equipment - manufacture of automatic bowling alley equipment (e.g. pin-setters) - manufacture of roundabouts, swings, shooting galleries and other fairground amusements', 1, 398854, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398862, '29', 'Manufacture of motor vehicles, trailers and semi-trailers', 'This division includes the manufacture of motor vehicles for transporting passengers or freight. The manufacture of various parts and accessories, as well as the manufacture of trailers and semi-trailers, is included here. The maintenance and repair of vehicles produced in this division are classified in 45.20.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398863, '29.1', 'Manufacture of motor vehicles', '', 1, 398862, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398864, '29.1', 'Manufacture of motor vehicles', 'This class includes: - manufacture of passenger cars - manufacture of commercial vehicles: . vans, lorries, over-the-road tractors for semi-trailers etc. - manufacture of buses, trolley-buses and coaches - manufacture of motor vehicle engines - manufacture of chassis for motor vehicles - manufacture of other motor vehicles: . snowmobiles, golf carts, amphibious vehicles . fire engines, street sweepers, travelling libraries, armoured cars etc. . concrete-mixer lorries - ATVs, go-carts and similar including race cars', 1, 398863, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398865, '29.2', 'Manufacture of bodies (coachwork) for motor vehicles; manufacture of trailers and semi-trailers', '', 1, 398862, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398866, '29.2', 'Manufacture of bodies (coachwork) for motor vehicles; manufacture of trailers and semi-trailers', 'This class includes: - manufacture of bodies, including cabs for motor vehicles - outfitting of all types of motor vehicles, trailers and semi-trailers - manufacture of trailers and semi-trailers: . tankers, removal trailers etc. . caravans etc. - manufacture of containers for carriage by one or more modes of transport', 1, 398865, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398867, '29.3', 'Manufacture of parts and accessories for motor vehicles', '', 1, 398862, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398868, '29.31', 'Manufacture of electrical and electronic equipment for motor vehicles', 'This class includes: - manufacture of motor vehicle electrical equipment, such as generators, alternators, spark plugs, ignition wiring harnesses, power window and door systems, assembly of purchased gauges into instrument panels, voltage regulators, etc.', 1, 398867, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398869, '29.32', 'Manufacture of other parts and accessories for motor vehicles', 'This class includes: - manufacture of diverse parts and accessories for motor vehicles: . brakes, gearboxes, axles, road wheels, suspension shock absorbers, radiators, silencers, exhaust pipes, catalytic converters, clutches, steering wheels, steering columns and steering boxes - manufacture of parts and accessories of bodies for motor vehicles: . safety belts, airbags, doors, bumpers - manufacture of car seats', 1, 398867, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398870, '30', 'Manufacture of other transport equipment', 'This division includes the manufacture of transportation equipment such as ship building and boat manufacturing, the manufacture of railroad rolling stock and locomotives, air and spacecraft and the manufacture of parts thereof.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398871, '30.1', 'Building of ships and boats', 'This group includes the building of ships, boats and other floating structures for transportation and other commercial purposes, as well as for sports and recreational purposes.', 1, 398870, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398872, '30.11', 'Building of ships and floating structures', 'This class includes the building of ships, except vessels for sports or recreation, and the construction of floating structures: This class includes: - building of commercial vessels: . passenger vessels, ferry boats, cargo ships, tankers, tugs etc. - building of warships - building of fishing boats and fish-processing factory vessels', 1, 398871, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398873, '30.12', 'Building of pleasure and sporting boats', 'This class includes: - manufacture of inflatable boats and rafts - building of sailboats with or without auxiliary motor - building of motor boats - building of recreation-type hovercraft - manufacture of personal watercraft - manufacture of other pleasure and sporting boats: . canoes, kayaks, rowing boats, skiffs', 1, 398871, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398874, '30.2', 'Manufacture of railway locomotives and rolling stock', '', 1, 398870, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398875, '30.2', 'Manufacture of railway locomotives and rolling stock', 'This class includes: - manufacture of electric, diesel, steam and other rail locomotives - manufacture of self-propelled railway or tramway coaches, vans and trucks, maintenance or service vehicles - manufacture of railway or tramway rolling stock, not self-propelled: . passenger coaches, goods vans, tank wagons, self-discharging vans and wagons, workshop vans, crane vans, tenders etc. - manufacture of specialised parts of railway or tramway locomotives or of rolling stock: . bogies, axles and wheels, brakes and parts of brakes; hooks and coupling devices, buffers and buffer parts; shock absorbers; wagon and locomotive frames; bodies; corridor connections etc.', 1, 398874, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398876, '30.3', 'Manufacture of air and spacecraft and related machinery', '', 1, 398870, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398877, '30.3', 'Manufacture of air and spacecraft and related machinery', 'This class includes: - manufacture of airplanes for the transport of goods or passengers, for use by the defence forces, for sport or other purposes - manufacture of helicopters - manufacture of gliders, hang-gliders - manufacture of dirigibles and hot air balloons - manufacture of parts and accessories of the aircraft of this class: . major assemblies such as fuselages, wings, doors, control surfaces, landing gear, fuel tanks, nacelles etc. . airscrews, helicopter rotors and propelled rotor blades . motors and engines of a kind typically found on aircraft . parts of turbojets and turboprops for aircraft - manufacture of ground flying trainers - manufacture of spacecraft and launch vehicles, satellites, planetary probes, orbital stations, shuttles - manufacture of intercontinental ballistic missiles (ICBM)', 1, 398876, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398878, '30.4', 'Manufacture of military fighting vehicles', '', 1, 398870, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398879, '30.4', 'Manufacture of military fighting vehicles', 'This class includes: - manufacture of tanks - manufacture of armoured amphibious military vehicles - manufacture of other military fighting vehicles', 1, 398878, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398880, '30.9', 'Manufacture of transport equipment n.e.c.', 'This group includes the manufacture of transport equipment other than motor vehicles and rail, water, air or space transport equipment and military vehicles.', 1, 398870, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398881, '30.91', 'Manufacture of motorcycles', 'This class includes: - manufacture of motorcycles, mopeds and cycles fitted with an auxiliary engine - manufacture of engines for motorcycles - manufacture of sidecars - manufacture of parts and accessories for motorcycles', 1, 398880, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398882, '30.92', 'Manufacture of bicycles and invalid carriages', 'This class includes: - manufacture of non-motorised bicycles and other cycles, including (delivery) tricycles, tandems, children''s bicycles and tricycles - manufacture of parts and accessories of bicycles - manufacture of invalid carriages with or without motor - manufacture of parts and accessories of invalid carriages - manufacture of baby carriages', 1, 398880, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398883, '30.99', 'Manufacture of other transport equipment n.e.c.', 'This class includes: - manufacture of hand-propelled vehicles: luggage trucks, handcarts, sledges, shopping carts etc. - manufacture of vehicles drawn by animals: sulkies, donkey-carts, hearses etc.', 1, 398880, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398884, '31', 'Manufacture of furniture', 'This division includes the manufacture of furniture and related products of any material except stone, concrete and ceramic. The processes used in the manufacture of furniture are standard methods of forming materials and assembling components, including cutting, moulding and laminating. The design of the article, for both aesthetic and functional qualities, is an important aspect of the production process. Some of the processes used in furniture manufacturing are similar to processes that are used in other segments of manufacturing. For example, cutting and assembly occurs in the production of wood trusses that are classified in division 16 (Manufacture of wood and wood products). However, the multiple processes distinguish wood furniture manufacturing from wood product manufacturing. Similarly, metal furniture manufacturing uses techniques that are also employed in the manufacture of roll-formed products classified in division 25 (Manufacture of fabricated metal products). The moulding process for plastics furniture is similar to the moulding of other plastics products. However, the manufacture of plastics furniture tends to be a specialised activity.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398885, '31', 'Manufacture of furniture', '', 1, 398884, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398886, '31.01', 'Manufacture of office and shop furniture', 'This class includes the manufacture of furniture of any kind, any material (except stone, concrete or ceramic) for any place and various purposes. This class includes: - manufacture of chairs and seats for offices, workrooms, hotels, restaurants and public premises - manufacture of chairs and seats for theatres, cinemas and the like - manufacture of special furniture for shops: counters, display cases, shelves etc. - manufacture of office furniture - manufacture of laboratory benches, stools, and other laboratory seating, laboratory furniture (e.g. cabinets and tables) - manufacture of furniture for churches, schools, restaurants', 1, 398884, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398887, '31.02', 'Manufacture of kitchen furniture', 'This class includes: - manufacture of kitchen furniture', 1, 398884, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398888, '31.03', 'Manufacture of mattresses', 'This class includes: - manufacture of mattresses: . mattresses fitted with springs or stuffed or internally fitted with a supporting material . uncovered cellular rubber or plastic mattresses - manufacture of mattress supports', 1, 398884, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398889, '31.09', 'Manufacture of other furniture', 'This class includes: - manufacture of sofas, sofa beds and sofa sets - manufacture of garden chairs and seats - manufacture of furniture for bedrooms, living rooms, gardens etc. - manufacture of cabinets for sewing machines, televisions etc.', 1, 398884, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398890, '32', 'Other manufacturing', 'This division includes the manufacture of a variety of goods not covered in other parts of the classification. Since this is a residual division, production processes, input materials and use of the produced goods can vary widely and usual criteria for grouping classes into divisions have not been applied here.', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398891, '32.1', 'Manufacture of jewellery, bijouterie and related articles', 'This group includes the manufacture of jewellery and imitation jewellery articles.', 1, 398890, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398892, '32.11', 'Striking of coins', 'This class includes: - manufacture of coins, including coins for use as legal tender, whether or not of precious metal', 1, 398891, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398893, '32.12', 'Manufacture of jewellery and related articles', 'This class includes: - production of worked pearls - production of precious and semi-precious stones in the worked state, including the working of industrial quality stones and synthetic or reconstructed precious or semi-precious stones - working of diamonds - manufacture of jewellery of precious metal or of base metals clad with precious metals, or precious or semi-precious stones, or of combinations of precious metal and precious or semi-precious stones or of other materials - manufacture of goldsmiths'' articles of precious metals or of base metals clad with precious metals: . dinnerware, flatware, hollowware, toilet articles, office or desk articles, articles for religious use etc. - manufacture of technical or laboratory articles of precious metal (except instruments and parts thereof): crucibles, spatulas, electroplating anodes etc. - manufacture of precious metal watch bands, wristbands, watch straps and cigarette cases', 1, 398891, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398894, '32.13', 'Manufacture of imitation jewellery and related articles', 'This class includes: - manufacture of costume or imitation jewellery: . rings, bracelets, necklaces, and similar articles of jewellery made from base metals plated with precious metals . jewellery containing imitation stones such as imitation gems stones, imitation diamonds, and similar - manufacture of metal watch bands (except precious metal)', 1, 398891, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398895, '32.2', 'Manufacture of musical instruments', '', 1, 398890, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398896, '32.2', 'Manufacture of musical instruments', 'This class includes: - manufacture of stringed instruments - manufacture of keyboard stringed instruments, including automatic pianos - manufacture of keyboard pipe organs, including harmoniums and similar keyboard instruments with free metal reeds - manufacture of accordions and similar instruments, including mouth organs - manufacture of wind instruments - manufacture of percussion musical instruments - manufacture of musical instruments, the sound of which is produced electronically - manufacture of musical boxes, fairground organs, calliopes etc. - manufacture of instrument parts and accessories: . metronomes, tuning forks, pitch pipes, cards, discs and rolls for automatic mechanical instruments etc.', 1, 398895, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398897, '32.3', 'Manufacture of sports goods', '', 1, 398890, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398898, '32.3', 'Manufacture of sports goods', 'This class includes the manufacture of sporting and athletic goods (except apparel and footwear). This class includes: - manufacture of articles and equipment for sports, outdoor and indoor games, of any material: . hard, soft and inflatable balls . rackets, bats and clubs . skis, bindings and poles . ski-boots . sailboards and surfboards . requisites for sport fishing, including landing nets . requisites for hunting, mountain climbing etc. . leather sports gloves and sports headgear . basins for swimming and padding pools etc. . ice skates, roller skates etc. . bows and crossbows . gymnasium, fitness centre or athletic equipment', 1, 398897, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398899, '32.4', 'Manufacture of games and toys', '', 1, 398890, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398900, '32.4', 'Manufacture of games and toys', 'This class includes the manufacture of dolls, toys and games (including electronic games), scale models and children''s vehicles (except metal bicycles and tricycles). This class includes: - manufacture of dolls and doll garments, parts and accessories - manufacture of action figures - manufacture of toy animals - manufacture of toy musical instruments - manufacture of playing cards - manufacture of board games and similar games - manufacture of electronic games: chess etc. - manufacture of reduced-size ("scale") models and similar recreational models, electrical trains, construction sets etc. - manufacture of coin-operated games, billiards, special tables for casino games, etc. - manufacture of articles for funfair, table or parlour games - manufacture of wheeled toys designed to be ridden, including plastic bicycles and tricycles - manufacture of puzzles and similar articles', 1, 398899, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398901, '32.5', 'Manufacture of medical and dental instruments and supplies', '', 1, 398890, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398902, '32.5', 'Manufacture of medical and dental instruments and supplies', 'This class includes the manufacture of laboratory apparatus, surgical and medical instruments, surgical appliances and supplies, dental equipment and supplies, orthodontic goods, dentures and orthodontic appliances. Included is the manufacture of medical, dental and similar furniture, where the additional specific functions determine the purpose of the product, such as dentist''s chairs with built-in hydraulic functions. This class includes: - manufacture of surgical drapes and sterile string and tissue - manufacture of dental fillings and cements (except denture adhesives), dental wax and other dental plaster preparations - manufacture of bone reconstruction cements - manufacture of dental laboratory furnaces - manufacture of laboratory ultrasonic cleaning machinery - manufacture of laboratory sterilisers - manufacture of laboratory type distilling apparatus, laboratory centrifuges - manufacture of medical, surgical, dental or veterinary furniture, such as: . operating tables . examination tables . hospital beds with mechanical fittings . dentists'' chairs - manufacture of bone plates and screws, syringes, needles, catheters, cannulae, etc. - manufacture of dental instruments (including dentists'' chairs incorporating dental equipment) - manufacture of artificial teeth, bridges, etc., made in dental labs - manufacture of orthopedic and prosthetic devices - manufacture of glass eyes - manufacture of medical thermometers - manufacture of ophthalmic goods, eyeglasses, sunglasses, lenses ground to prescription, contact lenses, safety goggles', 1, 398901, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398903, '32.9', 'Manufacturing n.e.c.', '', 1, 398890, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398904, '32.91', 'Manufacture of brooms and brushes', 'This class includes: - manufacture of brooms and brushes, including brushes constituting parts of machines, hand-operated mechanical floor sweepers, mops and feather dusters, paint brushes, paint pads and rollers, squeegees and other brushes, brooms, mops etc. - manufacture of shoe and clothes brushes', 1, 398903, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398905, '32.99', 'Other manufacturing n.e.c. ', 'This class includes: - manufacture of protective safety equipment . manufacture of fire resistant and protective safety clothing . manufacture of linemen''s safety belts and other belts for occupational use . manufacture of cork life preservers . manufacture of plastics hard hats and other personal safety equipment of plastics (e.g. athletic helmets) . manufacture of fire fighting protection suits . manufacture of metal safety headgear and other metal personal safety devices . manufacture of ear and noise plugs (e.g. for swimming and noise protection) . manufacture of gas masks - manufacture of pens and pencils of all kinds whether or not mechanical - manufacture of pencil leads - manufacture of date, sealing or numbering stamps, hand-operated devices for printing, or embossing labels, hand printing sets, prepared typewriter ribbons and inked pads - manufacture of globes - manufacture of umbrellas, sun-umbrellas, walking sticks, seat-sticks - manufacture of buttons, press-fasteners, snap-fasteners, press-studs, slide fasteners - manufacture of cigarette lighters - manufacture of articles of personal use: smoking pipes, scent sprays, vacuum flasks and other vacuum vessels for personal or household use, wigs, false beards, eyebrows - manufacture of miscellaneous articles: candles, tapers and the like; artificial flowers, fruit and foliage; jokes and novelties; hand sieves and hand riddles; tailors'' dummies; burial coffins etc. - manufacture of floral baskets, bouquets, wreaths and similar articles - taxidermy activities', 1, 398903, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398906, '33', 'Repair and installation of machinery and equipment', 'This division includes the specialised repair of goods produced in the manufacturing sector with the aim to restore machinery, equipment and other products to working order. The provision of general or routine maintenance (i.e. servicing) on such products to ensure they work efficiently and to prevent breakdown and unnecessary repairs is included. This division does only include specialised repair and maintenance activities. A substantial amount of repair is also done by manufacturers of machinery, equipment and other goods, in which case the classification of units engaged in these repair and manufacturing activities is done according to the value-added principle which would often assign these combined activities to the manufacture of the good. The same principle is applied for combined trade and repair. The rebuilding or remanufacture of machinery and equipment is considered a manufacturing activity and included in other divisions of this section. Repair and maintenance of goods that are utilised as capital goods as well as consumer goods is typically classified as repair and maintenance of household goods (e.g. office and household furniture repair, see 95.24).', 1, 398568, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398907, '33.1', 'Repair of fabricated metal products, machinery and equipment', 'This group includes the specialised repair of goods produced in the manufacturing sector with the aim to restore these metal products, machinery, equipment and other products to working order. The provision of general or routine maintenance (i.e. servicing) on such products to ensure they work efficiently and to prevent breakdown and unnecessary repairs is included.', 1, 398906, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398908, '33.11', 'Repair of fabricated metal products', 'This class includes the repair and maintenance of fabricated metal products of division 25. This class includes: - repair of metal tanks, reservoirs and containers - repair and maintenance for pipes and pipelines - mobile welding repair - repair of steel shipping drums - repair and maintenance of steam or other vapour generators - repair and maintenance of auxiliary plant for use with steam generators: . condensers, economisers, superheaters, steam collectors and accumulators - repair and maintenance of nuclear reactors, except isotope separators - repair and maintenance of parts for marine or power boilers - platework repair of central heating boilers and radiators - repair and maintenance of fire arms and ordnance (including repair of sporting and recreational guns) - repair and maintenance of shopping carts', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398909, '33.12', 'Repair of machinery', 'This class includes the repair and maintenance of industrial machinery and equipment like sharpening or installing commercial and industrial machinery blades and saws; the provision of welding (e.g. automotive, general) repair services; the repair of agricultural and other heavy and industrial machinery and equipment (e.g. forklifts and other materials handling equipment, machine tools, commercial refrigeration equipment, construction equipment and mining machinery), including machinery and equipment of division 28. This class includes: - repair and maintenance of non-motor vehicle engines - repair and maintenance of pumps, compressors and related equipment - repair and maintenance of fluid power machinery - repair of valves - repair of gearing and driving elements - repair and maintenance of industrial process furnaces - repair and maintenance of lifting and handling equipment - repair and maintenance of industrial refrigeration equipment and air purifying equipment - repair and maintenance of commercial-type general-purpose machinery - repair of power-driven hand-tools - repair and maintenance of metal cutting and metal forming machine tools and accessories - repair and maintenance of other machine tools - repair and maintenance of agricultural tractors - repair and maintenance of agricultural machinery and forestry and logging machinery - repair and maintenance of metallurgy machinery - repair and maintenance of mining, construction, and oil and gas field machinery - repair and maintenance of food, beverage, and tobacco processing machinery - repair and maintenance of textile apparel, and leather production machinery - repair and maintenance of papermaking machinery - repair and maintenance of plastic and rubber machinery - repair and maintenance of other special-purpose machinery of division 28 - repair and maintenance of weighing equipment - repair and maintenance of vending machines - repair and maintenance of cash registers - repair and maintenance of photocopy machines - repair of calculators, electronic or not - repair of typewriters', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398910, '33.13', 'Repair of electronic and optical equipment', 'This class includes the repair and maintenance of goods produced in groups 26.5, 26.6 and 26.7, except those that are considered household goods. This class includes: - repair and maintenance of the measuring, testing, navigating and control equipment of group 26.5, such as: . aircraft engine instruments . automotive emissions testing equipment . meteorological instruments . physical, electrical and chemical properties testing and inspection equipment . surveying instruments . radiation detection and monitoring instruments - repair and maintenance of irradiation, electromedical and electrotherapeutic equipment of class 26.60, such as: . magnetic resonance imaging equipment . medical ultrasound equipment . pacemakers . hearing aids . electrocardiographs . electromedical endoscopic equipment . irradiation apparatus - repair and maintenance of optical instruments and equipment of class 26.70, if the use is mainly commercial, such as: . binoculars . microscopes (except electron and proton microscopes) . telescopes . prisms and lenses (except ophthalmic) . photographic equipment', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398911, '33.14', 'Repair of electrical equipment', 'This class includes the repair and maintenance of goods of division 27, except those in class group 27.5 (domestic appliances). This class includes: - repair and maintenance of power, distribution, and specialty transformers - repair and maintenance of electric motors, generators, and motor generator sets - repair and maintenance of switchgear and switchboard apparatus - repair and maintenance of relays and industrial controls - repair and maintenance of primary and storage batteries - repair and maintenance of electric lighting equipment - repair and maintenance of current-carrying wiring devices and non current-carrying wiring devices for wiring electrical circuits', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398912, '33.15', 'Repair and maintenance of ships and boats', 'This class includes the repair and maintenance of ships and boats. However, the factory rebuilding or overhaul of ships is classified in division 30. This class includes: - repair and routine maintenance of ships - repair and maintenance of pleasure boats', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398913, '33.16', 'Repair and maintenance of aircraft and spacecraft', 'This class includes the repair and maintenance of aircraft and spacecraft. This class includes: - repair and maintenance of aircraft (except factory conversion, factory overhaul, factory rebuilding) - repair and maintenance of aircraft engines', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398914, '33.17', 'Repair and maintenance of other transport equipment', 'This class includes the repair and maintenance of other transport equipment of division 30, except motorcycles and bicycles. This class includes: - repair and maintenance of locomotives and railroad cars (except factory rebuilding or factory conversion) - repair of animal drawn buggies and wagons', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398915, '33.19', 'Repair of other equipment', 'This class includes the repair and maintenance of equipment not covered in other groups of this division. This class includes: - repair of fishing nets, including mending - repair or ropes, riggings, canvas and tarpaulins - repair of fertiliser and chemical storage bags - repair or reconditioning of wooden pallets, shipping drums or barrels, and similar items - repair of pinball machines and other coin-operated games - restoring of organs and other historical musical instruments', 1, 398907, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398916, '33.2', 'Installation of industrial machinery and equipment', '', 1, 398906, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398917, '33.2', 'Installation of industrial machinery and equipment', 'This class includes the specialised installation of machinery. However, the installation of equipment that forms an integral part of buildings or similar structures, such as installation of escalators, electrical wiring, burglar alarm systems or air-conditioning systems, is classified as construction. This class includes: - installation of industrial machinery in industrial plant - installation and assembly of industrial process control equipment - installation of other industrial equipment, e.g.: . communications equipment . mainframe and similar computers . irradiation and electromedical equipment etc. - dismantling large-scale machinery and equipment - activities of millwrights - machine rigging - installation of bowling alley equipment', 1, 398916, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398918, 'D', 'ELECTRICITY, GAS, STEAM AND AIR CONDITIONING SUPPLY', 'This section includes the activity of providing electric power, natural gas, steam, hot water and the like through a permanent infrastructure (network) of lines, mains and pipes. The dimension of the network is not decisive; also included are the distribution of electricity, gas, steam, hot water and the like in industrial parks or residential buildings. This section therefore includes the operation of electric and gas utilities, which generate, control and distribute electric power or gas.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398919, '35', 'Electricity, gas, steam and air conditioning supply', '', 1, 398918, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398920, '35.1', 'Electric power generation, transmission and distribution', 'This group includes the generation of bulk electric power, transmission from generating facilities to distribution centres and distribution to end users.', 1, 398919, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398921, '35.11', 'Production of electricity', 'This class includes: - operation of generation facilities that produce electric energy; including thermal, nuclear, hydroelectric, gas turbine, diesel and renewable', 1, 398920, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398922, '35.12', 'Transmission of electricity', 'This class includes: - operation of transmission systems that convey the electricity from the generation facility to the distribution system', 1, 398920, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398923, '35.13', 'Distribution of electricity', 'This class includes: - operation of distribution systems (i.e., consisting of lines, poles, meters, and wiring) that convey electric power received from the generation facility or the transmission system to the final consumer', 1, 398920, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398924, '35.14', 'Trade of electricity', 'This class includes: - sale of electricity to the user - activities of electric power brokers or agents that arrange the sale of electricity via power distribution systems operated by others - operation of electricity and transmission capacity exchanges for electric power', 1, 398920, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398925, '35.2', 'Manufacture of gas; distribution of gaseous fuels through mains', 'This group includes the manufacture of gas and the distribution of natural or synthetic gas to the consumer through a system of mains. Gas marketers or brokers, which arrange the sale of natural gas over distribution systems operated by others, are included. The separate operation of gas pipelines, typically done over long distances, connecting producers with distributors of gas, or between urban centres, is excluded from this group and classified with other pipeline transport activities.', 1, 398919, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398926, '35.21', 'Manufacture of gas', 'This class includes: - production of gas for the purpose of gas supply by carbonation of coal, from by-products of agriculture or from waste - manufacture of gaseous fuels with a specified calorific value, by purification, blending and other processes from gases of various types including natural gas', 1, 398925, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398927, '35.22', 'Distribution of gaseous fuels through mains', 'This class includes: - distribution and supply of gaseous fuels of all kinds through a system of mains', 1, 398925, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398928, '35.23', 'Trade of gas through mains', 'This class includes: - sale of gas to the user through mains - activities of gas brokers or agents that arrange the sale of gas over gas distribution systems operated by others - commodity and transport capacity exchanges for gaseous fuels', 1, 398925, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398929, '35.3', 'Steam and air conditioning supply', '', 1, 398919, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398930, '35.3', 'Steam and air conditioning supply', 'This class includes: - production, collection and distribution of steam and hot water for heating, power and other purposes - production and distribution of cooled air - production and distribution of chilled water for cooling purposes - production of ice, for food and non-food (e.g. cooling) purposes', 1, 398929, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398931, 'E', 'WATER SUPPLY; SEWERAGE, WASTE MANAGEMENT AND REMEDIATION ACTIVITIES', 'This section includes activities related to the management (including collection, treatment and disposal) of various forms of waste, such as solid or non-solid industrial or household waste, as well as contaminated sites. The output of the waste or sewage treatment process can either be disposed of or become an input into other production processes.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398932, '36', 'Water collection, treatment and supply', 'This division includes the collection, treatment and distribution of water for domestic and industrial needs. Collection of water from various sources, as well as distribution by various means is included.', 1, 398931, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398933, '36', 'Water collection, treatment and supply', '', 1, 398932, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398934, '36', 'Water collection, treatment and supply', 'This class includes water collection, treatment and distribution activities for domestic and industrial needs. Collection of water from various sources, as well as distribution by various means is included. This class includes: - collection of water from rivers, lakes, wells etc. - collection of rain water - purification of water for water supply purposes - treatment of water for industrial and other purposes - desalting of sea or ground water to produce water as the principal product of interest - distribution of water through mains, by trucks or other means - operation of irrigation canals', 1, 398932, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398935, '37', 'Sewerage', 'This division includes the operation of sewer systems or sewage treatment facilities that collect, treat, and dispose of sewage.', 1, 398931, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398936, '37', 'Sewerage', '', 1, 398935, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398937, '37', 'Sewerage', 'This class includes: - operation of sewer systems or sewer treatment facilities - collecting and transporting of human or industrial wastewater from one or several users, as well as rain water by means of sewerage networks, collectors, tanks and other means of transport (sewage vehicles etc.) - emptying and cleaning of cesspools and septic tanks, sinks and pits from sewage; servicing of chemical toilets - treatment of wastewater (including human and industrial wastewater, water from swimming pools etc.) by means of physical, chemical and biological processes like dilution, screening, filtering, sedimentation etc. - maintenance and cleaning of sewers and drains, including sewer rodding', 1, 398935, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398938, '38', 'Waste collection, treatment and disposal activities; materials recovery', 'This division includes the collection, treatment, and disposal of waste materials.', 1, 398931, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398939, '38.1', 'Waste collection', 'This group includes the collection of waste from households and businesses by means of refuse bins, wheeled bins, containers, etc. It includes collection of non-hazardous and hazardous waste e.g. waste from households, used batteries, used cooking oils and fats, waste oil from ships and used oil from garages, as well as construction and demolition waste.', 1, 398938, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398940, '38.11', 'Collection of non-hazardous waste', 'This class includes: - collection of non-hazardous solid waste (i.e. garbage) within a local area, such as collection of waste from households and businesses by means of refuse bins, wheeled bins, containers etc may include mixed recoverable materials - collection of recyclable materials - collection of refuse in litter-bins in public places', 1, 398939, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398941, '38.12', 'Collection of hazardous waste', 'This class includes the collection of solid and non-solid hazardous waste, i.e. explosive, oxidizing, flammable, toxic, irritant, carcinogenic, corrosive, infectious and other substances and preparations harmful for human health and environment. It may also entail identification, treatment, packaging and labeling of waste for the purposes of transport. This class includes: - collection of hazardous waste, such as: . used oil from shipment or garages . bio-hazardous waste . nuclear waste . used batteries etc. - operation of waste transfer stations for hazardous waste', 1, 398939, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398942, '38.2', 'Waste treatment and disposal', 'This group includes the disposal and treatment prior to disposal of various forms of waste by different means, such as treatment of organic waste with the aim of disposal; treatment and disposal of toxic live or dead animals and other contaminated waste; treatment and disposal of transition radioactive waste from hospitals, etc.; dumping of refuse on land or in water; burial or ploughing-under of refuse; disposal of used goods such as refrigerators to eliminate harmful waste; disposal of waste by incineration or combustion. Included is also energy recovery resulting from waste incineration process.', 1, 398938, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398943, '38.21', 'Treatment and disposal of non-hazardous waste', 'This class includes the disposal and treatment prior to disposal of solid or non-solid non-hazardous waste: - operation of landfills for the disposal of non-hazardous waste - disposal of non-hazardous waste by combustion or incineration or other methods, with or without the resulting production of electricity or steam, compost, substitute fuels, biogas, ashes or other by-products for further use etc. - treatment of organic waste for disposal', 1, 398942, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398944, '38.22', 'Treatment and disposal of hazardous waste', 'This class includes the disposal and treatment prior to disposal of solid or non-solid hazardous waste, including waste that if explosive, oxidising, flammable, toxic, irritant, carcinogenic, corrosive, infectious and other substances and preparations harmful for human health and environment. This class includes: - operation of facilities for treatment of hazardous waste - treatment and disposal of toxic live or dead animals and other contaminated waste - incineration of hazardous waste - disposal of used goods such as refrigerators to eliminate harmful waste - treatment, disposal and storage of radioactive nuclear waste including: . treatment and disposal of transition radioactive waste, i.e. decaying within the period of transport, from hospitals . encapsulation, preparation and other treatment of nuclear waste for storage', 1, 398942, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398945, '38.3', 'Materials recovery', '', 1, 398938, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398946, '38.31', 'Dismantling of wrecks', 'This class includes dismantling of wrecks of any type (automobiles, ships, computers, televisions and other equipment) for materials recovery.', 1, 398945, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398947, '38.32', 'Recovery of sorted materials', 'This class includes the processing of metal and non-metal waste and scrap and other articles into secondary raw materials, usually involving a mechanical or chemical transformation process. Examples of the mechanical or chemical transformation processes that are undertaken are: - mechanical crushing of metal waste from used cars, washing machines, bikes etc. - mechanical reduction of large iron pieces such as railway wagons - shredding of metal waste, end-of-life vehicles etc. - other methods of mechanical treatment as cutting, pressing to reduce the volume - reclaiming metals out of photographic waste, e.g. fixer solution or photographic films and paper - reclaiming of rubber such as used tyres to produce secondary raw material - sorting and pelleting of plastics to produce secondary raw material for tubes, flower pots, pallets and the like - processing (cleaning, melting, grinding) of plastic or rubber waste to granulates - crushing, cleaning and sorting of glass - crushing, cleaning and sorting of other waste such as demolition waste to obtain secondary raw material - processing of used cooking oils and fats into secondary raw materials - processing of other food, beverage and tobacco waste and residual substances into secondary raw materials', 1, 398945, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398948, '39', 'Remediation activities and other waste management services', 'This division includes the provision of remediation services, i.e. the cleanup of contaminated buildings and sites, soil, surface or ground water.', 1, 398931, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398949, '39', 'Remediation activities and other waste management services', '', 1, 398948, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398950, '39', 'Remediation activities and other waste management services', 'This class includes: - decontamination of soils and groundwater at the place of pollution, either in situ or ex situ, using e.g. mechanical, chemical or biological methods - decontamination of industrial plants or sites, including nuclear plants and sites - decontamination and cleaning up of surface water following accidental pollution, e.g. through collection of pollutants or through application of chemicals - cleaning up oil spills and other pollutions on land, in surface water, in ocean and seas, including coastal areas - asbestos, lead paint, and other toxic material abatement - clearing of landmines and the like (including detonation) - other specialised pollution-control activities', 1, 398948, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398951, 'F', 'CONSTRUCTION', 'This section includes general construction and specialised construction activities for buildings and civil engineering works. It includes new work, repair, additions and alterations, the erection of prefabricated buildings or structures on the site and also construction of a temporary nature. General construction is the construction of entire dwellings, office buildings, stores and other public and utility buildings, farm buildings etc., or the construction of civil engineering works such as motorways, streets, bridges, tunnels, railways, airfields, harbours and other water projects, irrigation systems, sewerage systems, industrial facilities, pipelines and electric lines, sports facilities etc. This work can be carried out on own account or on a fee or contract basis. Portions of the work and sometimes even the whole practical work can be subcontracted out. A unit that carries the overall responsibility for a construction project is classified here. Also included is the repair of buildings and engineering works. This section includes the complete construction of buildings (division 41), the complete construction of civil engineering works (division 42), as well as specialised construction activities, if carried out only as a part of the construction process (division 43). The rental of construction equipment with operator is classified with the specific construction activity carried out with this equipment.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398952, '41', 'Construction of buildings', 'This division includes general construction of buildings of all kinds. It includes new work, repair, additions and alterations, the erection of pre-fabricated buildings or structures on the site and also construction of temporary nature. Included is the construction of entire dwellings, office buildings, stores and other public and utility buildings, farm buildings, etc.', 1, 398951, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398953, '41.1', 'Development of building projects', '', 1, 398952, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398954, '41.1', 'Development of building projects', 'This class includes: - development of building projects for residential and non-residential buildings by bringing together financial, technical and physical means to realise the building projects for later sale', 1, 398953, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398955, '41.2', 'Construction of residential and non-residential buildings', 'This group includes the construction of complete residential or non-residential buildings, on own account for sale or on a fee or contract basis. Outsourcing parts or even the whole construction process is possible. If only specialised parts of the construction process are carried out, the activity is classified in division 43.', 1, 398952, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398956, '41.2', 'Construction of residential and non-residential buildings', 'This class includes: - construction of all types of residential buildings: . single-family houses . multi-family buildings, including high-rise buildings - construction of all types of non-residential buildings: . buildings for industrial production, e.g. factories, workshops, assembly plants etc. . hospitals, schools, office buildings . hotels, stores, shopping malls, restaurants . airport buildings . indoor sports facilities . parking garages, including underground parking garages . warehouses . religious buildings - assembly and erection of prefabricated constructions on the site', 1, 398955, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398957, '42', 'Civil engineering', 'This division includes general construction for civil engineering objects. It includes new work, repair, additions and alterations, the erection of pre-fabricated structures on the site and also construction of temporary nature. Included is the construction of heavy constructions such as motorways, streets, bridges, tunnels, railways, airfields, harbours and other water projects, irrigation systems, sewerage systems, industrial facilities, pipelines and electric lines, outdoor sports facilities, etc. This work can be carried out on own account or on a fee or contract basis. Portions of the work and sometimes even the whole practical work can be subcontracted out.', 1, 398951, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398958, '42.1', 'Construction of roads and railways', '', 1, 398957, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398959, '42.11', 'Construction of roads and motorways', 'This class includes: - construction of motorways, streets, roads, other vehicular and pedestrian ways - surface work on streets, roads, highways, bridges or tunnels: . asphalt paving of roads . road painting and other marking . installation of crash barriers, traffic signs and the like - construction of airfield runways', 1, 398958, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398960, '42.12', 'Construction of railways and underground railways', 'This class includes: - construction of railways and subways', 1, 398958, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398961, '42.13', 'Construction of bridges and tunnels', 'This class includes: - construction of bridges, including those for elevated highways - construction of tunnels', 1, 398958, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398962, '42.2', 'Construction of utility projects', '', 1, 398957, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398963, '42.21', 'Construction of utility projects for fluids', 'This class includes the construction of distribution lines for transportation of fluids and related buildings and structures that are integral part of these systems. This class includes: - construction of civil engineering constructions for: . long-distance and urban pipelines . water main and line construction . irrigation systems (canals) . reservoirs - construction of: . sewer systems, including repair . sewage disposal plants . pumping stations', 1, 398962, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398964, '42.22', 'Construction of utility projects for electricity and telecommunications', 'This class includes the construction of distribution lines for electricity and telecommunications and related buildings and structures that are integral part of these systems. This class includes: - construction of civil engineering constructions for: . long-distance and urban communication and power lines . power plants', 1, 398962, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398965, '42.9', 'Construction of other civil engineering projects', '', 1, 398957, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398966, '42.91', 'Construction of water projects', 'This class includes: - construction of: . waterways, harbour and river works, pleasure ports (marinas), locks, etc. . dams and dykes - dredging of waterways', 1, 398965, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398967, '42.99', 'Construction of other civil engineering projects n.e.c.', 'This class includes: - construction of industrial facilities, except buildings, such as: . refineries . chemical plants - construction work, other than buildings, such as: . outdoor sports facilities', 1, 398965, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398968, '43', 'Specialised construction activities', 'This division includes specialised construction activities (special trades), i.e. the construction of parts of buildings and civil engineering works or preparation therefore. These activities are usually specialised in one aspect common to different structures, requiring specialised skills or equipment, such as pile-driving, foundation work, carcass work, concrete work, brick laying, stone setting, scaffolding, roof covering, etc. The erection of steel structures is included, provided that the parts are not produced by the same unit. Specialised construction activities are mostly carried out under subcontract, but especially in repair construction it is done directly for the owner of the property. Included is the installation of all kind of utilities that make the construction function as such. These activities are usually performed at the site of the construction, although parts of the job may be carried out in a special shop. Included are activities such as plumbing, installation of heating and air-conditioning systems, antennas, alarm systems and other electrical work, sprinkler systems, elevators and escalators, etc. Also included are insulation work (water, heat, sound), sheet metal work, commercial refrigerating work, the installation of illumination and signalling systems for roads, railways, airports, harbours, etc. Also repair of the same type as the above mentioned activities is included. Building completion activities encompass activities that contribute to the completion or finishing of a construction such as glazing, plastering, painting, floor and wall tiling or covering with other materials like parquet, carpets, wallpaper, etc., floor sanding, finish carpentry, acoustical work, cleaning of the exterior, etc. Also repair of the same type as the above mentioned activities is included. The rental of equipment with operator is classified with the associated construction activity.', 1, 398951, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398969, '43.1', 'Demolition and site preparation', 'This group includes activities of preparing a site for subsequent construction activities, including the removal of previously existing structures.', 1, 398968, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398970, '43.11', 'Demolition', 'This class includes: - demolition or wrecking of buildings and other structures', 1, 398969, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398971, '43.12', 'Site preparation', 'This class includes: - clearing of building sites - earth moving: excavation, landfill, levelling and grading of construction sites, trench digging, rock removal, blasting, etc.', 1, 398969, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398972, '43.13', 'Test drilling and boring', 'This class includes: - test drilling, test boring and core sampling for construction, geophysical, geological or similar purposes', 1, 398969, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398973, '43.2', 'Electrical, plumbing and other construction installation activities', 'This group includes installation activities that support the functioning of a building as such, including installation of electrical systems, plumbing (water, gas and sewage systems), heat and air-conditioning systems, elevators etc.', 1, 398968, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398974, '43.21', 'Electrical installation', 'This class includes the installation of electrical systems in all kinds of buildings and civil engineering structures of electrical systems. This class includes: - installation of: . electrical wiring and fittings . telecommunications wiring . computer network and cable television wiring, including fibre optic . satellite dishes . lighting systems . fire alarms . burglar alarm systems . street lighting and electrical signals . airport runway lighting . electric solar energy collectors', 1, 398973, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398975, '43.22', 'Plumbing, heat and air-conditioning installation', 'This class includes the installation of plumbing, heating and air-conditioning systems, including additions, alterations, maintenance and repair. This class includes: - installation in buildings or other construction projects of: . heating systems (electric, gas and oil) . furnaces, cooling towers . non-electric solar energy collectors . plumbing and sanitary equipment . ventilation and air-conditioning equipment and ducts . gas fittings . steam piping . fire sprinkler systems . lawn sprinkler systems - duct work installation', 1, 398973, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398976, '43.29', 'Other construction installation', 'This class includes the installation of equipment other than electrical, plumbing, heating and air-conditioning systems or industrial machinery in buildings and civil engineering structures. This class includes: - installation in buildings or other construction projects of: . elevators, escalators, including repair and maintenance . automated and revolving doors . lightning conductors . vacuum cleaning systems . thermal, sound or vibration insulation', 1, 398973, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398977, '43.3', 'Building completion and finishing', '', 1, 398968, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398978, '43.31', 'Plastering', 'This class includes: - application in buildings or other construction projects of interior and exterior plaster or stucco, including related lathing materials', 1, 398977, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398979, '43.32', 'Joinery installation', 'This class includes: - installation of doors (except automated and revolving), windows, door and window frames, of wood or other materials - installation of fitted kitchens, built-in cupboards, staircases, shop fittings and the like - interior completion such as ceilings, movable partitions, etc.', 1, 398977, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398980, '43.33', 'Floor and wall covering', 'This class includes: - laying, tiling, hanging or fitting in buildings or other construction projects of: . ceramic, concrete or cut stone wall or floor tiles, ceramic stove fitting . parquet and other wooden floor coverings, wooden wall coverings . carpets and linoleum floor coverings, including of rubber or plastic . terrazzo, marble, granite or slate floor or wall coverings . wallpaper', 1, 398977, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398981, '43.34', 'Painting and glazing', 'This class includes: - interior and exterior painting of buildings - painting of civil engineering structures - installation of glass, mirrors, etc.', 1, 398977, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398982, '43.39', 'Other building completion and finishing', 'This class includes: - cleaning of new buildings after construction - other building completion and finishing work n.e.c.', 1, 398977, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398983, '43.9', 'Other specialised construction activities', '', 1, 398968, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398984, '43.91', 'Roofing activities', 'This class includes: - erection of roofs - roof covering', 1, 398983, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398985, '43.99', 'Other specialised construction activities n.e.c.', 'This class includes: - construction activities specialising in one aspect common to different kind of structures, requiring specialised skill or equipment: . construction of foundations, including pile driving . damp proofing and water proofing works . de-humidification of buildings . shaft sinking . erection of steel elements . steel bending . bricklaying and stone setting . scaffolds and work platform erecting and dismantling, excluding rental of scaffolds and work platforms . erection of chimneys and industrial ovens . work with specialist access requirements necessitating climbing skills and the use of related equipment, e.g. working at height on tall structures - subsurface work - construction of outdoor swimming pools - steam cleaning, sand blasting and similar activities for building exteriors - rental of cranes and other building equipment, which cannot be allocated to a specific construction type, with operator', 1, 398983, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398986, 'G', 'WHOLESALE AND RETAIL TRADE; REPAIR OF MOTOR VEHICLES AND MOTORCYCLES', 'This section includes wholesale and retail sale (i.e. sale without transformation) of any type of goods, and rendering services incidental to the sale of merchandise. Wholesaling and retailing are the final steps in the distribution of merchandise. Also included in this section are the repair of motor vehicles and motorcycles. Sale without transformation is considered to include the usual operations (or manipulations) associated with trade, for example sorting, grading and assembling of goods, mixing (blending) of goods (for example sand), bottling (with or without preceding bottle cleaning), packing, breaking bulk and repacking for distribution in smaller lots, storage (whether or not frozen or chilled). Division 45 includes all activities related to the sale and repair of motor vehicles and motorcycles, while divisions 46 and 47 include all other sale activities. The distinction between division 46 (wholesale) and division 47 (retail sale) is based on the predominant type of customer. Wholesale is the resale (sale without transformation) of new and used goods to retailers, business-to-business trade, such as to industrial, commercial, institutional or professional users, or resale to other wholesalers, or involves acting as an agent or broker in buying merchandise for, or selling merchandise to, such persons or companies. The principal types of businesses included are merchant wholesalers, i.e. wholesalers who take title to the goods they sell, such as wholesale merchants or jobbers, industrial distributors, exporters, importers, and cooperative buying associations, sales branches and sales offices (but not retail stores) that are maintained by manufacturing or mining units apart from their plants or mines for the purpose of marketing their products and that do not merely take orders to be filled by direct shipments from the plants or mines. Also included are merchandise and commodity brokers, commission merchants and agents and assemblers, buyers and cooperative associations engaged in the marketing of farm products. Wholesalers frequently physically assemble, sort and grade goods in large lots, break bulk, repack and redistribute in smaller lots, for example pharmaceuticals; store, refrigerate, deliver and install goods, engage in sales promotion for their customers and label design. Retailing is the resale (sale without transformation) of new and used goods mainly to the general public for personal or household consumption or utilisation, in shops, department stores, stalls, mail-order houses, door-to-door sales persons, hawkers, consumer cooperatives, auction houses etc. Most retailers take title to the goods they sell, but some act as agents for a principal and sell either on consignment or on a commission basis.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398987, '45', 'Wholesale and retail trade and repair of motor vehicles and motorcycles', 'This division includes all activities (except manufacture and rental) related to motor vehicles and motorcycles, including lorries and trucks, such as the wholesale and retail sale of new and second-hand vehicles, the repair and maintenance of vehicles and the wholesale and retail sale of parts and accessories for motor vehicles and motorcycles. Also included are activities of commission agents involved in wholesale or retail sale of vehicles.', 1, 398986, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398988, '45.1', 'Sale of motor vehicles', '', 1, 398987, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398989, '45.11', 'Sale of cars and light motor vehicles', 'This class includes: - wholesale and retail sale of new and used vehicles: . passenger motor vehicles, including specialised passenger motor vehicles such as ambulances and minibuses, etc. (with a weight not exceeding 3,5 tons)', 1, 398988, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398990, '45.19', 'Sale of other motor vehicles', 'This class includes: - wholesale and retail sale of new and used vehicles: . lorries, trailers and semi-trailers . camping vehicles such as caravans and motor homes', 1, 398988, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398991, '45.2', 'Maintenance and repair of motor vehicles', '', 1, 398987, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398992, '45.2', 'Maintenance and repair of motor vehicles', 'This class includes: - maintenance and repair of motor vehicles: . mechanical repairs . electrical repairs . electronic injection systems repair . ordinary servicing . bodywork repair . repair of motor vehicle parts . washing, polishing, etc. . spraying and painting . repair of screens and windows . repair of motor vehicle seats - tyre and tube repair, fitting or replacement - anti-rust treatment - installation of parts and accessories not as part of the manufacturing process', 1, 398991, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398993, '45.3', 'Sale of motor vehicle parts and accessories', 'This group includes wholesale and retail trade of all kinds of parts, components, supplies, tools and accessories for motor vehicles, such as: . rubber tyres and inner tubes for tyres . spark plugs, batteries, lighting equipment and electrical parts', 1, 398987, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398994, '45.31', 'Wholesale trade of motor vehicle parts and accessories', '', 1, 398993, FALSE);
