INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398997, '45.4', 'Sale, maintenance and repair of motorcycles and related parts and accessories', 'This class includes: - wholesale and retail sale of motorcycles, including mopeds - wholesale and retail sale of parts and accessories for motorcycles (including by commission agents and mail order houses) - maintenance and repair of motorcycles', 1, 398996, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398998, '46', 'Wholesale trade, except of motor vehicles and motorcycles', 'This division includes wholesale trade on own account or on a fee or contract basis (commission trade) related to domestic wholesale trade as well as international wholesale trade (import/export).', 1, 398986, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (398999, '46.1', 'Wholesale on a fee or contract basis', 'This group includes: - activities of commission agents, commodity brokers and all other wholesalers who trade on behalf and on the account of others - activities of those involved in bringing sellers and buyers together or undertaking commercial transactions on behalf of a principal, including on the Internet.', 1, 398998, FALSE);
