INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399027, '46.43', 'Wholesale of electrical household appliances', 'This class includes: - wholesale of electrical household appliances - wholesale of radio and television equipment - wholesale of photographic and optical goods - wholesale of electrical heating appliances - wholesale of recorded audio and video tapes, CDs, DVDs', 1, 399024, FALSE);
