INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399034, '46.5', 'Wholesale of information and communication equipment', 'This group includes the wholesale of information and communications technology (ICT) equipment, i.e. computers, telecommunications equipment and parts.', 1, 398998, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399035, '46.51', 'Wholesale of computers, computer peripheral equipment and software', 'This class includes: - wholesale of computers and computer peripheral equipment - wholesale of software', 1, 399034, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399036, '46.52', 'Wholesale of electronic and telecommunications equipment and parts', 'This class includes: - wholesale of electronic valves and tubes - wholesale of semi-conductor devices - wholesale of microchips and integrated circuits - wholesale of printed circuits - wholesale of blank audio and video tapes and diskettes, magnetic and optical disks (CDs, DVDs) - wholesale of telephone and communications equipment', 1, 399034, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399037, '46.6', 'Wholesale of other machinery, equipment and supplies', 'This group includes the wholesale of specialised machinery, equipment and supplies for all kinds of industries and general purpose machinery.', 1, 398998, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399038, '46.61', 'Wholesale of agricultural machinery, equipment and supplies', 'This class includes: - wholesale of agricultural machinery and equipment: . ploughs, manure spreaders, seeders . harvesters . threshers . milking machines . poultry-keeping machines, bee-keeping machines . tractors used in agriculture and forestry', 1, 399037, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399039, '46.62', 'Wholesale of machine tools', 'This class includes: - wholesale of machine tools of any type and for any material', 1, 399037, FALSE);
