INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399042, '46.65', 'Wholesale of office furniture', 'This class includes: - wholesale trade services related to: . goods classified in 31.01 (Manufacture of office and shop furniture)', 1, 399037, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399043, '46.66', 'Wholesale of other office machinery and equipment', 'This class includes: - wholesale of office machinery and equipment, except computers and computer peripheral equipment', 1, 399037, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399044, '46.69', 'Wholesale of other machinery and equipment', 'This class includes: - wholesale of transport equipment except motor vehicles, motorcycles and bicycles - wholesale of production-line robots - wholesale of wires and switches and other installation equipment for industrial use - wholesale of other electrical material such as electrical motors, transformers - wholesale of other machinery n.e.c. for use in industry (except mining, construction, civil engineering and textile industry), trade and navigation and other services', 1, 399037, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399045, '46.7', 'Other specialised wholesale', 'This group includes other specialised wholesale activities not classified in other groups of this division. This includes the wholesale of intermediate products, except agricultural, typically not for household use.', 1, 398998, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399046, '46.71', 'Wholesale of solid, liquid and gaseous fuels and related products', 'This class includes: - wholesale of fuels, greases, lubricants, oils such as: . charcoal, coal, coke, fuel wood, naphtha . crude petroleum, crude oil, diesel fuel, gasoline, fuel oil, heating oil, kerosene . liquefied petroleum gases, butane and propane gas . lubricating oils and greases, refined petroleum products', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399047, '46.72', 'Wholesale of metals and metal ores', 'This class includes: - wholesale of ferrous and non-ferrous metal ores - wholesale of ferrous and non-ferrous metals in primary forms - wholesale of ferrous and non-ferrous semi-finished metal products n.e.c. - wholesale of gold and other precious metals', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399048, '46.73', 'Wholesale of wood, construction materials and sanitary equipment', 'This class includes: - wholesale of wood in the rough - wholesale of products of primary processing of wood - wholesale of paint and varnish - wholesale of construction materials: . sand, gravel - wholesale of wallpaper and floor coverings - wholesale of flat glass - wholesale of sanitary equipment: . baths, washbasins, toilets and other sanitary porcelain - wholesale of prefabricated buildings', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399049, '46.74', 'Wholesale of hardware, plumbing and heating equipment and supplies', 'This class includes: - wholesale of hardware and locks - wholesale of fittings and fixtures - wholesale of hot water heaters - wholesale of sanitary installation equipment: . tubes, pipes, fittings, taps, T-pieces, connections, rubber pipes etc. - wholesale of tools such as hammers, saws, screwdrivers and other hand tools', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399050, '46.75', 'Wholesale of chemical products', 'This class includes: - wholesale of industrial chemicals: . aniline, printing ink, essential oils, industrial gases, chemical glues, colouring matter, synthetic resin, methanol, paraffin, scents and flavourings, soda, industrial salt, acids and sulphurs, starch derivates etc. - wholesale of fertilisers and agrochemical products', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399051, '46.76', 'Wholesale of other intermediate products', 'This class includes: - wholesale of plastic materials in primary forms - wholesale of rubber - wholesale of textile fibres etc. - wholesale of paper in bulk - wholesale of precious stones', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399052, '46.77', 'Wholesale of waste and scrap', 'This class includes: - wholesale of metal and non-metal waste and scrap and materials for recycling, including collecting, sorting, separating, stripping of used goods such as cars in order to obtain reusable parts, packing and repacking, storage and delivery, but without a real transformation process. Additionally, the purchased and sold waste has a remaining value.', 1, 399045, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399053, '46.9', 'Non-specialised wholesale trade', '', 1, 398998, FALSE);
