INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399061, '47.22', 'Retail sale of meat and meat products in specialised stores', 'This class includes: - retail sale of meat and meat products (including poultry)', 1, 399059, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399062, '47.23', 'Retail sale of fish, crustaceans and molluscs in specialised stores', 'This class includes: - retail sale of fish, other seafood and products thereof', 1, 399059, FALSE);
