INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399066, '47.29', 'Other retail sale of food in specialised stores', 'This class includes: - retail sale of dairy products and eggs - retail sale of other food products n.e.c.', 1, 399059, FALSE);
