INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399069, '47.4', 'Retail sale of information and communication equipment in specialised stores', 'This group includes the retail sale of information and communications technology (ICT) equipment, such as computers and peripheral equipment, telecommunications equipment and consumer electronics, by specialised stores.', 1, 399055, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399070, '47.41', 'Retail sale of computers, peripheral units and software in specialised stores', 'This class includes: - retail sale of computers - retail sale of computer peripheral equipment - retail sale of video game consoles - retail sale of non-customised software, including video games', 1, 399069, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399071, '47.42', 'Retail sale of telecommunications equipment in specialised stores', '', 1, 399069, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399072, '47.43', 'Retail sale of audio and video equipment in specialised stores', 'This class includes: - retail sale of radio and television equipment - retail sale of audio and video equipment - retail sale of CD, DVD etc. players and recorders', 1, 399069, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399073, '47.5', 'Retail sale of other household equipment in specialised stores', 'This group includes the retail sale of household equipment, such as textiles, hardware, carpets, electrical appliances or furniture, in specialised stores.', 1, 399055, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399074, '47.51', 'Retail sale of textiles in specialised stores', 'This class includes: - retail sale of fabrics - retail sale of knitting yarn - retail sale of basic materials for rug, tapestry or embroidery making - retail sale of textiles - retail sale of haberdashery: needles, sewing thread etc.', 1, 399073, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399075, '47.52', 'Retail sale of hardware, paints and glass in specialised stores', 'This class includes: - retail sale of hardware - retail sale of paints, varnishes and lacquers - retail sale of flat glass - retail sale of other building material such as bricks, wood, sanitary equipment - retail sale of do-it-yourself material and equipment', 1, 399073, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399076, '47.53', 'Retail sale of carpets, rugs, wall and floor coverings in specialised stores', 'This class includes: - retail sale of carpets and rugs - retail sale of curtains and net curtains - retail sale of wallpaper and floor coverings', 1, 399073, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399077, '47.54', 'Retail sale of electrical household appliances in specialised stores', '', 1, 399073, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399078, '47.59', 'Retail sale of furniture, lighting equipment and other household articles in specialised stores', 'This class includes: - retail sale of household furniture - retail sale of articles for lighting - retail sale of household utensils and cutlery, crockery, glassware, china and pottery - retail sale of wooden, cork and wickerwork goods - retail sale of non-electrical household appliances - retail sale of musical instruments and scores - retail sale of electrical security alarm systems, such as locking devices, safes, and vaults, without installation or maintenance services - retail sale of household articles and equipment n.e.c.', 1, 399073, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399079, '47.6', 'Retail sale of cultural and recreation goods in specialised stores', 'This group includes the retail sale in specialised stores of cultural and recreation goods, such as books, newspapers, music and video recordings, sporting equipment, games and toys.', 1, 399055, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399080, '47.61', 'Retail sale of books in specialised stores', 'This class includes: - retail sale of books of all kinds', 1, 399079, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399081, '47.62', 'Retail sale of newspapers and stationery in specialised stores', '', 1, 399079, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399082, '47.63', 'Retail sale of music and video recordings in specialised stores', 'This class includes: - retail sale of musical records, audio tapes, compact discs and cassettes - retail sale of video tapes and DVDs', 1, 399079, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399083, '47.64', 'Retail sale of sporting equipment in specialised stores', 'This class includes: - retail sale of sports goods, fishing gear, camping goods, boats and bicycles', 1, 399079, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399084, '47.65', 'Retail sale of games and toys in specialised stores', 'This class includes: - retail sale of games and toys, made of all materials', 1, 399079, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399085, '47.7', 'Retail sale of other goods in specialised stores', 'This group includes the sale in specialised stores carrying a particular line of products not included in other parts of the classification, such as clothing, footwear and leather articles, pharmaceutical and medical goods, watches, souvenirs, cleaning materials, weapons, flowers and pets and others.', 1, 399055, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399086, '47.71', 'Retail sale of clothing in specialised stores', 'This class includes: - retail sale of articles of clothing - retail sale of articles of fur - retail sale of clothing accessories such as gloves, ties, braces etc.', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399087, '47.72', 'Retail sale of footwear and leather goods in specialised stores', 'This class includes: - retail sale of footwear - retail sale of leather goods - retail sale of travel accessories of leather and leather substitutes', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399088, '47.73', 'Dispensing chemist in specialised stores', 'This class includes: - retail sale of pharmaceuticals', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399089, '47.74', 'Retail sale of medical and orthopaedic goods in specialised stores', '', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399090, '47.75', 'Retail sale of cosmetic and toilet articles in specialised stores', 'This class includes: - retail sale of perfumery, cosmetic and toilet articles', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399091, '47.76', 'Retail sale of flowers, plants, seeds, fertilisers, pet animals and pet food in specialised stores', '', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399092, '47.77', 'Retail sale of watches and jewellery in specialised stores', '', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399093, '47.78', 'Other retail sale of new goods in specialised stores', 'This class includes: - retail sale of photographic, optical and precision equipment - activities of opticians - retail sale of souvenirs, craftwork and religious articles - activities of commercial art galleries - retail sale of household fuel oil, bottled gas, coal and fuel wood - retail sale of weapons and ammunition - retail sale of stamps and coins - retail trade services of commercial art galleries - retail sale of non-food products n.e.c.', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399094, '47.79', 'Retail sale of second-hand goods in stores', 'This class includes: - retail sale of second-hand books - retail sale of other second-hand goods - retail sale of antiques - activities of auctioning houses (retail)', 1, 399085, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399095, '47.8', 'Retail sale via stalls and markets', 'This group includes the retail sale of any kind of new or second-hand product in a usually movable stall either along a public road or at a fixed marketplace.', 1, 399055, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399096, '47.81', 'Retail sale via stalls and markets of food, beverages and tobacco products', '', 1, 399095, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399097, '47.82', 'Retail sale via stalls and markets of textiles, clothing and footwear', '', 1, 399095, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399098, '47.89', 'Retail sale via stalls and markets of other goods', 'This class includes: - retail sale of other goods via stalls or markets, such as: . carpets and rugs . books . games and toys . household appliances and consumer electronics . music and video recordings', 1, 399095, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399099, '47.9', 'Retail trade not in stores, stalls or markets', 'This group includes retail sale activities by mail order houses, over the Internet, through door-to-door sales, vending machines etc.', 1, 399055, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399100, '47.91', 'Retail sale via mail order houses or via Internet', 'This class includes retail sale activities via mail order houses or via Internet, i.e. retail sale activities where the buyer makes his choice on the basis of advertisements, catalogues, information provided on a website, models or any other means of advertising and places his order by mail, phone or over the Internet (usually through special means provided by a website). The products purchased can be either directly downloaded from the Internet or physically delivered to the customer. This class includes: - retail sale of any kind of product by mail order - retail sale of any kind of product over the Internet', 1, 399099, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399101, '47.99', 'Other retail sale not in stores, stalls or markets', 'This class includes: - retail sale of any kind of product in any way that is not included in previous classes: . by direct sales or door-to-door sales persons . through vending machines etc. - direct selling of fuel (heating oil, firewood, etc.), delivered to the customers premises - activities of non-store auctions (retail, except Internet) - retail sale by (non-store) commission agents', 1, 399099, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399102, 'H', 'TRANSPORTATION AND STORAGE', 'This section includes the provision of passenger or freight transport, whether scheduled or not, by rail, pipeline, road, water or air and associated activities such as terminal and parking facilities, cargo handling, storage etc. Included in this section is the rental of transport equipment with driver or operator. Also included are postal and courier activities.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399103, '49', 'Land transport and transport via pipelines', 'This division includes the transport of passengers and freight via road and rail, as well as freight transport via pipelines.', 1, 399102, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399104, '49.1', 'Passenger rail transport, interurban', '', 1, 399103, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399105, '49.1', 'Passenger rail transport, interurban', 'This class includes: - rail transportation of passengers using railroad rolling stock on mainline networks, spread over an extensive geographic area - passenger transport by interurban railways - operation of sleeping cars or dining cars as an integrated operation of railway companies', 1, 399104, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399106, '49.2', 'Freight rail transport', '', 1, 399103, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399107, '49.2', 'Freight rail transport', 'This class includes: - freight transport on mainline rail networks as well as short line freight railroads', 1, 399106, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399108, '49.3', 'Other passenger land transport ', 'This group includes all land-based passenger transport activities other than rail transport. However, rail transport as part of urban or suburban transport systems is included there.', 1, 399103, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399109, '49.31', 'Urban and suburban passenger land transport', 'This class includes: - land transport of passengers by urban or suburban transport systems. This may include different modes of land transport, such as by motor bus, tramway, streetcar, trolley bus, underground and elevated railways etc. The transport is carried out on scheduled routes normally following a fixed time schedule, entailing the picking up and setting down of passengers at normally fixed stops.', 1, 399108, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399110, '49.32', 'Taxi operation', '', 1, 399108, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399111, '49.39', 'Other passenger land transport n.e.c.', 'This class includes: - other passenger road transport: . scheduled long-distance bus services . charters, excursions and other occasional coach services . airport shuttles - operation of teleferics, funiculars, ski and cable lifts if not part of urban or suburban transit systems', 1, 399108, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399112, '49.4', 'Freight transport by road and removal services', 'This group includes all land-based freight transport activities other than rail transport.', 1, 399103, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399113, '49.41', 'Freight transport by road', 'This class includes: - all freight transport operations by road: . logging haulage . stock haulage . refrigerated haulage . heavy haulage . bulk haulage, including haulage in tanker trucks including milk collection at farms . haulage of automobiles . transport of waste and waste materials, without collection or disposal', 1, 399112, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399114, '49.42', 'Removal services', 'This class includes: - removal (relocation) services to businesses and households by road transport', 1, 399112, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399115, '49.5', 'Transport via pipeline', '', 1, 399103, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399116, '49.5', 'Transport via pipeline', 'This class includes: - transport of gases, liquids, water, slurry and other commodities via pipelines', 1, 399115, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399117, '50', 'Water transport', 'This division includes the transport of passengers or freight over water, whether scheduled or not. Also included are the operation of towing or pushing boats, excursion, cruise or sightseeing boats, ferries, water taxis etc. Although the location is an indicator for the separation between sea and inland water transport, the deciding factor is the type of vessel used. Transport on sea-going vessels is classified in groups 50.1 and 50.2, while transport using other vessels is classified in groups 50.3 and 50.4.', 1, 399102, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399118, '50.1', 'Sea and coastal passenger water transport', 'This group includes the transport of passengers on vessels designed for operating on sea or coastal waters.', 1, 399117, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399119, '50.1', 'Sea and coastal passenger water transport', 'This class includes: - transport of passengers over seas and coastal waters, whether scheduled or not: . operation of excursion, cruise or sightseeing boats . operation of ferries, water taxis etc.', 1, 399118, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399120, '50.2', 'Sea and coastal freight water transport', 'This group includes the transport of freight on vessels designed for operating on sea or coastal waters.', 1, 399117, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399121, '50.2', 'Sea and coastal freight water transport', 'This class includes: - transport of freight over seas and coastal waters, whether scheduled or not - transport by towing or pushing of barges, oil rigs etc.', 1, 399120, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399122, '50.3', 'Inland passenger water transport', 'This group includes the transport of passengers on inland waters, involving vessels that are not suitable for sea transport.', 1, 399117, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399123, '50.3', 'Inland passenger water transport', 'This class includes: - transport of passengers via rivers, canals, lakes and other inland waterways, including inside harbours and ports', 1, 399122, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399124, '50.4', 'Inland freight water transport', 'This group includes the transport of freight on inland waters, involving vessels that are not suitable for sea transport.', 1, 399117, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399125, '50.4', 'Inland freight water transport', 'This class includes: - transport of freight via rivers, canals, lakes and other inland waterways, including inside harbours and ports', 1, 399124, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399126, '51', 'Air transport', 'This division includes the transport of passengers or freight by air or via space.', 1, 399102, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399127, '51.1', 'Passenger air transport', '', 1, 399126, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399128, '51.1', 'Passenger air transport', 'This class includes: - transport of passengers by air over regular routes and on regular schedules - charter flights for passengers - scenic and sightseeing flights', 1, 399127, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399129, '51.2', 'Freight air transport and space transport', '', 1, 399126, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399130, '51.21', 'Freight air transport', 'This class includes: - transport freight by air over regular routes and on regular schedules - non-scheduled transport of freight by air', 1, 399129, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399131, '51.22', 'Space transport', 'This class includes: - launching of satellites and space vehicles - space transport of freight and passengers', 1, 399129, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399132, '52', 'Warehousing and support activities for transportation', 'This division includes warehousing and support activities for transportation, such as operating of transport infrastructure (e.g. airports, harbours, tunnels, bridges, etc.), the activities of transport agencies and cargo handling.', 1, 399102, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399133, '52.1', 'Warehousing and storage', '', 1, 399132, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399134, '52.1', 'Warehousing and storage', 'This class includes: - operation of storage and warehouse facilities for all kinds of goods: . operation of grain silos, general merchandise warehouses, refrigerated warehouses, storage tanks etc.', 1, 399133, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399135, '52.2', 'Support activities for transportation', 'This group includes activities supporting the transport of passengers or freight, such as operation of parts of the transport infrastructure or activities related to handling freight immediately before or after transport or between transport segments. The operation and maintenance of all transport facilities is included.', 1, 399132, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399136, '52.21', 'Service activities incidental to land transportation', 'This class includes: - activities related to land transport of passengers, animals or freight: . operation of terminal facilities such as railway stations, bus stations, stations for the handling of goods . operation of railroad infrastructure . operation of roads, bridges, tunnels, car parks or garages, bicycle parkings, winter storage of caravans - switching and shunting - towing and road side assistance', 1, 399135, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399137, '52.22', 'Service activities incidental to water transportation', 'This class includes: - activities related to water transport of passengers, animals or freight: . operation of terminal facilities such as harbours and piers . operation of waterway locks etc. . navigation, pilotage and berthing activities . lighterage, salvage activities . lighthouse activities', 1, 399135, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399138, '52.23', 'Service activities incidental to air transportation', 'This class includes: - activities related to air transport of passengers, animals or freight: . operation of terminal facilities such as airway terminals etc. . airport and air-traffic-control activities . ground service activities on airfields etc.', 1, 399135, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399139, '52.24', 'Cargo handling', 'This class includes: - loading and unloading of goods or passengers'' luggage irrespective of the mode of transport used for transportation - stevedoring - loading and unloading of freight railway cars', 1, 399135, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399140, '52.29', 'Other transportation support activities ', 'This class includes: - forwarding of freight - arranging or organising of transport operations by rail, road, sea or air - organisation of group and individual consignments (including pickup and delivery of goods and grouping of consignments) - issue and procurement of transport documents and waybills - activities of customs agents - activities of sea-freight forwarders and air-cargo agents - brokerage for ship and aircraft space - goods-handling operations, e.g. temporary crating for the sole purpose of protecting the goods during transit, uncrating, sampling, weighing of goods', 1, 399135, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399141, '53', 'Postal and courier activities', 'This division includes postal and courier activities, such as pickup, transport and delivery of letters and parcels under various arrangements.', 1, 399102, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399142, '53.1', 'Postal activities under universal service obligation', '', 1, 399141, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399143, '53.1', 'Postal activities under universal service obligation', 'This class includes the activities of postal services operating under a universal service obligation by one or more designated universal service providers. The activities include use of the universal service infrastructure, including retail locations, sorting and processing facilities, and carrier routes to pickup and deliver the mail. The delivery can include letter-post, i.e. letters, postcards, printed papers (newspaper, periodicals, advertising items, etc.), small packets, goods or documents. Also included are other services necessary to support the universal service obligation. This class includes: - pickup, sorting, transport and delivery (domestic or international) of letter-post and (mail-type) parcels and packages by postal services operating under a universal service obligation. One or more modes of transport may be involved and the activity may be carried out with either self-owned (private) transport or via public transport. - collection of letter-mail and parcels from public letter-boxes or from post offices', 1, 399142, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399144, '53.2', 'Other postal and courier activities', '', 1, 399141, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399145, '53.2', 'Other postal and courier activities', 'This class includes: - pickup, sorting, transport and delivery (domestic or international) of letter-post and (mail-type) parcels and packages by firms operating outside the scope of a universal service obligation. One or more modes of transport may be involved and the activity may be carried out with either self-owned (private) transport or via public transport.', 1, 399144, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399146, 'I', 'ACCOMMODATION AND FOOD SERVICE ACTIVITIES', 'This section includes the provision of short-stay accommodation for visitors and other travellers and the provision of complete meals and drinks fit for immediate consumption. The amount and type of supplementary services provided within this section can vary widely.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399147, '55', 'Accommodation', 'This division includes the provision of short-stay accommodation for visitors and other travellers.', 1, 399146, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399148, '55.1', 'Hotels and similar accommodation', '', 1, 399147, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399149, '55.1', 'Hotels and similar accommodation', 'This class includes the provision of accommodation, typically on a daily or weekly basis, principally for short stays by visitors. This includes the provision of furnished accommodation in guest rooms and suites. Services include daily cleaning and bed-making. A range of additional services may be provided such as food and beverage services, parking, laundry services, swimming pools and exercise rooms, recreational facilities as well as conference and convention facilities. This class includes accommodation provided by: - hotels - resort hotels - suite/apartment hotels - motels', 1, 399148, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399150, '55.2', 'Holiday and other short-stay accommodation', '', 1, 399147, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399151, '55.2', 'Holiday and other short-stay accommodation', 'This class includes the provision of accommodation, typically on a daily or weekly basis, principally for short stays by visitors, in self-contained space consisting of complete furnished rooms or areas for living/dining and sleeping, with cooking facilities or fully equipped kitchens. This may take the form of apartments or flats in small free-standing multi-storey buildings or clusters of buildings, or single storey bungalows, chalets, cottages and cabins. Very minimal complementary services, if any, are provided. This class includes accommodation provided by: - children''s and other holiday homes - visitor flats and bungalows - cottages and cabins without housekeeping services - youth hostels and mountain refuges', 1, 399150, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399152, '55.3', 'Camping grounds, recreational vehicle parks and trailer parks', '', 1, 399147, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399153, '55.3', 'Camping grounds, recreational vehicle parks and trailer parks', 'This class includes: - provision of accommodation in campgrounds, trailer parks, recreational camps and fishing and hunting camps for short stay visitors - provision of space and facilities for recreational vehicles', 1, 399152, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399154, '55.9', 'Other accommodation', '', 1, 399147, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399155, '55.9', 'Other accommodation', 'This class includes the provision temporary or longer-term accommodation in single or shared rooms or dormitories for students, migrant (seasonal) workers and other individuals. This class includes: - student residences - school dormitories - workers hostels - rooming and boarding houses - railway sleeping cars', 1, 399154, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399156, '56', 'Food and beverage service activities', 'This division includes food and beverage serving activities providing complete meals or drinks fit for immediate consumption, whether in traditional restaurants, self-service or take-away restaurants, whether as permanent or temporary stands with or without seating. Decisive is the fact that meals fit for immediate consumption are offered, not the kind of facility providing them.', 1, 399146, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399157, '56.1', 'Restaurants and mobile food service activities', '', 1, 399156, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399158, '56.1', 'Restaurants and mobile food service activities', 'This class includes the provision of food services to customers, whether they are served while seated or serve themselves from a display of items, whether they eat the prepared meals on the premises, take them out or have them delivered. This includes the preparation and serving of meals for immediate consumption from motorised vehicles or non-motorised carts. This class includes activities of: - restaurants - cafeterias - fast-food restaurants - take-out eating places - ice cream truck vendors - mobile food carts - food preparation in market stalls', 1, 399157, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399159, '56.2', 'Event catering and other food service activities', 'This group includes catering activities for individual events or for a specified period of time and the operation of food concessions, such as at sports or similar facilities.', 1, 399156, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399160, '56.21', 'Event catering activities', 'This class includes the provision of food services based on contractual arrangements with the customer, at the location specified by the customer, for a specific event.', 1, 399159, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399161, '56.29', 'Other food service activities', 'This class includes industrial catering, i.e. the provision of food services based on contractual arrangements with the customer, for a specific period of time. Also included is the operation of food concessions at sports and similar facilities. The food is usually prepared in a central unit. This class includes: - activities of food service contractors (e.g. for transportation companies) - operation of food concessions at sports and similar facilities - operation of canteens or cafeterias (e.g. for factories, offices, hospitals or schools) on a concession basis', 1, 399159, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399162, '56.3', 'Beverage serving activities', '', 1, 399156, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399163, '56.3', 'Beverage serving activities', 'This class includes preparation and serving of beverages for immediate consumption on the premises. This class includes activities of: - bars - taverns - cocktail lounges - discotheques (with beverage serving predominant) - beer parlours - coffee shops - fruit juice bars - mobile beverage vendors', 1, 399162, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399164, 'J', 'INFORMATION AND COMMUNICATION', 'This section includes the production and distribution of information and cultural products, the provision of the means to transmit or distribute these products, as well as data or communications, information technology activities and the processing of data and other information service activities. The main components of this section are publishing activities (division 58), including software publishing, motion picture and sound recording activities (division 59), radio and TV broadcasting and programming activities (division 60), telecommunications activities (division 61), information technology activities (division 62) and other information service activities (division 63). Publishing includes the acquisition of copyrights for content (information products) and making this content available to the general public by engaging in (or arranging for) the reproduction and distribution of this content in various forms. All the feasible forms of publishing (in print, electronic or audio form, on the Internet, as multimedia products such as CD-ROM reference books etc.) are included in this section. Activities related to production and distribution of TV programming span divisions 59, 60 and 61, reflecting different stages in this process. Individual components, such as movies, television series etc. are produced by activities in division 59, while the creation of a complete television channel programme, from components produced in division 59 or other components (such as live news programming) is included in division 60. Division 60 also includes the broadcasting of this programme by the producer. The distribution of the complete television programme by third parties, i.e. without any alteration of the content, is included in division 61. This distribution in division 61 can be done through broadcasting, satellite or cable systems.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399165, '58', 'Publishing activities', 'This division includes the publishing of books, brochures, leaflets, dictionaries, encyclopaedias, atlases, maps and charts; publishing of newspapers, journals and periodicals; directory and mailing list and other publishing, as well as software publishing. Publishing includes the acquisition of copyrights to content (information products) and making this content available to the general public by engaging in (or arranging for) the reproduction and distribution of this content in various forms. All the feasible forms of publishing (in print, electronic or audio form, on the Internet, as multimedia products such as CD-ROM reference books etc.), except publishing of motion pictures, are included in this division.', 1, 399164, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399166, '58.1', 'Publishing of books, periodicals and other publishing activities', 'This group includes activities of publishing books, newspapers, magazines and other periodicals, directories and mailing lists, and other works such as photos, engravings, postcards, timetables, forms, posters and reproductions of works of art. These works are characterised by the intellectual creativity required in their development and are usually protected by copyright.', 1, 399165, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399167, '58.11', 'Book publishing', 'This class includes the activities of publishing of books in print, electronic (CD, electronic displays etc.) or audio form or on the Internet. Included are: - publishing of books, brochures, leaflets and similar publications, including publishing of dictionaries and encyclopaedias - publishing of atlases, maps and charts - publishing of audio books - publishing of encyclopaedias etc. on CD-ROM', 1, 399166, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399168, '58.12', 'Publishing of directories and mailing lists', 'This class includes the publishing of lists of facts/information (databases), that are protected in their form, but not in their content. These lists can be published in printed or electronic form. This class includes: - publishing of mailing lists - publishing of telephone books - publishing of other directories and compilations, such as case law, pharmaceutical compendia etc.', 1, 399166, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399169, '58.13', 'Publishing of newspapers', 'This class includes the publishing of newspapers, including advertising newspapers, appearing at least four times a week. Publishing can be done in print or electronic form, including on the Internet.', 1, 399166, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399170, '58.14', 'Publishing of journals and periodicals', 'This class includes the publishing of periodicals and other journals, appearing less than four times a week. Publishing can be done in print or electronic form, including on the Internet. Publishing of radio and television schedules is included here.', 1, 399166, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399171, '58.19', 'Other publishing activities', 'This class includes: - publishing (including on-line) of: . catalogues . photos, engravings and postcards . greeting cards . forms . posters, reproduction of works of art . advertising material . other printed matter - on-line publishing of statistics and other information', 1, 399166, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399172, '58.2', 'Software publishing', '', 1, 399165, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399173, '58.21', 'Publishing of computer games', 'This class includes: - publishing of computer games for all platforms', 1, 399172, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399174, '58.29', 'Other software publishing', 'This class includes: - publishing of ready-made (non-customised) software, including translation or adaptation of non-customised software for a particular market on own account: . operating systems . business and other applications', 1, 399172, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399175, '59', 'Motion picture, video and television programme production, sound recording and music publishing activities', 'This division includes production of theatrical and non-theatrical motion pictures whether on film, video tape or disc for direct projection in theatres or for broadcasting on television; supporting activities such as film editing, cutting, dubbing etc.; distribution of motion pictures and other film productions to other industries; as well as motion picture or other film productions projection. Buying and selling of motion picture or other film productions distribution rights is also included.', 1, 399164, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399176, '59.1', 'Motion picture, video and television programme activities', 'This group includes production of theatrical and non-theatrical motion pictures whether on film, video tape, DVD or other media, including digital distribution, for direct projection in theatres or for broadcasting on television; supporting activities such as film editing, cutting, dubbing etc.; distribution of motion pictures or other film productions (video tapes, DVDs, etc) to other industries; as well as their projection.', 1, 399175, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399177, '59.11', 'Motion picture, video and television programme production activities', 'This class includes: - production of motion pictures, videos, television programmes (televisions series, documentaries etc.), or television advertisements', 1, 399176, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399178, '59.12', 'Motion picture, video and television programme post-production activities', 'This class includes post-production activities such as editing, film/tape transfers, titling, subtitling, credits, closed captioning, computer-produced graphics, animation and special effects, developing and processing motion picture film, as well as activities of motion picture film laboratories and activities of special laboratories for animated films.', 1, 399176, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399179, '59.13', 'Motion picture, video and television programme distribution activities', 'This class includes: - distributing film, video tapes, DVDs and similar productions to motion picture theatres, television networks and stations, and exhibitors', 1, 399176, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399180, '59.14', 'Motion picture projection activities', 'This class includes: - activities of motion picture or video tape projection in cinemas, in the open air or in other projection facilities - activities of cine-clubs', 1, 399176, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399181, '59.2', 'Sound recording and music publishing activities', '', 1, 399175, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399182, '59.2', 'Sound recording and music publishing activities', 'This class includes the activities of production of original (sound) master recordings, such as tapes, CDs; releasing, promoting and distributing sound recordings to wholesalers, retailers or directly to the public. These activities might be integrated or not with the production of master recordings in the same unit. If not, the unit exercising these activities has to obtain the reproduction and distribution rights to master recordings.', 1, 399181, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399183, '60', 'Programming and broadcasting activities', 'This division includes the activities of creating content or acquiring the right to distribute content and subsequently broadcasting that content, such as radio, television and data programs of entertainment, news, talk, and the like. Also included is data broadcasting, typically integrated with radio or TV broadcasting. The broadcasting can be performed using different technologies, over-the-air, via satellite, via a cable network or via Internet.', 1, 399164, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399184, '60.1', 'Radio broadcasting', '', 1, 399183, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399185, '60.1', 'Radio broadcasting', 'This class includes: - activities of broadcasting audio signals through radio broadcasting studios and facilities for the transmission of aural programming to the public, to affiliates or to subscribers', 1, 399184, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399186, '60.2', 'Television programming and broadcasting activities', '', 1, 399183, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399187, '60.2', 'Television programming and broadcasting activities', 'This class includes the creation of a complete television channel programme, from purchased programme components (e.g. movies, documentaries etc.), self produced programme components (e.g. local news, live reports) or a combination thereof. This complete television programme can be either broadcast by the producing unit or produced for transmission by a third party distributor, such as cable companies or satellite television providers. The programming may be of a general or specialised nature (e.g. limited formats such as news, sports, education or youth oriented programming). This class includes programming that is made freely available to users, as well as programming that is available only on a subscription basis. The programming of video-on-demand channels is also included here.', 1, 399186, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399188, '61', 'Telecommunications', 'This division includes the activities of providing telecommunications and related service activities, that is transmitting voice, data, text, sound and video. The transmission facilities that carry out these activities may be based on a single technology or a combination of technologies. The commonality of activities classified in this division is the transmission of content, without being involved in its creation. The breakdown in this division is based on the type of infrastructure operated. In the case of transmission of television signals this may include the bundling of complete programming channels (produced in division 60) in to programme packages for distribution.', 1, 399164, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399189, '61.1', 'Wired telecommunications activities', '', 1, 399188, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399190, '61.1', 'Wired telecommunications activities', 'This class includes: - operating, maintaining or providing access to facilities for the transmission of voice, data, text, sound and video using a wired telecommunications infrastructure, including: . operating and maintaining switching and transmission facilities to provide point-to-point communications via landlines, microwave or a combination of landlines and satellite linkups . operating of cable distribution systems (e.g. for distribution of data and television signals) . furnishing telegraph and other non-vocal communications using own facilities The transmission facilities that carry out these activities, may be based on a single technology or a combination of technologies.', 1, 399189, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399191, '61.2', 'Wireless telecommunications activities', '', 1, 399188, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399192, '61.2', 'Wireless telecommunications activities', 'This class includes: - operating, maintaining or providing access to facilities for the transmission of voice, data, text, sound, and video using a wireless telecommunications infrastructure - maintaining and operating paging as well as cellular and other wireless telecommunications networks The transmission facilities provide omni-directional transmission via airwaves and may be based on a single technology or a combination of technologies.', 1, 399191, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399193, '61.3', 'Satellite telecommunications activities', '', 1, 399188, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399194, '61.3', 'Satellite telecommunications activities', 'This class includes: - operating, maintaining or providing access to facilities for the transmission of voice, data, text, sound and video using a satellite telecommunications infrastructure - delivery of visual, aural or textual programming received from cable networks, local television stations or radio networks to consumers via direct-to-home satellite systems. (The units classified here do not generally originate programming material.)', 1, 399193, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399195, '61.9', 'Other telecommunications activities', '', 1, 399188, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399196, '61.9', 'Other telecommunications activities', 'This class includes: - provision of specialised telecommunications applications, such as satellite tracking, communications telemetry, and radar station operations - operation of satellite terminal stations and associated facilities operationally connected with one or more terrestrial communications systems and capable of transmitting telecommunications to or receiving telecommunications from satellite systems - provision of Internet access over networks between the client and the ISP not owned or controlled by the ISP, such as dial-up Internet access etc. - provision of telephone and Internet access in facilities open to the public - provision of telecommunications services over existing telecom connections: . VOIP (Voice Over Internet Protocol) provision - telecommunications resellers (i.e. purchasing and reselling network capacity without providing additional services)', 1, 399195, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399197, '62', 'Computer programming, consultancy and related activities', 'This division includes the following activities of providing expertise in the field of information technologies: writing, modifying, testing and supporting software; planning and designing computer systems that integrate computer hardware, software and communication technologies; on-site management and operation of clients'' computer systems and/or data processing facilities; and other professional and technical computer-related activities.', 1, 399164, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399198, '62', 'Computer programming, consultancy and related activities', '', 1, 399197, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399199, '62.01', 'Computer programming activities', 'This class includes the writing, modifying, testing and supporting of software. This class includes: - designing the structure and content of, and/or writing the computer code necessary to create and implement: . systems software (including updates and patches) . software applications (including updates and patches) . databases . web pages - customising of software, i.e. modifying and configuring an existing application so that it is functional within the clients'' information system environment', 1, 399197, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399200, '62.02', 'Computer consultancy activities', 'This class includes the planning and designing of computer systems which integrate computer hardware, software and communication technologies. Services may include related users training.', 1, 399197, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399201, '62.03', 'Computer facilities management activities', 'This class includes the provision of on-site management and operation of clients'' computer systems and/or data processing facilities, as well as related support services.', 1, 399197, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399202, '62.09', 'Other information technology and computer service activities', 'This class includes other information technology and computer related activities not elsewhere classified, such as: - computer disaster recovery services - installation (setting-up) of personal computers - software installation services', 1, 399197, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399203, '63', 'Information service activities', 'This division includes the activities of web search portals, data processing and hosting activities, as well as other activities that primarily supply information.', 1, 399164, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399204, '63.1', 'Data processing, hosting and related activities; web portals', 'This group includes the provision of infrastructure for hosting, data processing services and related activities, as well as the provision of search facilities and other portals for the Internet.', 1, 399203, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399205, '63.11', 'Data processing, hosting and related activities', 'This class includes: - provision of infrastructure for hosting, data processing services and related activities - specialized hosting activities such as: . Web hosting . streaming services . application hosting - application service provisioning - general time-share provision of mainframe facilities to clients - data processing activities: . complete processing of data supplied by clients . generation of specialized reports from data supplied by clients - provision of data entry services', 1, 399204, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399206, '63.12', 'Web portals', 'This class includes: - operation of web sites that use a search engine to generate and maintain extensive databases of Internet addresses and content in an easily searchable format - operation of other websites that act as portals to the Internet, such as media sites providing periodically updated content', 1, 399204, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399207, '63.9', 'Other information service activities', 'This group includes the activities of news agencies and all other remaining information service activities.', 1, 399203, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399208, '63.91', 'News agency activities', 'This class includes: - news syndicate and news agency activities furnishing news, pictures and features to the media', 1, 399207, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399209, '63.99', 'Other information service activities n.e.c.', 'This class includes other information service activities not elsewhere classified such as: - computer-based telephone information services - information search services on a contract or fee basis - news clipping services, press clipping services, etc.', 1, 399207, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399210, 'K', 'FINANCIAL AND INSURANCE ACTIVITIES', 'This section includes financial service activities, including insurance, reinsurance and pension funding activities and activities to support financial services.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399211, '64', 'Financial service activities, except insurance and pension funding', 'This division includes the activities of obtaining and redistributing funds other than for the purpose of insurance or pension funding or compulsory social security. Note: National institutional arrangements are likely to play a significant role in determining the classification within this division.', 1, 399210, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399212, '64.1', 'Monetary intermediation', 'This group includes the obtaining of funds in the form of transferable deposits, i.e. funds that are fixed in money terms, obtained on a day-to-day basis and, apart from central banking, obtained from non-financial sources.', 1, 399211, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399213, '64.11', 'Central banking', 'This class includes: - issuing and managing the country''s currency - monitoring and control of the money supply - taking deposits that are used for clearance between financial institutions - supervising banking operations - holding the country''s international reserves - acting as banker to the government The activities of central banks will vary for institutional reasons.', 1, 399212, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399214, '64.19', 'Other monetary intermediation', 'This class includes the receiving of deposits and/or close substitutes for deposits and extending of credit or lending funds. The granting of credit can take a variety of forms, such as loans, mortgages, credit cards etc. These activities are generally carried out by monetary institutions other than central banks, such as: - banks - savings banks - credit unions', 1, 399212, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399215, '64.2', 'Activities of holding companies', '', 1, 399211, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399216, '64.2', 'Activities of holding companies', 'This class includes the activities of holding companies, i.e. units that hold the assets (owning controlling-levels of equity) of a group of subsidiary corporations and whose principal activity is owning the group. The holding companies in this class do not provide any other service to the businesses in which the equity is held, i.e. they do not administer or manage other units.', 1, 399215, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399217, '64.3', 'Trusts, funds and similar financial entities', '', 1, 399211, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399218, '64.3', 'Trusts, funds and similar financial entities', 'This class includes legal entities organised to pool securities or other financial assets, without managing, on behalf of shareholders or beneficiaries. The portfolios are customised to achieve specific investment characteristics, such as diversification, risk, rate of return and price volatility. These entities earn interest, dividends and other property income, but have little or no employment and no revenue from the sale of services. This class includes: - open-end investment funds - closed-end investment funds - trusts, estates or agency accounts, administered on behalf of the beneficiaries under the terms of a trust agreement, will or agency agreement - unit investment trust funds', 1, 399217, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399219, '64.9', 'Other financial service activities, except insurance and pension funding', 'This group includes financial service activities other than those conducted by monetary institutions.', 1, 399211, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399220, '64.91', 'Financial leasing', 'This class includes: - leasing where the term approximately covers the expected life of the asset and the lessee acquires substantially all the benefits of its use and takes all the risks associated with its ownership. The ownership of the asset may or may not eventually be transferred. Such leases cover all or virtually all costs including interest.', 1, 399219, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399221, '64.92', 'Other credit granting', 'This class includes: - financial service activities primarily concerned with making loans by institutions not involved in monetary intermediation, where the granting of credit can take a variety of forms, such as loans, mortgages, credit cards etc., providing the following types of services: . granting of consumer credit . international trade financing . provision of long-term finance to industry by industrial banks . money lending outside the banking system . credit granting for house purchase by specialised non-depository institutions . pawnshops and pawnbrokers', 1, 399219, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399222, '64.99', 'Other financial service activities, except insurance and pension funding n.e.c.', 'This class includes: - other financial service activities primarily concerned with distributing funds other than by making loans: . factoring activities . writing of swaps, options and other hedging arrangements . activities of viatical settlement companies - own-account investment activities, such as by venture capital companies, investment clubs etc.', 1, 399219, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399223, '65', 'Insurance, reinsurance and pension funding, except compulsory social security', 'This division includes the underwriting annuities and insurance policies and investing premiums to build up a portfolio of financial assets to be used against future claims. Provision of direct insurance and reinsurance are included.', 1, 399210, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399224, '65.1', 'Insurance', 'This group includes life insurance with or without a substantial savings element and non-life insurance.', 1, 399223, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399225, '65.11', 'Life insurance', 'This class includes: - underwriting annuities and life insurance policies, disability income insurance policies, and accidental death and dismemberment insurance policies (with or without a substantial savings element)', 1, 399224, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399226, '65.12', 'Non-life insurance', 'This class includes: - provision of insurance services other than life insurance: . accident and fire insurance . health insurance . travel insurance . property insurance . motor, marine, aviation and transport insurance . pecuniary loss and liability insurance', 1, 399224, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399227, '65.2', 'Reinsurance', '', 1, 399223, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399228, '65.2', 'Reinsurance', 'This class includes: - activities of assuming all or part of the risk associated with existing insurance policies originally underwritten by other insurance carriers', 1, 399227, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399229, '65.3', 'Pension funding', '', 1, 399223, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399230, '65.3', 'Pension funding', 'This class includes legal entities (i.e. funds, plans and/or programmes) organised to provide retirement income benefits exclusively for the sponsor''s employees or members. This includes pension plans with defined benefits, as well as individual plans where benefits are simply defined through the member’s contribution. This class includes: - employee benefit plans - pension funds and plans - retirement plans', 1, 399229, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399231, '66', 'Activities auxiliary to financial services and insurance activities', 'This division includes the provision of services involved in or closely related to financial service activities, but not themselves providing financial services. The primary breakdown of this division is according to the type of financial transaction or funding served.', 1, 399210, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399232, '66.1', 'Activities auxiliary to financial services, except insurance and pension funding', 'This group includes the furnishing of physical or electronic marketplaces for the purpose of facilitating the buying and selling of stocks, stock options, bonds or commodity contracts.', 1, 399231, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399233, '66.11', 'Administration of financial markets', 'This class includes the operation and supervision of financial markets other than by public authorities, such as: - commodity contracts exchanges - futures commodity contracts exchanges - securities exchanges - stock exchanges - stock or commodity options exchanges', 1, 399232, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399234, '66.12', 'Security and commodity contracts brokerage', 'This class includes: - dealing in financial markets on behalf of others (e.g. stock broking) and related activities - securities brokerage - commodity contracts brokerage - activities of bureaux de change etc.', 1, 399232, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399235, '66.19', 'Other activities auxiliary to financial services, except insurance and pension funding', 'This class includes activities auxiliary to financial service activities not elsewhere classified, such as: - financial transaction processing and settlement activities, including for credit card transactions - investment advisory services - activities of mortgage advisers and brokers', 1, 399232, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399236, '66.2', 'Activities auxiliary to insurance and pension funding', 'This group includes acting as agents (i.e. brokers) in selling annuities and insurance policies or provide other employee benefits and insurance and pension related services such as claims adjustment and third party administration.', 1, 399231, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399237, '66.21', 'Risk and damage evaluation', 'This class includes the provision of administration services of insurance, such as assessing and settling insurance claims, such as: - assessing insurance claims . claims adjusting . risk assessing . risk and damage evaluation . average and loss adjusting - settling insurance claims', 1, 399236, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399238, '66.22', 'Activities of insurance agents and brokers', 'This class includes: - activities of insurance agents and brokers (insurance intermediaries) in selling, negotiating or soliciting of annuities and insurance and reinsurance policies', 1, 399236, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399239, '66.29', 'Other activities auxiliary to insurance and pension funding', 'This class includes: - activities involved in or closely related to insurance and pension funding (except financial intermediation, claims adjusting and activities of insurance agents): . salvage administration . actuarial services', 1, 399236, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399240, '66.3', 'Fund management activities', '', 1, 399231, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399241, '66.3', 'Fund management activities', 'This class includes portfolio and fund management activities on a fee or contract basis, for individuals, businesses and others, such as: - management of mutual funds - management of other investment funds - management of pension funds', 1, 399240, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399242, 'L', 'REAL ESTATE ACTIVITIES', 'This section includes acting as lessors, agents and/or brokers in one or more of the following: selling or buying real estate, rental real estate, providing other real estate services such as appraising real estate or acting as real estate escrow agents. Activities in this section may be carried out on own or leased property and may be done on a fee or contract basis. This section includes real estate property managers.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399243, '68', 'Real estate activities', '', 1, 399242, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399244, '68.1', 'Buying and selling of own real estate', '', 1, 399243, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399245, '68.1', 'Buying and selling of own real estate', 'This class includes: - buying and selling of self-owned real estate: . apartment buildings and dwellings . non-residential buildings, including exhibition halls, self-storage facilities, malls and shopping centres . land', 1, 399244, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399246, '68.2', 'Rental and operating of own or leased real estate', '', 1, 399243, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399247, '68.2', 'Rental and operating of own or leased real estate', 'This class includes: - rental and operating of self-owned or leased real estate: . apartment buildings and dwellings . non-residential buildings, including exhibition halls, self-storage facilities . land - provision of homes and furnished or unfurnished flats or apartments for more permanent use, typically on a monthly or annual basis', 1, 399246, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399248, '68.3', 'Real estate activities on a fee or contract basis', '', 1, 399243, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399249, '68.31', 'Real estate agencies', 'This class includes the provision of real estate activities by real estate agencies: - intermediation in buying, selling and rental of real estate on a fee or contract basis - advisory activities and appraisal services in connection to buying, selling and rental of real estate, on a fee or contract basis - real estate escrow agents activities', 1, 399248, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399250, '68.32', 'Management of real estate on a fee or contract basis', '', 1, 399248, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399251, 'M', 'PROFESSIONAL, SCIENTIFIC AND TECHNICAL ACTIVITIES', 'This section includes specialised professional, scientific and technical activities. These activities require a high degree of training, and make specialised knowledge and skills available to users.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399252, '69', 'Legal and accounting activities', 'This division includes legal representation of one party''s interest against another party, whether or not before courts or other judicial bodies by, or under supervision of, persons who are members of the bar, such as advice and representation in civil cases, advice and representation in criminal actions, advice and representation in connection with labour disputes.', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399253, '69.1', 'Legal activities', '', 1, 399252, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399254, '69.1', 'Legal activities', 'This class includes: - legal representation of one party''s interest against another party, whether or not before courts or other judicial bodies by, or under supervision of, persons who are members of the bar: . advice and representation in civil cases . advice and representation in criminal cases . advice and representation in connection with labour disputes - general counselling and advising, preparation of legal documents: . articles of incorporation, partnership agreements or similar documents in connection with company formation . patents and copyrights . preparation of deeds, wills, trusts etc. - other activities of notaries public, civil law notaries, bailiffs, arbitrators, examiners and referees', 1, 399253, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399255, '69.2', 'Accounting, bookkeeping and auditing activities; tax consultancy', '', 1, 399252, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399256, '69.2', 'Accounting, bookkeeping and auditing activities; tax consultancy', 'This class includes: - recording of commercial transactions from businesses or others - preparation or auditing of financial accounts - examination of accounts and certification of their accuracy - preparation of personal and business income tax returns - advisory activities and representation on behalf of clients before tax authorities', 1, 399255, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399257, '70', 'Activities of head offices; management consultancy activities', 'This division includes the provision of advice and assistance to businesses and other organisations on management issues, such as strategic and organisational planning; financial planning and budgeting; marketing objectives and policies; human resource policies, practices, and planning; production scheduling; and control planning.', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399258, '70.1', 'Activities of head offices', '', 1, 399257, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399259, '70.1', 'Activities of head offices', 'This class includes the overseeing and managing of other units of the company or enterprise; undertaking the strategic or organisational planning and decision making role of the company or enterprise; exercising operational control and managing the day-to-day operations of their related units. This class includes activities of: - head offices - centralised administrative offices - corporate offices - district and regional offices - subsidiary management offices', 1, 399258, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399260, '70.2', 'Management consultancy activities', '', 1, 399257, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399261, '70.21', 'Public relations and communication activities', 'This class includes the provision of advice, guidance and operational assistance, including lobbying activities, to businesses and other organisations on public relations and communication.', 1, 399260, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399262, '70.22', 'Business and other management consultancy activities', 'This class includes the provision of advice, guidance and operational assistance to businesses and other organisations on management issues, such as corporate strategic and organisational planning, business process reengineering, change management, cost reduction and other financial issues; marketing objectives and policies; human resource policies, practices and planning; compensation and retirement strategies; production scheduling and control planning. This provision of business services may include advice, guidance or operational assistance to businesses and the public service regarding: - design of accounting methods or procedures, cost accounting programmes, budgetary control procedures - advice and help to businesses and public services in planning, organisation, efficiency and control, management information etc.', 1, 399260, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399263, '71', 'Architectural and engineering activities; technical testing and analysis', 'This division includes the provision of architectural services, engineering services, drafting services, building inspection services and surveying and mapping services.', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399264, '71.1', 'Architectural and engineering activities and related technical consultancy', 'This group includes the provision of architectural services, engineering services, drafting services, building inspection services and surveying and mapping services and the like.', 1, 399263, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399265, '71.11', 'Architectural activities ', 'This class includes: - architectural consulting activities: . building design and drafting . town and city planning and landscape architecture', 1, 399264, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399266, '71.12', 'Engineering activities and related technical consultancy', 'This class includes: - engineering design (i.e. applying physical laws and principles of engineering in the design of machines, materials, instruments, structures, processes and systems) and consulting activities for: . machinery, industrial processes and industrial plant . projects involving civil engineering, hydraulic engineering, traffic engineering . water management projects . projects elaboration and realisation relative to electrical and electronic engineering, mining engineering, chemical engineering, mechanical, industrial and systems engineering, safety engineering - elaboration of projects using air conditioning, refrigeration, sanitary and pollution control engineering, acoustical engineering etc. - geophysical, geologic and seismic surveying - geodetic surveying activities: . land and boundary surveying activities . hydrologic surveying activities . subsurface surveying activities . cartographic and spatial information activities', 1, 399264, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399267, '71.2', 'Technical testing and analysis', '', 1, 399263, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399268, '71.2', 'Technical testing and analysis', 'This class includes the performance of physical, chemical and other analytical testing of all types of materials and products, such as: . acoustics and vibration testing . testing of composition and purity of minerals etc. . testing activities in the field of food hygiene, including veterinary testing and control in relation to food production . testing of physical characteristics and performance of materials, such as strength, thickness, durability, radioactivity etc. . qualification and reliability testing . performance testing of complete machinery: motors, automobiles, electronic equipment etc. . radiographic testing of welds and joints . failure analysis . testing and measuring of environmental indicators: air and water pollution etc. - certification of products, including consumer goods, motor vehicles, aircraft, pressurised containers, nuclear plants etc. - periodic road-safety testing of motor vehicles - testing with use of models or mock-ups (e.g. of aircraft, ships, dams etc.) - operation of police laboratories', 1, 399267, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399269, '72', 'Scientific research and development ', 'This division includes the activities of three types of research and development: 1) basic research: experimental or theoretical work undertaken primarily to acquire new knowledge of the underlying foundations of phenomena and observable facts, without particular application or use in view, 2) applied research: original investigation undertaken in order to acquire new knowledge, directed primarily towards a specific practical aim or objective and 3) experimental development: systematic work, drawing on existing knowledge gained from research and/or practical experience, directed to producing new materials, products and devices, to installing new processes, systems and services, and to improving substantially those already produced or installed. Research and experimental development activities in this division are subdivided into two categories: natural sciences and engineering; social sciences and the humanities.', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399270, '72.1', 'Research and experimental development on natural sciences and engineering', 'This group comprises basic research, applied research, experimental development in natural sciences and engineering.', 1, 399269, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399271, '72.11', 'Research and experimental development on biotechnology', 'This class includes research and experimental development on biotechnology: - DNA/RNA: genomics, pharmacogenomics, gene probes, genetic engineering, DNA/RNA sequencing/synthesis/amplification, gene expression profiling, and use of antisense technology - proteins and other molecules: sequencing/synthesis/engineering of proteins and peptides (including large molecule hormones); improved delivery methods for large molecule drugs; proteomics, protein isolation and purification, signalling, identification of cell receptors - cell and tissue culture and engineering: cell/tissue culture, tissue engineering (including tissue scaffolds and biomedical engineering), cellular fusion, vaccine/immune stimulants, embryo manipulation - process biotechnology techniques: fermentation using bioreactors, bioprocessing, bioleaching, biopulping, biobleaching, biodesulphurisation, bioremediation, biofiltration and phytoremediation - gene and RNA vectors: gene therapy, viral vectors - bioinformatics: construction of databases on genomes, protein sequences; modelling complex biological processes, including systems biology - nanobiotechnology: applies the tools and processes of nano/microfabrication to build devices for studying biosystems and applications in drug delivery, diagnostics etc.', 1, 399270, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399272, '72.19', 'Other research and experimental development on natural sciences and engineering', 'This class includes: - research and experimental development on natural science and engineering other than biotechnological research and experimental development: . research and development on natural sciences . research and development on engineering and technology . research and development on medical sciences . research and development on agricultural sciences . interdisciplinary research and development, predominantly on natural sciences and engineering', 1, 399270, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399273, '72.2', 'Research and experimental development on social sciences and humanities', '', 1, 399269, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399274, '72.2', 'Research and experimental development on social sciences and humanities', 'This class includes: - research and development on social sciences - research and development on humanities - interdisciplinary research and development, predominantly on social sciences and humanities', 1, 399273, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399275, '73', 'Advertising and market research', 'This division includes the creation of advertising campaigns and placement of such advertising in periodicals, newspapers, radio and television, or other media as well as the design of display structures and sites.', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399276, '73.1', 'Advertising', '', 1, 399275, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399277, '73.11', 'Advertising agencies', 'This class includes the provision of a full range of advertising services (i.e., through in-house capabilities or subcontracting), including advice, creative services, production of advertising material, and buying. It includes: - creation and realisation of advertising campaigns: . creating and placing advertising in newspapers, periodicals, radio, television, the Internet and other media . creating and placing of outdoor advertising, e.g. billboards, panels, bulletins and frames, window dressing, showroom design, car and bus carding etc. . aerial advertising . distribution or delivery of advertising material or samples . creation of stands and other display structures and sites - conducting marketing campaigns and other advertising services aimed at attracting and retaining customers . promotion of products . point-of-sale marketing . direct mail advertising . marketing consulting', 1, 399276, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399278, '73.12', 'Media representation', 'This class includes - media representation, i.e. sale or re-sale of time and space for various media soliciting advertising', 1, 399276, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399279, '73.2', 'Market research and public opinion polling', '', 1, 399275, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399280, '73.2', 'Market research and public opinion polling', 'This class includes: - investigation into market potential, awareness, acceptance and familiarity of goods and services and buying habits of consumers for the purpose of sales promotion and development of new goods and services, including statistical analyses of the results - investigation into collective opinions of the public about political, economic and social issues and statistical analysis thereof', 1, 399279, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399281, '74', 'Other professional, scientific and technical activities', 'This division includes the provision of professional scientific and technical services (except legal and accounting activities; architecture and engineering activities; technical testing and analysis; management and management consultancy activities; research and development and advertising activities).', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399282, '74.1', 'Specialised design activities', '', 1, 399281, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399283, '74.1', 'Specialised design activities', 'This class includes: - fashion design related to textiles, wearing apparel, shoes, jewellery, furniture and other interior decoration and other fashion goods as well as other personal or household goods - industrial design, i.e. creating and developing designs and specifications that optimise the use, value and appearance of products, including the determination of the materials, mechanism, shape, colour and surface finishes of the product, taking into consideration human characteristics and needs, safety, market appeal in distribution, use and maintenance - activities of graphic designers - activities of interior decorators', 1, 399282, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399284, '74.2', 'Photographic activities', '', 1, 399281, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399285, '74.2', 'Photographic activities', 'This class includes: - commercial and consumer photograph production: . portrait photography for passports, schools, weddings etc. . photography for commercials, publishers, fashion, real estate or tourism purposes . aerial photography . videotaping of events: weddings, meetings etc. - film processing: . developing, printing and enlarging from client-taken negatives or cine-films . film developing and photo printing laboratories . one hour photo shops (not part of camera stores) . mounting of slides . copying and restoring or transparency retouching in connection with photographs - activities of photojournalists', 1, 399284, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399286, '74.3', 'Translation and interpretation activities', '', 1, 399281, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399287, '74.3', 'Translation and interpretation activities', '', 1, 399286, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399288, '74.9', 'Other professional, scientific and technical activities n.e.c.', '', 1, 399281, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399289, '74.9', 'Other professional, scientific and technical activities n.e.c.', 'This class includes a great variety of service activities generally delivered to commercial clients. It includes those activities for which more advanced professional, scientific and technical skill levels are required, but does not include ongoing, routine business functions that are generally of short duration. This class includes: - business brokerage activities, i.e. arranging for the purchase and sale of small and medium-sized businesses, including professional practices, but not including real estate brokerage - patent brokerage activities (arranging for the purchase and sale of patents) - appraisal activities other than for real estate and insurance (for antiques, jewellery, etc.) - bill auditing and freight rate information - weather forecasting activities - security consulting - agronomy consulting - environmental consulting - other technical consulting - activities of consultants other than architecture, engineering and management consultants - activities of quantity surveyors', 1, 399288, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399290, '75', 'Veterinary activities', 'This division includes the provision of animal health care and control activities for farm animals or pet animals. These activities are carried out by qualified veterinarians in veterinary hospitals as well as when visiting farms, kennels or homes, in own consulting and surgery rooms or elsewhere.', 1, 399251, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399291, '75', 'Veterinary activities', '', 1, 399290, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399292, '75', 'Veterinary activities', 'This class includes: - animal health care and control activities for farm animals - animal health care and control activities for pet animals These activities are carried out by qualified veterinarians when working in veterinary hospitals as well as when visiting farms, kennels or homes, in own consulting and surgery rooms or elsewhere.', 1, 399290, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399293, 'N', 'ADMINISTRATIVE AND SUPPORT SERVICE ACTIVITIES', 'This section includes a variety of activities that support general business operations. These activities differ from those in section M, since their primary purpose is not the transfer of specialised knowledge.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399294, '77', 'Rental and leasing activities', 'This division includes the rental and leasing of tangible and non-financial intangible assets, including a wide array of tangible goods, such as automobiles, computers, consumer goods, and industrial machinery and equipment, to customers in return for a periodic rental or lease payment. It is subdivided into: (1) the rental of motor vehicles, (2) the rental of recreational and sports equipment and personal and household equipment, (3) the leasing of other machinery and equipment of the kind often used for business operations, including other transport equipment and (4) the leasing of intellectual property products and similar products. Only the provision of operating leases is included in this division.', 1, 399293, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399295, '77.1', 'Rental and leasing of motor vehicles', '', 1, 399294, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399296, '77.11', 'Rental and leasing of cars and light motor vehicles', 'This class includes: - rental and operational leasing of the following types of vehicles: . passenger cars and other light motor vehicles (with a weight not exceeding 3,5 tons) without driver', 1, 399295, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399297, '77.12', 'Rental and leasing of trucks', 'This class includes: - rental and operational leasing of the following types of vehicles: . trucks, utility trailers and heavy motor vehicles (with a weight exceeding 3,5 tons) . recreational vehicles', 1, 399295, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399298, '77.2', 'Rental and leasing of personal and household goods', 'This group includes the rental of personal and household goods as well as rental of recreational and sports equipment and video tapes. Activities generally include short-term rental of goods although in some instances, the goods may be leased for longer periods of time.', 1, 399294, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399299, '77.21', 'Rental and leasing of recreational and sports goods', 'This class includes rental of recreational and sports equipment: - pleasure boats, canoes, sailboats - bicycles - beach chairs and umbrellas - other sports equipment - skis', 1, 399298, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399300, '77.22', 'Rental of video tapes and disks', 'This class includes: - rental of video tapes, records, CDs, DVDs etc.', 1, 399298, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399301, '77.29', 'Rental and leasing of other personal and household goods', 'This class includes: - rental of all kinds of household or personal goods, to households or industries (except recreational and sports equipment): . textiles, wearing apparel and footwear . furniture, pottery and glass, kitchen and tableware, electrical appliances and house wares . jewellery, musical instruments, scenery and costumes . books, journals and magazines . machinery and equipment used by amateurs or as a hobby e.g. tools for home repairs . flowers and plants . electronic equipment for household use', 1, 399298, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399302, '77.3', 'Rental and leasing of other machinery, equipment and tangible goods', '', 1, 399294, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399303, '77.31', 'Rental and leasing of agricultural machinery and equipment', 'This class includes: - rental and operational leasing of agricultural and forestry machinery and equipment without operator: . rental of products produced by class 28.30, such as agricultural tractors etc.', 1, 399302, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399304, '77.32', 'Rental and leasing of construction and civil engineering machinery and equipment', 'This class includes: - rental and operational leasing of construction and civil engineering machinery and equipment without operator: . crane lorries . scaffolds and work platforms, without erection and dismantling', 1, 399302, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399305, '77.33', 'Rental and leasing of office machinery and equipment (including computers)', 'This class includes: - rental and operational leasing of office machinery and equipment without operator: . computers and computer peripheral equipment . duplicating machines, typewriters and word-processing machines . accounting machinery and equipment: cash registers, electronic calculators etc. . office furniture', 1, 399302, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399306, '77.34', 'Rental and leasing of water transport equipment', 'This class includes: - rental and operational leasing of water-transport equipment without operator: . commercial boats and ships', 1, 399302, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399307, '77.35', 'Rental and leasing of air transport equipment', 'This class includes: - rental and operational leasing of air transport equipment without operator: . airplanes . hot-air balloons', 1, 399302, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399308, '77.39', 'Rental and leasing of other machinery, equipment and tangible goods n.e.c.', 'This class includes: - rental and operational leasing, without operator, of other machinery and equipment that are generally used as capital goods by industries: . engines and turbines . machine tools . mining and oilfield equipment . professional radio, television and communication equipment . motion picture production equipment . measuring and controlling equipment . other scientific, commercial and industrial machinery - rental and operational leasing of land-transport equipment (other than motor vehicles) without drivers: . motorcycles, caravans and campers etc. . railroad vehicles', 1, 399302, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399309, '77.4', 'Leasing of intellectual property and similar products, except copyrighted works', '', 1, 399294, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399310, '77.4', 'Leasing of intellectual property and similar products, except copyrighted works', 'This class includes the activities of allowing others to use intellectual property products and similar products for which a royalty payment or licensing fee is paid to the owner of the product (i.e. the asset holder). The leasing of these products can take various forms, such as permission for reproduction, use in subsequent processes or products, operating businesses under a franchise etc. The current owners may or may not have created these products. This class includes: - leasing of intellectual property products (except copyrighted works, such as books or software) - receiving royalties or licensing fees for the use of: . patented entities . trademarks or service marks . brand names . mineral exploration and evaluation . franchise agreements', 1, 399309, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399311, '78', 'Employment activities', 'This division includes activities of listing employment vacancies and referring or placing applicants for employment, where the individuals referred or placed are not employees of the employment agencies, supplying workers to clients'' businesses for limited periods of time to supplement the working force of the client, and the activities of providing other human resources. This division includes: - executive search and placement activities - activities of theatrical casting agencies', 1, 399293, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399312, '78.1', 'Activities of employment placement agencies', '', 1, 399311, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399313, '78.1', 'Activities of employment placement agencies', 'This class includes listing employment vacancies and referring or placing applicants for employment, where the individuals referred or placed are not employees of the employment agencies. This class includes: - personnel search, selection referral and placement activities, including executive placement and search activities - activities of casting agencies and bureaus, such as theatrical casting agencies - activities of on-line employment placement agencies', 1, 399312, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399314, '78.2', 'Temporary employment agency activities', '', 1, 399311, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399315, '78.2', 'Temporary employment agency activities', 'This class includes the activities of supplying workers to clients'' businesses for limited periods of time to temporarily replace or supplement the working force of the client, where the individuals provided are employees of the temporary help service unit. However, units classified here do not provide direct supervision of their employees at the clients'' work sites.', 1, 399314, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399316, '78.3', 'Other human resources provision', '', 1, 399311, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399317, '78.3', 'Other human resources provision', 'This class includes the activities of providing human resources for client businesses. The units classified here represent the employer of record for the employees on matters relating to payroll, taxes, and other fiscal and human resource issues, but they are not responsible for direction and supervision of employees. The provision of human resources is typically done on a long-term or permanent basis and the units classified here perform a wide range of human resource and personnel management duties associated with this provision.', 1, 399316, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399318, '79', 'Travel agency, tour operator and other reservation service and related activities', 'This division includes the activity of agencies, primarily engaged in selling travel, tour, transportation and accommodation services to the general public and commercial clients and the activity of arranging and assembling tours that are sold through travel agencies or directly by agents such as tour operators; and other travel-related services including reservation services.', 1, 399293, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399319, '79.1', 'Travel agency and tour operator activities', 'This group includes the activities of agencies, primarily engaged in selling travel, tour, transportation and accommodation services to the general public and commercial clients and the activity of arranging and assembling tours that are sold through travel agencies or directly by agents such as tour operators.', 1, 399318, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399320, '79.11', 'Travel agency activities', 'This class includes: - activities of agencies, primarily engaged in selling travel, tour, transportation and accommodation services on a wholesale or retail basis to the general public and commercial clients', 1, 399319, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399321, '79.12', 'Tour operator activities', 'This class includes: - arranging and assembling tours that are sold through travel agencies or directly by tour operators. The tours may include any or all of the following: . transportation . accommodation . food . visits to museums, historical or cultural sites, theatrical, musical or sporting events', 1, 399319, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399322, '79.9', 'Other reservation service and related activities', '', 1, 399318, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399323, '79.9', 'Other reservation service and related activities', 'This class includes: - other travel-related reservation services: . reservations for transportation, hotels, restaurants, car rentals, entertainment and sport etc. - time-share exchange services - ticket sales activities for theatrical, sports and other amusement and entertainment events - visitor assistance services: . provision of travel information to visitors . activities of tourist guides - tourism promotion activities', 1, 399322, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399324, '80', 'Security and investigation activities', 'This division includes security-related services such as: investigation and detective services; guard and patrol services; picking up and delivering money, receipts, or other valuable items with personnel and equipment to protect such properties while in transit; operation of electronic security alarm systems, such as burglar and fire alarms, where the activity focuses on remote monitoring these systems, but often involves also sale, installation and repair services. If the latter components are provided separate, they are excluded from this division and classified in retail sale, construction etc.', 1, 399293, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399325, '80.1', 'Private security activities', '', 1, 399324, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399326, '80.1', 'Private security activities', 'This class includes the provision of one or more of the following: guard and patrol services, picking up and delivering money, receipts, or other valuable items with personnel and equipment to protect such properties while in transit. This class includes: - armoured car services - bodyguard services - polygraph services - fingerprinting services - security guard services - security shredding of information on any media', 1, 399325, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399327, '80.2', 'Security systems service activities', '', 1, 399324, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399328, '80.2', 'Security systems service activities', 'This class includes: - monitoring or remote monitoring of electronic security alarm systems, such as burglar and fire alarms, including their installation and maintenance - installing, repairing, rebuilding, and adjusting mechanical or electronic locking devices, safes and security vaults in connection with later monitoring and remote monitoring The units carrying out these activities may also engage in selling such security systems, mechanical or electronic locking devices, safes and security vaults.', 1, 399327, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399329, '80.3', 'Investigation activities', '', 1, 399324, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399330, '80.3', 'Investigation activities', 'This class includes: - investigation and detective service activities - activities of all private investigators, independent of the type of client or purpose of investigation', 1, 399329, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399331, '81', 'Services to buildings and landscape activities', 'This division includes the provision of a number of general support services, such as the provision of a combination of support services within a client''s facilities, the interior and exterior cleaning of buildings of all types, cleaning of industrial machinery, cleaning of trains, buses, planes, etc., cleaning of the inside of road and sea tankers, disinfecting and exterminating activities for buildings, ships, trains, etc., bottle cleaning, street sweeping, snow and ice removal, provision of landscape care and maintenance services and provision of these services along with the design of landscape plans and/or the construction (i.e. installation) of walkways, retaining walls, decks, fences, ponds, and similar structures.', 1, 399293, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399332, '81.1', 'Combined facilities support activities', '', 1, 399331, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399333, '81.1', 'Combined facilities support activities', 'This class includes the provision of a combination of support services within a client''s facilities. These services include general interior cleaning, maintenance, trash disposal, guard and security, mail routing, reception, laundry and related services to support operations within facilities. These support activities are performed by operating staff, which is not involved with or responsible for the core business or activities of the client.', 1, 399332, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399334, '81.2', 'Cleaning activities', 'This group includes the activities of general interior cleaning of all types of buildings, exterior cleaning of buildings, specialised cleaning activities for buildings or other specialised cleaning activities, cleaning of industrial machinery, cleaning of the inside of road and sea tankers, disinfecting and extermination activities for buildings and industrial machinery, bottle cleaning, street sweeping, snow and ice removal.', 1, 399331, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399335, '81.21', 'General cleaning of buildings', 'This class includes: - general (non-specialized) cleaning of all types of buildings, such as: . offices . houses or apartments . factories . shops . institutions - general (non-specialized) cleaning of other business and professional premises and multiunit residential buildings These activities are mostly interior cleaning although they may include the cleaning of associated exterior areas such as windows or passageways.', 1, 399334, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399336, '81.22', 'Other building and industrial cleaning activities', 'This class includes: - exterior cleaning of buildings of all types, including offices, factories, shops, institutions and other business and professional premises and multiunit residential buildings - specialised cleaning activities for buildings such as window cleaning, chimney cleaning and cleaning of fireplaces, stoves, furnaces, incinerators, boilers, ventilation ducts and exhaust units - cleaning of industrial machinery - other building and industrial cleaning activities, n.e.c.', 1, 399334, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399337, '81.29', 'Other cleaning activities', 'This class includes: - swimming pool cleaning and maintenance activities - cleaning of trains, buses, planes, etc. - cleaning of the inside of road and sea tankers - disinfecting and exterminating activities - bottle cleaning - street sweeping and snow and ice removal - other cleaning activities, n.e.c.', 1, 399334, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399338, '81.3', 'Landscape service activities', '', 1, 399331, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399339, '81.3', 'Landscape service activities', 'This class includes: - planting, care and maintenance of: . parks and gardens for: . private and public housing . public and semi-public buildings (schools, hospitals, administrative buildings, church buildings etc.) . municipal grounds (parks, green areas, cemeteries etc.) . highway greenery (roads, train lines and tramlines, waterways, ports) . industrial and commercial buildings - greenery for: . buildings (roof gardens, façade greenery, indoor gardens etc.) . sports grounds (football fields, golf courses etc.), play grounds, lawns for sunbathing and other recreational parks . stationary and flowing water (basins, alternating wet areas, ponds, swimming pools, ditches, watercourses, plant sewage systems) - plants for protection against noise, wind, erosion, visibility and dazzling', 1, 399338, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399340, '82', 'Office administrative, office support and other business support activities', 'This division includes the provision of a range of day-to-day office administrative services, as well as ongoing routine business support functions for others, on a contract or fee basis.', 1, 399293, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399341, '82.1', 'Office administrative and support activities', 'This group includes the provision of a range of day-to-day office administrative services, such as financial planning, billing and record keeping, personnel and physical distribution and logistics for others on a contract or fee basis.', 1, 399340, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399342, '82.11', 'Combined office administrative service activities', 'This class includes the provision of a combination of day-to-day office administrative services, such as reception, financial planning, billing and record keeping, personnel and mail services etc. for others on a contract or fee basis.', 1, 399341, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399343, '82.19', 'Photocopying, document preparation and other specialised office support activities', 'This class includes a variety of copying, document preparation and specialised office support activities. The document copying/printing activities included here cover only short-run type printing activities. This class includes: - document preparation - document editing or proofreading - typing and word processing - secretarial support services - transcription of documents, and other secretarial services - letter or resume writing - provision of mailbox rental and other postal and mailing services, such as pre-sorting, addressing, etc. - photocopying - duplicating - blueprinting - other document copying services (without also providing printing services, such as offset printing, quick printing, digital printing, pre-press services)', 1, 399341, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399344, '82.2', 'Activities of call centres', '', 1, 399340, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399345, '82.2', 'Activities of call centres', 'This class includes the activities of: - inbound call centres, answering calls from clients by using human operators, automatic call distribution, computer telephone integration, interactive voice response systems or similar methods to receive orders, provide product information, deal with customer requests for assistance or address customer complaints - outbound call centres using similar methods to sell or market goods or services to potential customers, undertake market research or public opinion polling and similar activities for clients', 1, 399344, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399346, '82.3', 'Organisation of conventions and trade shows', '', 1, 399340, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399347, '82.3', 'Organisation of conventions and trade shows', 'This class includes the organisation, promotion and/or management of events, such as business and trade shows, conventions, conferences and meetings, whether or not including the management and provision of the staff to operate the facilities in which these events take place.', 1, 399346, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399348, '82.9', 'Business support service activities n.e.c.', 'This group includes the activities of collection agencies, credit bureaus and all support activities typically provided to businesses not elsewhere classified.', 1, 399340, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399349, '82.91', 'Activities of collection agencies and credit bureaus', 'This class includes: - collection of payments for claims and remittance of payments collected to the clients, such as bill or debt collection services - compiling of information, such as credit and employment histories on individuals and credit histories on businesses and providing the information to financial institutions, retailers and others who have a need to evaluate the creditworthiness of these persons and businesses', 1, 399348, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399350, '82.92', 'Packaging activities', 'This class includes: - packaging activities on a fee or contract basis, whether or not these involve an automated process: . bottling of liquids, including beverages and food . packaging of solids (blister packaging, foil-covered etc.) . security packaging of pharmaceutical preparations . labelling, stamping and imprinting . parcel-packing and gift-wrapping', 1, 399348, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399351, '82.99', 'Other business support service activities n.e.c.', 'This class includes: - providing verbatim reporting and stenotype recording of live legal proceedings and transcribing subsequent recorded materials, such as: . court reporting or stenotype recording services . public stenography services - real-time (i.e. simultaneous) closed captioning of live television performances of meetings, conferences - address bar coding services - bar code imprinting services - fundraising organisation services on a contract or fee basis - repossession services - parking meter coin collection services - activities of independent auctioneers - administration of loyalty programmes - other support activities typically provided to businesses not elsewhere classified', 1, 399348, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399352, 'O', 'PUBLIC ADMINISTRATION AND DEFENCE; COMPULSORY SOCIAL SECURITY', 'This section includes activities of a governmental nature, normally carried out by the public administration. This includes the enactment and judicial interpretation of laws and their pursuant regulation, as well as the administration of programmes based on them, legislative activities, taxation, national defence, public order and safety, immigration services, foreign affairs and the administration of government programmes. The legal or institutional status is not, in itself, the determining factor for an activity to belong in this section, rather than the activity being of a nature specified in the previous paragraph. This means that activities classified elsewhere in NACE do not fall under this section, even if carried out by public entities. For example, administration of the school system (i.e. regulations, checks, curricula) falls under this section, but teaching itself does not (see section P), and a prison or military hospital is classified to health (see section Q). Similarly, some activities described in this section may be carried out by non-government units.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399353, '84', 'Public administration and defence; compulsory social security', '', 1, 399352, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399354, '84.1', 'Administration of the State and the economic and social policy of the community', 'This group includes general administration (e.g. executive, legislative, financial administration etc. at all levels of government) and supervision in the field of social and economic life.', 1, 399353, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399355, '84.11', 'General public administration activities', 'This class includes: - executive and legislative administration of central, regional and local bodies - administration and supervision of fiscal affairs: . operation of taxation schemes . duty/tax collection on goods and tax violation investigation . customs administration - budget implementation and management of public funds and public debt: . raising and receiving of money and control of their disbursement - administration of overall (civil) research and development policy and associated funds - administration and operation of overall economic and social planning and statistical services at the various levels of government', 1, 399354, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399356, '84.12', 'Regulation of the activities of providing health care, education, cultural services and other social services, excluding social security', 'This class includes: - public administration of programmes aimed to increase personal well-being: . health . education . culture . sport . recreation . environment . housing . social services - public administration of research and development policies and associated funds for these areas', 1, 399354, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399357, '84.13', 'Regulation of and contribution to more efficient operation of businesses', 'This class includes: - public administration and regulation, including subsidy allocation, for different economic sectors: . agriculture . land use . energy and mining resources . infrastructure . transport . communication . hotels and tourism . wholesale and retail trade - administration of research and development policies and associated funds to improve economic performance - administration of general labour affairs - implementation of regional development policy measures, e.g. to reduce unemployment', 1, 399354, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399358, '84.2', 'Provision of services to the community as a whole', 'This group includes foreign affairs, defence and public order and safety activities.', 1, 399353, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399359, '84.21', 'Foreign affairs', 'This class includes: - administration and operation of the ministry of foreign affairs and diplomatic and consular missions stationed abroad or at offices of international organisations - administration, operation and support for information and cultural services intended for distribution beyond national boundaries - aid to foreign countries, whether or not routed through international organisations - provision of military aid to foreign countries - management of foreign trade, international financial and foreign technical affairs', 1, 399358, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399360, '84.22', 'Defence activities', 'This class includes: - administration, supervision and operation of military defence affairs and land, sea, air and space defence forces such as: . combat forces of army, navy and air force . engineering, transport, communications, intelligence, material, personnel and other non-combat forces and commands . reserve and auxiliary forces of the defence establishment . military logistics (provision of equipment, structures, supplies etc.) . health activities for military personnel in the field - administration, operation and support of civil defence forces - support for the working out of contingency plans and the carrying out of exercises in which civilian institutions and populations are involved - administration of defence-related research and development policies and related funds', 1, 399358, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399361, '84.23', 'Justice and judicial activities', 'This class includes: - administration and operation of administrative civil and criminal law courts, military tribunals and the judicial system, including legal representation and advice on behalf of the government or when provided by the government in cash or services - rendering of judgements and interpretations of the law - arbitration of civil actions - prison administration and provision of correctional services, including rehabilitation services, regardless of whether their administration and operation is done by government units or by private units on a contract or fee basis', 1, 399358, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399362, '84.24', 'Public order and safety activities', 'This class includes: - administration and operation of regular and auxiliary police forces supported by public authorities and of port, border, coastguards and other special police forces, including traffic regulation, alien registration, maintenance of arrest records - provision of supplies for domestic emergency use in case of peacetime disasters', 1, 399358, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399363, '84.25', 'Fire service activities', 'This class includes: - fire fighting and fire prevention: . administration and operation of regular and auxiliary fire brigades in fire prevention, fire fighting, rescue of persons and animals, assistance in civic disasters, floods, road accidents etc.', 1, 399358, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399364, '84.3', 'Compulsory social security activities', '', 1, 399353, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399365, '84.3', 'Compulsory social security activities', 'This class includes: - funding and administration of government-provided social security programmes: . sickness, work-accident and unemployment insurance . retirement pensions . programmes covering losses of income due to maternity, temporary disablement, widowhood etc.', 1, 399364, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399366, 'P', 'EDUCATION', 'This section includes education at any level or for any profession. The instructions may be oral or written and may be provided by radio, television, Internet or via correspondence. It includes education by the different institutions in the regular school system at its different levels as well as adult education, literacy programmes etc. Also included are military schools and academies, prison schools etc. at their respective levels. The section includes public as well as private education. For each level of initial education, the classes include special education for physically or mentally handicapped pupils. The breakdown of the categories in this section is based on the level of education offered as defined by the levels of ISCED 1997. The activities of educational institutions providing courses on ISCED level 0 are classified in 85.10, on ISCED level 1 in 85.20, on ISCED levels 2-3 in group 85.3, on ISCED level 4 in 85.41 and on ISCED level 5-6 in 85.42.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399367, '85', 'Education', '', 1, 399366, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399368, '85.1', 'Pre-primary education', '', 1, 399367, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399369, '85.1', 'Pre-primary education ', 'This class includes: - pre-primary education (education preceding the first level). Pre-primary education is defined as the initial stage of organised instruction designed primarily to introduce very young children to a school-type environment, that is, to provide a bridge between the home and a school-based atmosphere.', 1, 399368, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399370, '85.2', 'Primary education', '', 1, 399367, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399371, '85.2', 'Primary education ', 'This class includes primary education: the furnishing of academic courses and associated course work that give students a sound basic education in reading, writing and mathematics and an elementary understanding of other subjects such as history, geography, natural science, social science, art and music. Such education is generally provided for children, however the provision of literacy programmes within or outside the school system, which are similar in content to programmes in primary education but are intended for those considered too old to enter elementary schools, is also included (i.e. adult literacy programmes).', 1, 399370, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399372, '85.3', 'Secondary education', 'This group includes the provision of general secondary and technical and vocational secondary education.', 1, 399367, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399373, '85.31', 'General secondary education ', 'This class includes provision of the type of education that lays the foundation for lifelong learning and human development and is capable of furthering education opportunities. Such units provide programmes that are usually on a more subject-oriented pattern using more specialised teachers, and more often employ several teachers conducting classes in their field of specialisation. Subject specialisation at this level often begins to have some influence even on the educational experience of those pursuing a general programme. Such programmes are designated to qualify students either for technical and vocational education or for entrance to higher education without any special subject prerequisite. This class includes: - lower general secondary education corresponding more or less to the period of compulsory school attendance - upper general secondary education giving, in principle, access to higher education', 1, 399372, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399374, '85.32', 'Technical and vocational secondary education ', 'This class includes provision of education typically emphasising subject-matter specialisation and instruction in both theoretical background and practical skills generally associated with present or prospective employment. The aim of a programme can vary from preparation for a general field of employment to a very specific job. This class includes: - technical and vocational education below the level of higher education as defined in 85.4', 1, 399372, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399375, '85.4', 'Higher education', 'This group includes the furnishing of post-secondary non-tertiary and academic courses and granting of degrees at baccalaureate, graduate or post-graduate level. The requirement for admission is a diploma at least at upper secondary education level.', 1, 399367, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399376, '85.41', 'Post-secondary non-tertiary education', 'This class includes provision of post-secondary education, which cannot be considered tertiary education. For example provision of supplementary post-secondary education to prepare for tertiary education or post-secondary non-tertiary vocational.', 1, 399375, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399377, '85.42', 'Tertiary education', 'This class includes: - first, second and third stages of tertiary education', 1, 399375, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399378, '85.5', 'Other education', 'This group includes general continuing education and continuing vocational education and training for any profession, hobby or self-development purposes. It includes camps and schools offering instruction in athletic activities to groups or individuals, foreign language instruction, instruction in the arts, drama or music or other instruction or specialised training, not comparable to the education in groups 85.1 - 85.4.', 1, 399367, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399379, '85.51', 'Sports and recreation education', 'This class includes the provision of instruction in athletic activities to groups of individuals, such as by camps and schools. Overnight and day sports instruction camps are also included. It does not include academic schools, colleges and universities. Instruction may be provided in diverse settings, such as the unit''s or client''s training facilities, educational institutions or by other means. Instruction provided in this class is formally organised. This class includes: - sports instruction (baseball, basketball, cricket, football, etc) - camps, sports instruction - gymnastics instruction - riding instruction, academies or schools - swimming instruction - professional sports instructors, teachers, coaches - martial arts instruction - card game instruction (such as bridge) - yoga instruction', 1, 399378, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399380, '85.52', 'Cultural education', 'This class includes provision of instruction in the arts, drama and music. Units giving this type of instructions might be named "schools”, "studios”, "classes” etc. They provide formally organised instruction, mainly for hobby, recreational or self-development purposes, but such instruction does not lead to a professional diploma, baccalaureate or graduate degree. This class includes: - piano teachers and other music instruction - art instruction - dance instruction and dance studios - drama schools (except academic) - fine arts schools (except academic) - performing arts schools (except academic) - photography schools (except commercial)', 1, 399378, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399381, '85.53', 'Driving school activities', '', 1, 399378, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399382, '85.59', 'Other education n.e.c.', 'This class includes: - education that is not definable by level - academic tutoring - learning centres offering remedial courses - professional examination review courses - language instruction and conversational skills instruction - computer training - religious instruction', 1, 399378, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399383, '85.6', 'Educational support activities', '', 1, 399367, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399384, '85.6', 'Educational support activities', 'This class includes: - provision of non-instructional activities that support educational processes or systems: . educational consulting . educational guidance counselling activities . educational testing evaluation activities . educational testing activities . organisation of student exchange programmes', 1, 399383, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399385, 'Q', 'HUMAN HEALTH AND SOCIAL WORK ACTIVITIES', 'This section includes the provision of health and social work activities. Activities include a wide range of activities, starting from health care provided by trained medical professionals in hospitals and other facilities, over residential care activities that still involve a degree of health care activities to social work activities without any involvement of health care professionals.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399386, '86', 'Human health activities', 'This division includes activities of short- or long-term hospitals, general or specialty medical, surgical, psychiatric and substance abuse hospitals, sanatoria, preventoria, medical nursing homes, asylums, mental hospital institutions, rehabilitation centres, leprosaria and other human health institutions which have accommodation facilities and which engage in providing diagnostic and medical treatment to inpatients with any of a wide variety of medical conditions.', 1, 399385, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399387, '86.1', 'Hospital activities', '', 1, 399386, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399388, '86.1', 'Hospital activities', 'This class includes: - short- or long-term hospital activities, i.e. medical, diagnostic and treatment activities, of general hospitals (e.g. community and regional hospitals, hospitals of non-profit organisations, university hospitals, military-base and prison hospitals) and specialised hospitals (e.g. mental health and substance abuse hospitals, hospitals for infectious diseases, maternity hospitals, specialised sanatoriums). The activities are chiefly directed to inpatients, are carried out under the direct supervision of medical doctors and include: . services of medical and paramedical staff . services of laboratory and technical facilities, including radiologic and anaesthesiologic services . emergency room services . provision of operating room services, pharmacy services, food and other hospital services . services of family planning centres providing medical treatment such as sterilisation and termination of pregnancy, with accommodation', 1, 399387, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399389, '86.2', 'Medical and dental practice activities', 'This group includes medical consultation and treatment done by general medical practitioners and medical specialists, including surgeons, dentists etc. These activities can be carried out in private practice, group practices and in hospital outpatient clinics, and in clinics such as those attached to firms, schools, homes for the aged, labour organisations and fraternal organisations, as well as in patients'' homes.', 1, 399386, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399390, '86.21', 'General medical practice activities', 'This class includes: - medical consultation and treatment in the field of general medicine carried out by general practitioners.', 1, 399389, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399391, '86.22', 'Specialist medical practice activities', 'This class includes: - medical consultation and treatment in the field of specialised medicine by medical specialists and surgeons', 1, 399389, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399392, '86.23', 'Dental practice activities', 'This class includes: - dental practice activities of a general or specialised nature, e.g. dentistry, endodontic and pediatric dentistry; oral pathology - orthodontic activities', 1, 399389, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399393, '86.9', 'Other human health activities', '', 1, 399386, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399394, '86.9', 'Other human health activities', 'This class includes: - activities for human health not performed by hospitals or by medical doctors or dentists: . activities of nurses, midwives, physiotherapists or other paramedical practitioners in the field of optometry, hydrotherapy, medical massage, occupational therapy, speech therapy, chiropody, homeopathy, chiropractic, acupuncture etc. These activities may be carried out in health clinics such as those attached to firms, schools, homes for the aged, labour organisations and fraternal organisations and in residential health facilities other than hospitals, as well as in own consulting rooms, patients'' homes or elsewhere.', 1, 399393, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399395, '87', 'Residential care activities', 'This division includes the provision of residential care combined with either nursing, supervisory or other types of care as required by the residents. Facilities are a significant part of the production process and the care provided is a mix of health and social services with the health services being largely some level of nursing services.', 1, 399385, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399396, '87.1', 'Residential nursing care activities', '', 1, 399395, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399397, '87.1', 'Residential nursing care activities', 'This class includes: - activities of: . homes for the elderly with nursing care . convalescent homes . rest homes with nursing care . nursing care facilities . nursing homes', 1, 399396, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399398, '87.2', 'Residential care activities for mental retardation, mental health and substance abuse', '', 1, 399395, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399399, '87.2', 'Residential care activities for mental retardation, mental health and substance abuse', 'This class includes the provision of residential care (but not licensed hospital care) to people with mental retardation, mental illness, or substance abuse problems. Facilities provide room, board, protective supervision and counselling and some health care. This class includes: - activities of: . facilities for alcoholism or drug addiction treatment . psychiatric convalescent homes . residential group homes for the emotionally disturbed . mental retardation facilities . mental health halfway houses', 1, 399398, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399400, '87.3', 'Residential care activities for the elderly and disabled', '', 1, 399395, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399401, '87.3', 'Residential care activities for the elderly and disabled', 'This class includes the provision of residential and personal care services for the elderly and disabled who are unable to fully care for themselves and/or who do not desire to live independently. The care typically includes room, board, supervision, and assistance in daily living, such as housekeeping services. In some instances these units provide skilled nursing care for residents in separate on-site facilities. This class includes: - activities of: . assisted-living facilities . continuing care retirement communities . homes for the elderly with minimal nursing care . rest homes without nursing care', 1, 399400, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399402, '87.9', 'Other residential care activities', '', 1, 399395, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399403, '87.9', 'Other residential care activities', 'This class includes the provision of residential and personal care services for persons, except the elderly and disabled, who are unable to fully care for themselves or who do not desire to live independently. This class includes: - activities provided on a round-the-clock basis directed to provide social assistance to children and special categories of persons with some limits on ability for self-care, but where medical treatment or education are not important elements: . orphanages . children''s boarding homes and hostels . temporary homeless shelters . institutions that take care of unmarried mothers and their children The activities may be carried out by government offices or private organisations.', 1, 399402, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399404, '88', 'Social work activities without accommodation', 'This division includes the provision of a variety of social assistance services directly to clients. The activities in this division do not include accommodation services, except on a temporary basis.', 1, 399385, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399405, '88.1', 'Social work activities without accommodation for the elderly and disabled', '', 1, 399404, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399406, '88.1', 'Social work activities without accommodation for the elderly and disabled', 'This class includes: - social, counselling, welfare, referral and similar services which are aimed at the elderly and disabled in their homes or elsewhere and carried out by government offices or by private organisations, national or local self-help organisations and by specialists providing counselling services: . visiting of the elderly and disabled . day-care activities for the elderly or for disabled adults . vocational rehabilitation and habilitation activities for disabled persons provided that the education component is limited', 1, 399405, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399407, '88.9', 'Other social work activities without accommodation', '', 1, 399404, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399408, '88.91', 'Child day-care activities', '', 1, 399407, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399409, '88.99', 'Other social work activities without accommodation n.e.c.', 'This class includes: - social, counselling, welfare, refugee, referral and similar services which are delivered to individuals and families in their homes or elsewhere and carried out by government offices or by private organisations, disaster relief organisations and national or local self-help organisations and by specialists providing counselling services: . welfare and guidance activities for children and adolescents . adoption activities, activities for the prevention of cruelty to children and others . household budget counselling, marriage and family guidance, credit and debt counselling services . community and neighbourhood activities . activities for disaster victims, refugees, immigrants etc., including temporary or extended shelter for them . vocational rehabilitation and habilitation activities for unemployed persons provided that the education component is limited . eligibility determination in connection with welfare aid, rent supplements or food stamps . day facilities for the homeless and other socially weak groups . charitable activities like fund-raising or other supporting activities aimed at social work', 1, 399407, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399410, 'R', 'ARTS, ENTERTAINMENT AND RECREATION', 'This section includes a wide range of activities to meet varied cultural, entertainment and recreational interests of the general public, including live performances, operation of museum sites, gambling, sports and recreation activities.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399411, '90', 'Creative, arts and entertainment activities', 'This division includes the operation of facilities and provision of services to meet the cultural and entertainment interests of their customers. This includes the production and promotion of, and participation in, live performances, events or exhibits intended for public viewing; the provision of artistic, creative or technical skills for the production of artistic products and live performances.', 1, 399410, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399412, '90', 'Creative, arts and entertainment activities', 'This group includes activities in the creative and performing arts and related activities.', 1, 399411, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399413, '90.01', 'Performing arts', 'This class includes: - production of live theatrical presentations, concerts and opera or dance productions and other stage productions: . activities of groups, circuses or companies, orchestras or bands . activities of individual artists such as actors, dancers, musicians, lecturers or speakers', 1, 399411, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399414, '90.02', 'Support activities to performing arts', 'This class includes: - support activities to performing arts for production of live theatrical presentations, concerts and opera or dance productions and other stage productions: . activities of directors, producers, stage-set designers and builders, scene shifters, lighting engineers etc.', 1, 399411, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399415, '90.03', 'Artistic creation', 'This class includes: - activities of individual artists such as sculptors, painters, cartoonists, engravers, etchers etc. - activities of individual writers, for all subjects including fictional writing, technical writing etc. - activities of independent journalists - restoring of works of art such as paintings etc.', 1, 399411, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399416, '90.04', 'Operation of arts facilities', 'This class includes: - operation of concert and theatre halls and other arts facilities', 1, 399411, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399417, '91', 'Libraries, archives, museums and other cultural activities', 'This division includes the activities of libraries and archives; the operation of museums of all kinds, botanical and zoological gardens; the operation of historical sites and nature reserves activities.', 1, 399410, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399418, '91', 'Libraries, archives, museums and other cultural activities', '', 1, 399417, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399419, '91.01', 'Library and archives activities', 'This class includes: - documentation and information activities of libraries of all kinds, reading, listening and viewing rooms, public archives providing service to the general public or to a special clientele, such as students, scientists, staff, members as well as operation of government archives: . organisation of a collection, whether specialised or not . cataloguing collections . lending and storage of books, maps, periodicals, films, records, tapes, works of art etc. . retrieval activities in order to comply with information requests etc. - stock photo and movie libraries and services', 1, 399417, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399420, '91.02', 'Museums activities', 'This class includes: - operation of museums of all kinds: . art museums, museums of jewellery, furniture, costumes, ceramics, silverware . natural history, science and technological museums, historical museums, including military museums . other specialised museums . open-air museums', 1, 399417, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399421, '91.03', 'Operation of historical sites and buildings and similar visitor attractions', 'This class includes: - operation and preservation of historical sites and buildings', 1, 399417, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399422, '91.04', 'Botanical and zoological gardens and nature reserves activities', 'This class includes: - operation of botanical and zoological gardens, including children''s zoos - operation of nature reserves, including wildlife preservation, etc.', 1, 399417, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399423, '92', 'Gambling and betting activities', 'This division includes the operation of gambling facilities such as casinos, bingo halls and video gaming terminals and the provision of gambling services, such as lotteries and off-track betting.', 1, 399410, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399424, '92', 'Gambling and betting activities', '', 1, 399423, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399425, '92', 'Gambling and betting activities', 'This class includes gambling and betting activities such as: - sale of lottery tickets - operation (exploitation) of coin-operated gambling machines - operation of virtual gambling web sites - bookmaking and other betting operations - off-track betting - operation of casinos, including "floating casinos”', 1, 399423, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399426, '93', 'Sports activities and amusement and recreation activities', 'This division includes the provision of recreational, amusement and sports activities (except museums activities, preservation of historical sites, botanical and zoological gardens and nature reserves activities; and gambling and betting activities).', 1, 399410, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399427, '93.1', 'Sports activities', 'This group includes the operation of sports facilities; activities of sports teams or clubs primarily participating in live sports events before a paying audience; independent athletes engaged in participating in live sporting or racing events before a paying audience; owners of racing participants such as cars, dogs, horses, etc. primarily engaged in entering them in racing events or other spectator sports events; sports trainers providing specialised services to support participants in sports events or competitions; operators of arenas and stadiums; other activities of organising, promoting or managing sports events, n.e.c.', 1, 399426, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399428, '93.11', 'Operation of sports facilities', 'This class includes: - operation of facilities for indoor or outdoor sports events (open, closed or covered, with or without spectator seating): . football, hockey, cricket, rugby stadiums . racetracks for car, dog, horse races . swimming pools and stadiums . track and field stadiums . winter sports arenas and stadiums . ice-hockey arenas . boxing arenas . golf courses . bowling lanes - organisation and operation of outdoor or indoor sports events for professionals or amateurs by organisations with own facilities', 1, 399427, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399429, '93.12', 'Activities of sports clubs', 'This class includes the activities of sports clubs, which, whether professional, semi-professional or amateur clubs, give their members the opportunity to engage in sporting activities. This class includes: - operation of sports clubs: . football clubs . bowling clubs . swimming clubs . golf clubs . boxing clubs . winter sports clubs . chess clubs . track and field clubs . shooting clubs, etc.', 1, 399427, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399430, '93.13', 'Fitness facilities', 'This class includes: - fitness and body-building clubs and facilities', 1, 399427, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399431, '93.19', 'Other sports activities', 'This class includes: - activities of producers or promoters of sports events, with or without facilities - activities of individual own-account sportsmen and athletes, referees, judges, timekeepers etc. - activities of sports leagues and regulating bodies - activities related to promotion of sporting events - activities of racing stables, kennels and garages - operation of sport fishing and hunting preserves - activities of mountain guides - support activities for sport or recreational hunting and fishing', 1, 399427, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399432, '93.2', 'Amusement and recreation activities', 'This group includes a wide range of units that operate facilities or provide services to meet the varied recreational interests of their patrons. It includes the operation of a variety of attractions, such as mechanical rides, water rides, games, shows, theme exhibits and picnic grounds.', 1, 399426, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399433, '93.21', 'Activities of amusement parks and theme parks', 'This class includes activities of amusement parks or theme parks. It includes the operation of a variety of attractions, such as mechanical rides, water rides, games, shows, theme exhibits and picnic grounds.', 1, 399432, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399434, '93.29', 'Other amusement and recreation activities', 'This class includes activities related to entertainment and recreation (except amusement parks and theme parks) not elsewhere classified: - operation (exploitation) of coin-operated games - activities of recreation parks (without accommodation) - operation of recreational transport facilities, e.g. marinas - operation of ski hills - rental of leisure and pleasure equipment as an integral part of recreational facilities - fairs and shows of a recreational nature - activities of beaches, including rental of facilities such as bathhouses, lockers, chairs etc. - operation of dance floors', 1, 399432, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399435, 'S', 'OTHER SERVICE ACTIVITIES', 'This section (as a residual category) includes the activities of membership organisations, the repair of computers and personal and household goods and a variety of personal service activities not covered elsewhere in the classification.', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399436, '94', 'Activities of membership organisations', 'This division includes activities of organisations representing interests of special groups or promoting ideas to the general public. These organisations usually have a constituency of members, but their activities may involve and benefit non-members as well. The primary breakdown of this division is determined by the purpose that these organisations serve, namely interests of employers, self-employed individuals and the scientific community (group 94.1), interests of employees (group 94.2) or promotion of religious, political, cultural, educational or recreational ideas and activities (group 94.9).', 1, 399435, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399437, '94.1', 'Activities of business, employers and professional membership organisations', 'This group includes the activities of units that promote the interests of the members of business and employers organisations.', 1, 399436, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399438, '94.11', 'Activities of business and employers membership organisations', 'This class includes: - activities of organisations whose members'' interests centre on the development and prosperity of enterprises in a particular line of business or trade, including farming, or on the economic growth and climate of a particular geographical area or political subdivision without regard for the line of business. - activities of federations of such associations - activities of chambers of commerce, guilds and similar organisations - dissemination of information, representation before government agencies, public relations and labour negotiations of business and employer organisations', 1, 399437, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399439, '94.12', 'Activities of professional membership organisations', 'This class includes: - activities of organisations whose members'' interests centre chiefly on a particular scholarly discipline or professional practice or technical field, such as medical associations, legal associations, accounting associations, engineering associations, architects associations etc. - activities of associations of specialists engaged in scientific, academic or cultural activities, such as associations of writers, painters, performers of various kinds, journalists etc. - dissemination of information, the establishment and supervision of standards of practice, representation before government agencies and public relations of professional organisations', 1, 399437, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399440, '94.2', 'Activities of trade unions', '', 1, 399436, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399441, '94.2', 'Activities of trade unions', 'This class includes: - promoting of the interests of organised labour and union employees', 1, 399440, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399442, '94.9', 'Activities of other membership organisations', 'This group includes the activities of units (except business and employers organisations, professional organisations, trade unions) that promote the interests of their members.', 1, 399436, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399443, '94.91', 'Activities of religious organisations', 'This class includes: - activities of religious organisations or individuals providing services directly to worshippers in churches, mosques, temples, synagogues or other places - activities of organisations furnishing monastery and convent services - religious retreat activities', 1, 399442, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399444, '94.92', 'Activities of political organisations', 'This class includes: - activities of political organisations and auxiliary organisations such as young people''s auxiliaries associated with a political party. These organisations chiefly engage in influencing decision-taking in public governing bodies by placing members of the party or those sympathetic to the party in political office and involve the dissemination of information, public relations, fund-raising etc.', 1, 399442, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399445, '94.99', 'Activities of other membership organisations n.e.c.', 'This class includes: - activities of organisations (not directly affiliated to a political party) furthering a public cause or issue by means of public education, political influence, fund-raising etc.: . citizens initiative or protest movements . environmental and ecological movements . organisations supporting community and educational facilities n.e.c. . organisations for the protection and betterment of special groups, e.g. ethnic and minority groups . associations for patriotic purposes, including war veterans'' associations - consumer associations - automobile associations - associations for the purpose of social acquaintanceship such as rotary clubs, lodges etc. - associations of youth, young persons'' associations, student associations, clubs and fraternities etc. - associations for the pursuit of a cultural or recreational activity or hobby (other than sports or games), e.g. poetry, literature and book clubs, historical clubs, gardening clubs, film and photo clubs, music and art clubs, craft and collectors'' clubs, social clubs, carnival clubs etc.', 1, 399442, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399446, '95', 'Repair of computers and personal and household goods', 'This division includes the repair and maintenance of computers peripheral equipment such as desktops, laptops, computer terminals, storage devices and printers.', 1, 399435, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399447, '95.1', 'Repair of computers and communication equipment', 'This group includes the repair and maintenance of computers and peripheral equipment and communications equipment.', 1, 399446, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399448, '95.11', 'Repair of computers and peripheral equipment', 'This class includes the repair of electronic equipment, such as computers and computing machinery and peripheral equipment. This class includes the repair and maintenance of: - desktop computers - laptop computers - magnetic disk drives, flash drives and other storage devices - optical disk drives (CD-RW, CD-ROM, DVD-ROM, DVD-RW) - printers - monitors - keyboards - mice, joysticks and trackball accessories - internal and external computer modems - dedicated computer terminals - computer servers - scanners, including bar code scanners - smart card readers - virtual reality helmets - computer projectors', 1, 399447, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399449, '95.12', 'Repair of communication equipment', 'This class includes repair and maintenance of communications equipment such as: - cordless telephones - cellular phones - carrier equipment modems - fax machines - communications transmission equipment (e.g. routers, bridges, modems) - two-way radios - commercial TV and video cameras', 1, 399447, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399450, '95.2', 'Repair of personal and household goods', 'This group includes the repair and servicing of personal and household goods.', 1, 399446, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399451, '95.21', 'Repair of consumer electronics', 'This class includes repair and maintenance of consumer electronics: - repair of consumer electronics: . television, radio receivers . video cassette recorders (VCR) . CD players . household-type video cameras', 1, 399450, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399452, '95.22', 'Repair of household appliances and home and garden equipment', 'This class includes the repair and servicing household appliances and home and garden equipment: - repair and servicing of household appliances . refrigerators, stoves, washing machines, clothes dryers, room air conditioners, etc. - repair and servicing of home and garden equipment . lawnmowers, edgers, snow- and leaf- blowers, trimmers, etc.', 1, 399450, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399453, '95.23', 'Repair of footwear and leather goods', 'This class includes repair and maintenance of footwear and leather goods: - repair of boots, shoes, luggage and the like - fitting of heels', 1, 399450, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399454, '95.24', 'Repair of furniture and home furnishings', 'This class includes: - reupholstering, refinishing, repairing and restoring of furniture and home furnishings including office furniture', 1, 399450, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399455, '95.25', 'Repair of watches, clocks and jewellery', 'This class includes: - repair of watches, clocks and their parts such as watch cases and housings of all materials; movements, chronometers, etc. - repair of jewellery', 1, 399450, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399456, '95.29', 'Repair of other personal and household goods', 'This class includes repair of personal and household goods: - repair of bicycles - repair and alteration of clothing - repair of sporting goods (except sporting guns) and camping equipment - repair of books - repair of musical instruments (except organs and historical musical instruments) - repair of toys and similar articles - repair of other personal and household goods - piano-tuning', 1, 399450, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399457, '96', 'Other personal service activities', 'This division includes all service activities not mentioned elsewhere in the classification. Notably it includes types of services such as washing and (dry-)cleaning of textiles and fur products, hairdressing and other beauty treatment, funeral and related activities.', 1, 399435, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399458, '96', 'Other personal service activities', '', 1, 399457, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399459, '96.01', 'Washing and (dry-)cleaning of textile and fur products', 'This class includes: - laundering and dry cleaning, pressing etc., of all kinds of clothing (including fur) and textiles, provided by mechanical equipment, by hand or by self-service coin-operated machines, whether for the general public or for industrial or commercial clients - laundry collection and delivery - carpet and rug shampooing and drapery and curtain cleaning, whether on clients'' premises or not - provision of linens, work uniforms and related items by laundries - diaper supply services', 1, 399457, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399460, '96.02', 'Hairdressing and other beauty treatment', 'This class includes: - hair washing, trimming and cutting, setting, dyeing, tinting, waving, straightening and similar activities for men and women - shaving and beard trimming - facial massage, manicure and pedicure, make-up etc.', 1, 399457, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399461, '96.03', 'Funeral and related activities', 'This class includes: - burial and incineration of human or animal corpses and related activities: . preparing the dead for burial or cremation and embalming and morticians'' services . providing burial or cremation services . rental of equipped space in funeral parlours - rental or sale of graves - maintenance of graves and mausoleums', 1, 399457, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399462, '96.04', 'Physical well-being activities', 'This class includes: - activities of Turkish baths, sauna and steam baths, solariums, reducing and slendering salons, massage salons etc.', 1, 399457, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399463, '96.09', 'Other personal service activities n.e.c.', 'This class includes: - astrological and spiritualists'' activities - social activities such as escort services, dating services, services of marriage bureaux - pet care services such as boarding, grooming, sitting and training pets - genealogical organisations - activities of tattooing and piercing studios - shoe shiners, porters, valet car parkers etc. - concession operation of coin-operated personal service machines (photo booths, weighing machines, machines for checking blood pressure, coin-operated lockers etc.)', 1, 399457, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399464, 'T', 'ACTIVITIES OF HOUSEHOLDS AS EMPLOYERS; UNDIFFERENTIATED GOODS- AND SERVICES-PRODUCING ACTIVITIES OF HOUSEHOLDS FOR OWN USE', '', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399465, '97', 'Activities of households as employers of domestic personnel', '', 1, 399464, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399466, '97', 'Activities of households as employers of domestic personnel', '', 1, 399465, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399467, '97', 'Activities of households as employers of domestic personnel', 'This class includes the activities of households as employers of domestic personnel such as maids, cooks, waiters, valets, butlers, laundresses, gardeners, gatekeepers, stable-lads, chauffeurs, caretakers, governesses, babysitters, tutors, secretaries etc. It allows the domestic personnel employed to state the activity of their employer in censuses or studies, even though the employer is an individual. The product produced by this activity is consumed by the employing household.', 1, 399465, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399468, '98', 'Undifferentiated goods- and services-producing activities of private households for own use', 'This division includes the undifferentiated subsistence goods-producing and services-producing activities of households. Households should be classified here only if it is impossible to identify a primary activity for the subsistence activities of the household. If the household engages in market activities, it should be classified according to the primary market activity carried out.', 1, 399464, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399469, '98.1', 'Undifferentiated goods-producing activities of private households for own use', '', 1, 399468, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399470, '98.1', 'Undifferentiated goods-producing activities of private households for own use', 'This class includes the undifferentiated subsistence goods-producing activities of households, that is to say, the activities of households that are engaged in a variety of activities that produce goods for their own subsistence. These activities include hunting and gathering, farming, the production of shelter and clothing and other goods produced by the household for its own subsistence. If households are also engaged in the production of marketed goods, they are classified to the appropriate goods-producing industry of NACE. If they are principally engaged in a specific goods-producing subsistence activity, they are classified to the appropriate goods-producing industry of NACE.', 1, 399469, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399471, '98.2', 'Undifferentiated service-producing activities of private households for own use', '', 1, 399468, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399472, '98.2', 'Undifferentiated service-producing activities of private households for own use', 'This class includes the undifferentiated subsistence services-producing activities of households. These activities include cooking, teaching, caring for household members and other services produced by the household for its own subsistence. If households are also engaged in the production of multiple goods for subsistence purposes, they are classified to the undifferentiated goods-producing subsistence activities of households.', 1, 399471, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399473, 'U', 'ACTIVITIES OF EXTRATERRITORIAL ORGANISATIONS AND BODIES', '', 1, 398480, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399474, '99', 'Activities of extraterritorial organisations and bodies', '', 1, 399473, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399475, '99', 'Activities of extraterritorial organisations and bodies', '', 1, 399474, FALSE);
INSERT INTO staf_process (id, code, name, description, type_id, parent_id, is_separator) VALUES (399476, '99', 'Activities of extraterritorial organisations and bodies', 'This class includes: - activities of international organisations such as the United Nations and the specialised agencies of the United Nations system, regional bodies etc., the International Monetary Fund, the World Bank, the World Customs Organisation, the Organisation for Economic Co-operation and Development, the organisation of Petroleum Exporting Countries, the European Communities, the European Free Trade Association etc.', 1, 399474, FALSE);
